INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1142, /*level*/1, /*item_detail*/"WinWinな関係の衣装スタンド。ドヤァ !\n5th Anniversary Party ニコ生SPで採用されたもの。\nPにも推される喜びを知ってほしい。字がとても上手。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1150, /*level*/1, /*item_detail*/"スケッチブック。\n自由にお絵かきができる。\n上手な風景画が描いてあり、みんなの間で話題になった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1160, /*level*/1, /*item_detail*/"青が基調のクリスマステーブル。\n大きなキャンドルと青いポインセチアが並んでいる。\n厳かな雰囲気に、アイドルもおしとやかに振る舞いだす。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1161, /*level*/1, /*item_detail*/"青が基調のクリスマスソファー。\nジンジャーブレッドマンやキャンディケインの\nクッションが置いてある。美味しそうだが食べられない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001203);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1162, /*level*/1, /*item_detail*/"座り心地のいいロッキングチェア。\n激しく揺らしたり、編み物をしたり、居眠りをしたり…。\nアイドルによって使い方はさまざま。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1163, /*level*/1, /*item_detail*/"クリスマスシェルフ。\nみなで持ち寄ったクリスマス仕様の小物が置かれている。\nクリスマスを楽しむ気持ちが詰まっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1164, /*level*/1, /*item_detail*/"青のクリスマスツリー。\nシンプルな飾り付けだが、幻想的に輝いている。\nその輝きには、本場のサンタもうなった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000303);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1165, /*level*/1, /*item_detail*/"クリスマスキャンドル。\n火を灯すといい香りがして、ロマンティックな雰囲気を\n演出してくれる。みんな思わずウットリ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1166, /*level*/1, /*item_detail*/"汽車のおもちゃ。\n飾るもよし遊ぶもよしの一品。乗り物好きなアイドルが\nみんなに乗り物を好きになってほしくて購入したもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1167, /*level*/1, /*item_detail*/"煙突付きの暖炉。\n揺れる火を眺めると、安らぎのひとときを過ごせそう。\n煙突には侵入禁止。あるアイドルは少しガッカリした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503502);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1168, /*level*/1, /*item_detail*/"雪の結晶のネオンサイン。\n雪の結晶をイメージしたネオンがピカピカ光る。\nこれを見ると、雪の日の思い出がよみがえる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1169, /*level*/1, /*item_detail*/"重厚な青の壁紙。\n北欧風の飾りがポイントになっている。\n「外国の家みたい」と、アイドルたちがはしゃいでいた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1170, /*level*/1, /*item_detail*/"クリスマスをイメージした絨毯が敷かれた床。\n冬の寒い日でも、温かな雰囲気でルームを包んでくれる。\nほっこりとした気分になって、いつもより話も弾む。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1171, /*level*/1, /*item_detail*/"クリスマスの森の背景。\nしんしんと雪が降り、木に飾られたネオンが光る。\n雪景色にゆらめく光を眺めながら、心温まる話をしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1180, /*level*/1, /*item_detail*/"エンジェルウィング。いかがだろうか？\nアイドルが自らの背中にピッとつけて飛ぶための翼。\nこの翼をつけ、ポーズをどーんとキメるのもいいかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1190, /*level*/1, /*item_detail*/"鏡餅クッション。\n鏡餅を模した大きなクッション。\nよく似た着ぐるみを着て、隣に座るアイドルがいたとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1191, /*level*/1, /*item_detail*/"赤色のこたつ。\nほっこりと温まりながらくつろげる。\n普段忙しいアイドルたちもたまにはのびのび…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1192, /*level*/1, /*item_detail*/"華やかな門松。\n酉年にちなんで、鶏のぬいぐるみが据えられている。\nぬいぐるみは、アイドルが飾るために準備したもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1193, /*level*/1, /*item_detail*/"書き初めセット。\n新年の抱負を書くために設置された。\n新年への期待を込めて、みんな真面目に書いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1194, /*level*/1, /*item_detail*/"和風フラワーアレンジメント。\n花々を雅やかにアレンジしたもの。花や華道に\n精通しているアイドルたちが、和気あいあいと制作した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1195, /*level*/1, /*item_detail*/"招き猫。\n反転させると、挙げる足が変わる優れもの。\n得たいご利益によって置き方を変えてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1196, /*level*/1, /*item_detail*/"こま。\nお正月の定番の遊びであるこま回しができる。\n誰が一番上手に回せるかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1197, /*level*/1, /*item_detail*/"丸窓。\n和風のルームとマッチする雅やかな一品。\nこの窓を見て、はんなりした気持ちになろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1198, /*level*/1, /*item_detail*/"掛け軸。\nアイドルが書き初めで書いた「迎春」が飾られている。\nこれで春を迎える心構えはバッチリ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1199, /*level*/1, /*item_detail*/"梅模様の壁。\n壁紙には梅があしらわれ、華やかな雰囲気を演出。\nこの部屋にいると、着物が着たくなるという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1200, /*level*/1, /*item_detail*/"古都の背景。\n梅の花が咲き、いい香りが漂っている。\n暖かな春はもう、すぐそこだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1210, /*level*/1, /*item_detail*/"イベント「あんきら ! ？狂騒曲」で獲得した\n杏のうさぎクッション。きらりが心を込めてデコった。\nあんきらふたりの大好きなものが詰まっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1211, /*level*/1, /*item_detail*/"イベント「あんきら ! ？狂騒曲」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nハートにキラッ☆アイドルは存在感を見せつけた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1212, /*level*/1, /*item_detail*/"イベント「あんきら ! ？狂騒曲」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルはハチャメチャなステージを思いきり楽しんだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1213, /*level*/1, /*item_detail*/"イベント「あんきら ! ？狂騒曲」で獲得した\n純金のトロフィー。アイドルを伝説級のステージに\n導いたＰに贈られる。みんなでハピハピな関係を築いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1220, /*level*/1, /*item_detail*/"サンタのくつ。\nサンタにプレゼントがもらえるように、アイドルたちが\n設置したもの。聖夜にはプレゼントが…？", /*effect_value_1*/1, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1221, /*level*/1, /*item_detail*/"ブッシュドノエルクッション。\nサンタからのプレゼント。夢とともに圧縮して\nあったようで、包みを開けると物凄い勢いで膨らんだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1230, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したお茶屋セット。\n上り坂を歩くのに疲れたら、このお茶屋で一休み。\n今まで歩いてきた道を見れば、また歩き出せるはずだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000106);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1231, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nアイドルははんなりと舞い踊り、会場を沸かせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1232, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\nアイドルは乙女の花道で、大和撫子然と歌い上げた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1233, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルは命を燃やし、ステージに見事な花を咲かせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1240, /*level*/1, /*item_detail*/"スターがやってくるドア。\nCM連動キャンペーンのアイテム。期間限定で、\nアイドルの先輩がルームへくつろぎにきてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1250, /*level*/1, /*item_detail*/"ヒーローウォッチ。\n光の私物で、ヒーローに変身するためのおもちゃ。\nカッコよくポーズをキメるのもクセになるアイテム。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1260, /*level*/1, /*item_detail*/"イベント「Lunatic Show」で獲得したベッド。\nここで眠ると、キラキラした悪夢が見られる。\n悪夢に出てくるあの子ともお友達になれるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1261, /*level*/1, /*item_detail*/"イベント「Lunatic Show」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルはほとばしる想いをめいっぱい叫んだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1262, /*level*/1, /*item_detail*/"イベント「Lunatic Show」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルはキラキラした戦慄の世界をつくりあげた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1263, /*level*/1, /*item_detail*/"イベント「Lunatic Show」で獲得した\n純金のトロフィー。アイドルを伝説級のステージに\n導いたＰに贈られる。皆でゴートゥーヘル ! スパーク !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1270, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得したおかしな魔法の書。\n呪文を唱えると、お皿の上にお菓子が現われる。\n一口食べるだけで、めくるめく世界への扉が開く。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1271, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルは愛情をいっぱいに込めたワルツを振る舞った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1272, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルは甘いパフォーマンスでファンを虜にした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1273, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルはファンを甘いおとぎの世界へと誘った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1280, /*level*/1, /*item_detail*/"バレンタインモチーフのソファー。\nしっとりふかふかで、チョコシフォンケーキのような\n触り心地。ここに座って、大好きなスイーツを語ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1281, /*level*/1, /*item_detail*/"バレンタインモチーフのチェア。\n座るだけで、エレガントな気分になれる。\nこのチェアに腰かけて、ティータイムはいかが？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000104);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1282, /*level*/1, /*item_detail*/"バレンタインのスイーツがいっぱい載ったテーブル。\nカラフルで美味しそうなチョコレートに誘われて、\nつい、つまみ食いしちゃいそう…♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1283, /*level*/1, /*item_detail*/"カウンターテーブル。\nここで飲んだり、食べたり、話したり。用途はいろいろ。\n気の利くアイドルがこまめに拭くので、いつもピカピカ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1284, /*level*/1, /*item_detail*/"かわいいレジカウンター。\nルームをお店に早変わりさせちゃおう !\nちびっこアイドルもお菓子屋の店員さんに早変わり♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1285, /*level*/1, /*item_detail*/"チョコレートが並んだショーケース。\n気に入ったチョコレートを、アイドルが心を込めて\nラッピングしてくれる。まるで、お菓子屋さんみたい !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1286, /*level*/1, /*item_detail*/"バレンタインオブジェ。\n美味しそうなスイーツを象ったオブジェ。\n本物と勘違いして、食べようとするアイドルが続出。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1287, /*level*/1, /*item_detail*/"ホワイトチョコレート色のテディベア。\nふわふわの抱き心地で、癒される。\n一つもチョコをもらえなかった人へ、そっと差し出そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1288, /*level*/1, /*item_detail*/"ミルクチョコレート色のテディベア。\nアイドルが抱きつくのに、ちょうどいいサイズ。\nほんのりチョコレートの香りがただよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1289, /*level*/1, /*item_detail*/"バレンタインにぴったりなパーティション。\n仕切りとしてだけでなく、おしゃれなインテリアとしても\n大活躍。スウィートな雰囲気のルームをつくろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1290, /*level*/1, /*item_detail*/"高級チョコレートを飾ってある棚。\n普段は鍵がかかっていて、開かない。\nその扉は、一年で一番スウィートな日にだけ開くらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1291, /*level*/1, /*item_detail*/"バレンタインモチーフのアートボード。\nスイーツのイラストが飾られている。甘いもの好きな\nアイドルが、絵が上手なアイドルに描いてもらった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1292, /*level*/1, /*item_detail*/"お菓子屋さんのような壁。\nお部屋を爽やかなミントグリーンでラッピングして、\nアイドルへ甘い時間をプレゼントしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1293, /*level*/1, /*item_detail*/"お菓子屋さんのような床。\n温かみのあるベージュは、ルームを優しい雰囲気にする。\nお客さんを「いらっしゃいませ」と迎えよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1294, /*level*/1, /*item_detail*/"バレンタインテーマの背景。\n可愛いパティスリーの扉を開くと…そこは事務所 !\nスウィートなアイテムに囲まれて、アイドルも幸せそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1300, /*level*/1, /*item_detail*/"難波さん家のたこやき機。\n笑美が腕を振るおうと、持ち込んだもの。\n上手に焼けるか、みんなで腕を競い合おう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1310, /*level*/1, /*item_detail*/"控え室のテーブル。\n飲み物や台本、雑誌などが置かれている。\nみんなで使うので、すぐに物でいっぱいになってしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1311, /*level*/1, /*item_detail*/"控え室のメイクスペース。\nメイクさんこだわりの化粧品が揃っている。\n可愛くメイクして、スタンバイしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1312, /*level*/1, /*item_detail*/"控え室のスツール。\nアイドルでも運びやすい軽量設計。大人数でも大丈夫。\nみんなでお話をして、本番前の緊張をほぐそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1515301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1313, /*level*/1, /*item_detail*/"控え室のチェスト。\nアイドルの私物を置いておくための棚。LIVEで\n夢中になった後は、忘れ物をしやすいので注意しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1314, /*level*/1, /*item_detail*/"フィッティングルーム。\nアイドルたちが、夢の衣装に着替えるスペース。\n中を覗いてはいけない。アイドルたちとの約束だぞ☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1315, /*level*/1, /*item_detail*/"ハンガースタンド。\n本番の衣装を掛けておく。ここに掛けてある衣装を見て、アイドルたちはステージへの意識を高める。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1316, /*level*/1, /*item_detail*/"シンプルなコートハンガー。\nアイドルたちが私物を掛けて使用する。\n一番上に☆を掲げたアイドルがいた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1317, /*level*/1, /*item_detail*/"緑が目に優しい観葉植物。\n見ているだけで、癒されると評判。この観葉植物を見て、\nアイドルたちが本番前の緊張をほぐしていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1318, /*level*/1, /*item_detail*/"姿見。\n本番に出ていく直前、アイドルたちがこの姿未で\n最終チェックをする。その表情は凛々しく、頼もしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1319, /*level*/1, /*item_detail*/"控え室の時計。\n本番までの時間を刻んでいる。ソワソワしながら\n見上げると、あまり進んでいなかったりする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1320, /*level*/1, /*item_detail*/"控え室の壁。\nホワイトの壁だが、よく見ると…なんとホワイトボード !\nアイドルたちもフォーメーションの確認などに使う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1321, /*level*/1, /*item_detail*/"控え室の床。\nピカピカに磨き抜かれている。清掃員が応援を込めて、\n丁寧に掃除してくれた。アイドルたちも感謝している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1322, /*level*/1, /*item_detail*/"色とりどりの星が散りばめられたポップな背景。\nアイドルたちは、今日のステージで星のように輝くためにレッスンを重ねてきた。さぁ、自分を信じて… !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1330, /*level*/1, /*item_detail*/"ジュースをつくるミキサー。\nネネがおすすめする健康志向のレシピで、おいしい\nミックスジュースを作ろう。アレもコレも詰め込んで♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1340, /*level*/1, /*item_detail*/"イベント「情熱ファンファンファーレ」で獲得した\nトランペット。情熱的にファンファーレを演奏する\n準備はOK？ 『それでは元気よくいってみよう ! 』", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1341, /*level*/1, /*item_detail*/"イベント「情熱ファンファンファーレ」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\n会場のみんなで青春にテンションを上げて飛び込んだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1342, /*level*/1, /*item_detail*/"イベント「情熱ファンファンファーレ」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nFANと一緒にFUNなファンファーレを歌った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1343, /*level*/1, /*item_detail*/"イベント「情熱ファンファンファーレ」で獲得した\n純金のトロフィー。アイドルを伝説級のステージに\n導いたＰに贈られる。情熱的で前向きな歌を贈った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1350, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した法螺貝。\n芳乃が番組で、仲間を応援する際に使用した小道具。\nぶおおー♪と吹けば、皆のやる気もアップ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1351, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルは雅やかに歌い、ファンと想いを重ねた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1352, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルは一歩を踏み出し、新たな風を吹かせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1353, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n純金のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルは会場を薄紅色に染め上げ、歓声を浴びた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1360, /*level*/1, /*item_detail*/"サイバーチェア。\nのあがお仕事で使用したもの。座ると、ホログラム映像が\n映し出される。偶像を見つめるその瞳は何を想うのか…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1370, /*level*/1, /*item_detail*/"どんちゃんのぬいぐるみ。\n本物のどんちゃんはけやき製のため、季節の湿度差で\n伸び縮みするが、ぬいぐるみにはサイズの変動はない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1371, /*level*/1, /*item_detail*/"かっちゃんのぬいぐるみ。\n好物はやきそばだというかっちゃん。事務所でやきそばを\n食べていると、視線を感じるとかそうでもないとか…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1372, /*level*/1, /*item_detail*/"太鼓の達人 イエローVer.の筐体がルームに !\n遊び放題 ! 思う存分ドンドンカッカッしよう !\nみんなもおなじみのあの曲も遊べるぞ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1380, /*level*/1, /*item_detail*/"ひなまつりのお菓子が載ったちゃぶ台。\n表面には可愛らしい桃の花の模様があしらわれている。\nひなあられをつまみ食いすると、おいしくて止まらない !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1381, /*level*/1, /*item_detail*/"黒漆塗りに金の細工が施された豪華な箪笥。\n中には、アイドルたちの衣装が詰まっている。\nひとたび衣装を着れば、おひなさまも顔負けの華やかさ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1382, /*level*/1, /*item_detail*/"雅やかな雰囲気の鏡台。\nここで、アイドルたちが身だしなみを整える。\nおひなさまのような美しさを目指して…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1383, /*level*/1, /*item_detail*/"おひなさまごっこセット。\nお内裏様役は、なんと、ぴにゃこら太。\n隣に座って、おひなさま気分を味わおう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1384, /*level*/1, /*item_detail*/"桃の花が生けられた花瓶。\n近付くと、春を告げる香りが漂ってくる。\nアイドルのすこやかな活躍を願って、この花を飾ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1385, /*level*/1, /*item_detail*/"橘が生けられた花瓶。\n魔除けや邪気払いのために飾るもの。\nこの橘の側で、橘氏が立ち話…いや、何でもない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1386, /*level*/1, /*item_detail*/"桃色のざぶとん。ビビッドな色で、\nファンシーなお部屋にもマッチする優れもの。\n従来のざぶとんのイメージを一新する乙女のざぶとん。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1387, /*level*/1, /*item_detail*/"三色団子クッション。\nおっきな三色団子 ! …と思いきや、クッションだった。\nもちもちの触感が、食欲をそそる。そして、思わず…", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000107);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1388, /*level*/1, /*item_detail*/"桃の花が描かれた屏風。\nおそろいの家具と一緒に置けば、雅やかな\nルームに早変わり。ひなまつりを楽しく迎えよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1389, /*level*/1, /*item_detail*/"ひなまつりをテーマにした壁紙。\nふすまには大きな桃の木が描かれている。\nこの桃の木のように、大きくすこやかに成長していこう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1390, /*level*/1, /*item_detail*/"桃の花が印象的な背景。\n大きな桃の木と、たなびく雲が、一足早い春を\n感じさせてくれる。みんなで桃の花を見に行こう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1400, /*level*/1, /*item_detail*/"ピンクのクッション。\n可愛くてふわふわ。座るだけで幸せな気分になれる。\nつい、だらだらしてしまうアイドルもいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1401, /*level*/1, /*item_detail*/"ブルーのクッション。\nさわやかな色で、落ち着いた気分になれる。\n一息入れて、リラックスしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1402, /*level*/1, /*item_detail*/"オレンジのクッション。\n思わず、跳びはねたくなる弾力 ! ぼよーん !\n跳びはねるのは危ないので、座っておしゃべりしようね。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1403, /*level*/1, /*item_detail*/"学習机。\n同じ机が家にあるアイドルもいる。\nかつて学生だったアイドルは「懐かしい」とはしゃいだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1404, /*level*/1, /*item_detail*/"お菓子がのったテーブル。\nアイドルたちが持ち寄ったお菓子が並んでいる。\nお菓子と話題があれば、それで十分 ! お茶会にしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1405, /*level*/1, /*item_detail*/"クローゼット。\n中にはお気に入りのお洋服がズラリと並んでいる。\n今日は何を着ようかな？実は悩むのも楽しい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1406, /*level*/1, /*item_detail*/"ペーパーボックス。\nアイドルたちのお気に入りグッズが詰まっている。\nコスメでしょ、キーホルダーでしょ…入りきらな～い !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1407, /*level*/1, /*item_detail*/"鏡ののったウッドシェルフ。\nアイドルたちが身支度を整える場所となっている。\n『可愛くなりたい』その想いが、少女たちを可愛くする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1408, /*level*/1, /*item_detail*/"ぐうたらスペース。\nお菓子もマンガも手の届く範囲に置いて、もう一歩も\n動かない…。確かな意志を感じるスペース。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1409, /*level*/1, /*item_detail*/"ロフトベッド。\nベッドの下のスペースも有効活用できる優れモノ。\nベッドの下にいると落ち着くと好評。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1410, /*level*/1, /*item_detail*/"うちの本棚。\n並んでいるマンガは、アイドルが選んだもの。\nマブいマンガも、トキめくマンガも取り揃えている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1411, /*level*/1, /*item_detail*/"シンプルスタンドミラー。\n身支度を整えて、ここで確認 ! ポーズを決めて…OK !\nよし ! 胸を張って、いってきます !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1412, /*level*/1, /*item_detail*/"ピンクのごみ箱。\nごみ箱だって気を抜かず、お部屋の隅々まで可愛らしく。アイドルたちのそんなこだわりが感じられる一品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1413, /*level*/1, /*item_detail*/"ブルーのごみ箱。\nパステルブルーに水玉柄がさわやか。\nルームに爽快感をプラスしたいときにオススメ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1414, /*level*/1, /*item_detail*/"オレンジのごみ箱。\nビビッドカラーで、元気っ子のお部屋にオススメ。\n走り回って、蹴飛ばしたりしないようにね !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1415, /*level*/1, /*item_detail*/"仲間と対戦して盛り上がれ ! ぴにゃタワーゲーム !\nルールはかんたん。積み上げたぴにゃこら太を\n倒したほうの負けだ。今、熱き戦いが幕を開ける… !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1416, /*level*/1, /*item_detail*/"お花ラグ。\nたくさん敷いて、ルームをお花畑みたいにしよう !\nお花好きアイドルにはたまらない一品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000502);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1417, /*level*/1, /*item_detail*/"ピンクのカーテン。\n可愛いお部屋にぴったりなカーテン。\nそんなお部屋でみる夢は…きっととってもラブリー♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1418, /*level*/1, /*item_detail*/"ブルーのカーテン。\n心を落ち着かせ、集中力を高める効果がある。\nアイドルたちからは台本読みがはかどると評判。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1419, /*level*/1, /*item_detail*/"オレンジのカーテン。\nビタミンカラーで、お部屋をハツラツとした印象に。\n元気をもらえると、アイドルたちからも好評。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1420, /*level*/1, /*item_detail*/"ピンクの小花柄の壁紙。\nキュートなピンクに、小さな花をあしらったデザイン。\n可愛らしいもの愛好会認定の品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1421, /*level*/1, /*item_detail*/"ブルーの小花柄の壁紙。\nさわやかさと可愛らしさを両立したアイテム。\nいつもさわやかな気分にさせてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1422, /*level*/1, /*item_detail*/"オレンジの小花柄の壁紙。\nルームを元気で、明るい雰囲気にできるアイテム。\n今日も一日明るい気分で過ごせる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1423, /*level*/1, /*item_detail*/"白い木目調の床。\n清潔感があり、上品。どんなインテリアにも使える\n万能な床。ひとつ持っておいて、損はなし !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1424, /*level*/1, /*item_detail*/"マンションの背景。\n朝には「いってきます」の声、夕暮れには「ただいま」、\n夜には「おやすみなさい」…。明るい声が聞こえてくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1430, /*level*/1, /*item_detail*/"イベント「∀NSWER」で獲得したクッション。\n美玲御用達のブランドG&Mのイメージキャラクター\nであるデビキャのデザイン。サンキューガルモン !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1431, /*level*/1, /*item_detail*/"イベント「∀NSWER」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。個性的な\nアイドルたちはファンを惹きつけ、味方にした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1432, /*level*/1, /*item_detail*/"イベント「∀NSWER」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは\n代わりのきかない自分だけの音色を響かせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1433, /*level*/1, /*item_detail*/"イベント「∀NSWER」で獲得した純金のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは\n個性の嵐を起こし、自分だけの答えを手に入れた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
