INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1441, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n会いに来てくれたファンを、笑顔で迎えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1442, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\nアイドルたちは、私の本当の私 ! を素直に伝えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1443, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちは、「私変わった」と思える瞬間を得た。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1450, /*level*/1, /*item_detail*/"「The STARLIGHT Dream」にまつわるアイテム。\nお城が飛び出す絵本。誰もが憧れるお城が\n目の前に現れる。夢の世界を思いっきり楽しもう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1460, /*level*/1, /*item_detail*/"超☆腕章セット。\n清美の私物。これをつければ、誰でも超☆風紀委員 !\nみんなでつけて、超☆風紀委員会を結成しよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1490, /*level*/1, /*item_detail*/"お城の舞台セットの片側。\nCM連動キャンペーンのアイテム。もう片方と並べると、\nお城が現われる。アイドルは素敵な衣装を身にまとう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1515201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1491, /*level*/1, /*item_detail*/"お城の舞台セットの片側。\nCM連動キャンペーンのアイテム。もう片方と並べると、\n大きな舞台が現われる。アイドルたちを導こう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1515101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1492, /*level*/1, /*item_detail*/"スターの王子様の衣装スタンド。\nCM連動キャンペーンのアイテム。\nアイドルの大先輩が着用した衣装が飾られている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1470, /*level*/1, /*item_detail*/"ガーデンテーブル。\nおしゃべりをしたり、お弁当を食べたりできるスペース。\n多忙なアイドルもここに集って、ちょっと息抜き。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1471, /*level*/1, /*item_detail*/"ウッドベンチ。\n手入れが行き届いていて、座り心地がよい。\n木の温もりを感じながら、日向ぼっこしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000107);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1472, /*level*/1, /*item_detail*/"ウッドスツール。\n実は超高級品。その出来が素晴らしすぎて、\n立ち上がって拍手を送りたくなる。パチパチ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1473, /*level*/1, /*item_detail*/"ピクニックセット。\nアイドルが朝早くから準備をしたもの。\nみんなで食べると、いつもよりおいしく感じるね。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1474, /*level*/1, /*item_detail*/"フラワーバスケット。\n色とりどりの花で、花かんむりを作って遊ぼう。\n自分に似合うとっておきの花かんむりを作ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1475, /*level*/1, /*item_detail*/"ウッドブランコ。\n木の温もりを感じながら遊ぼう。すっかり\n大人のアイドルも、つい、はしゃいでしまう楽しさ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1476, /*level*/1, /*item_detail*/"デイジーの花が咲いているプランター。\n「純潔」の花言葉を持つデイジーを選んだアナタ…\nアナタの清らかな眼差しに、周囲も爽やかな気分に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1477, /*level*/1, /*item_detail*/"ゼラニウムの花が咲いているプランター。\n「真の友情」を花言葉に持つゼラニウムを選んだアナタ…\n友情の炎がファイヤーで、ボンバーしちゃうかも♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1478, /*level*/1, /*item_detail*/"パンジーの花が咲いているプランター。\n「私を思って」を花言葉に持つパンジーを選んだアナタ…\nパンジーの柄がだんだん顔に見えてきちゃうかも ! ？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1479, /*level*/1, /*item_detail*/"プランターボックス。\nビッグサイズで、間仕切りにも使える。\nこんなプランターはじめて… ! 感動ものの一品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1480, /*level*/1, /*item_detail*/"こいのぼり。\n空中を悠々と泳ぐこい。その姿は雄大で、圧巻。\n中に入りたいと言うアイドルを、みんなで制した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1481, /*level*/1, /*item_detail*/"木の柵の壁。\n隙間を覗くと、ちょっぴり怖い。そのスリルも、\nちょっぴりクセになる…あ、怖い…。そんな壁です。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1482, /*level*/1, /*item_detail*/"屋上庭園の床。\n木と緑が調和して、優しい雰囲気を醸している。\n都会の真ん中に、オアシスをつくろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1483, /*level*/1, /*item_detail*/"屋上庭園テーマの背景。\n屋上から、都会の風景を望みながら、ほっとひと息。\n緑あふれる楽園で、安らぎの時間を過ごそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1500, /*level*/1, /*item_detail*/"イベント「Nocturne」で獲得した撮影セット。\n瑞樹たちが撮影をしたセットと同じもの。\n時の流れをも止める美しさを、この一瞬に閉じ込めよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1515001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1501, /*level*/1, /*item_detail*/"イベント「Nocturne」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nその出逢いから、愛が鼓動を刻み始めた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1502, /*level*/1, /*item_detail*/"イベント「Nocturne」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\n駆け抜ける想いは鼓動となり、どこまでも響いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
