INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1510, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した\n小型プラネタリウム。アナスタシア愛用の品と\n同じもの。スクリーンに美しい星座を映し出す。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1515401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1511, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nアイドルたちは、伝えたい想いを胸に舞台に立った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
