INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1670, /*level*/1, /*item_detail*/"まゆのまゆ。\n防音室で中はカラオケになっている。実用性◎。\n使用中は、リボンがシュルシュルシュルッと巻きつく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1516801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1680, /*level*/1, /*item_detail*/"ファンシードリンク。ストローを差して飲もう。\nおや、ハート型のストローが…？\n一緒に飲む王子様はどんな人かな…むふ…むふふ…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1517001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1690, /*level*/1, /*item_detail*/"ウォーターヒヤシンスソファー。\n東南アジアの川や湖などに生える水草を使い、丁寧に\n編み込まれたソファー。座るだけでリゾート気分♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000103);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1691, /*level*/1, /*item_detail*/"ウォーターヒヤシンスソファーベッド。\nふかふかで最高の寝心地を誇る逸品。\nリゾートホテルに来た気分で飛び込むアイドルが続出 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1692, /*level*/1, /*item_detail*/"ヨガマット。\nリゾート地でゆったりヨガを…。そんな気分で、\nヨガができるぞ。イヤなこともデトックス !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1516901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1693, /*level*/1, /*item_detail*/"蓮の水盤。\n蓮の花が優美に浮かんでいる。その可憐で大きな花は\nステージで笑顔を咲かせるアイドルたちのよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1694, /*level*/1, /*item_detail*/"プルメリアの水盤。\nプルメリアはハワイの首飾りであるレイにも\n使用される花。甘い香りはアイドルたちを虜にする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1695, /*level*/1, /*item_detail*/"モンステラの鉢植え。\n迫力のある葉が特徴の観葉植物。その緑は見る者全てを\n寛容に受け入れ癒してくれる。さすがカンヨウ植物～ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1696, /*level*/1, /*item_detail*/"ストーンレリーフ。\n立体的で、小さくとも存在感のあるアートオブジェ。\n南国リゾート風の雰囲気を一層盛り立てるひと品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1697, /*level*/1, /*item_detail*/"床下水槽。\n南国の海さながら、床下をカラフルな魚が泳ぎまわる。\nお魚好きなアイドルも大満足。えさやりもおまかせ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1698, /*level*/1, /*item_detail*/"リゾートパーティション。\n透かし彫りが美しい。南の風を遮ることなく、空間を\n仕切る…。その働きぶり、まさにパーティションの鑑。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
