INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1820, /*level*/1, /*item_detail*/"衣装スタンド/ショータイム・イリュージョン\n一夜のイリュージョンを彩る華やかなドレス。\n煌く幻の想い出だけが、みんなの心に残り続ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1830, /*level*/1, /*item_detail*/"純金に輝くぴにゃこら太像。\nギラギラと輝き、見るものを圧倒する。\nあらゆる攻撃をものともしない強靭ボディ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1517701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1890, /*level*/1, /*item_detail*/"ピンク色の扇風機。\nクーラーと併用することで、\n効率的に室温を下げられるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1891, /*level*/1, /*item_detail*/"ピンク色の蚊取り豚。日本の夏の風物詩。\nなかなかパワフルな女の子の豚らしい。\nアイドルたちのお肌を傷つける蚊は許さない !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1892, /*level*/1, /*item_detail*/"レッドのL字ソファー。\n事務所を明るい雰囲気に演出。\n柔らかなクッションは、みんな抱きしめたがる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1893, /*level*/1, /*item_detail*/"レッドのスチールチェスト。\nたくさんの引き出しで、整理して収納するのに向いてい\nる。中身の整理され方で、いれた人の性格がわかる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1894, /*level*/1, /*item_detail*/"みけねこがお招きする、ぐうたらスペース。\n動かないためになら全力を出す、一見矛盾した意志が生\nみ出した空間。気づくと数時間経っているとのうわさ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1895, /*level*/1, /*item_detail*/"パステルカラーのロフトベッド。\nふかふかな布団は眠り心地抜群。\nベッド下でやすらぐもよし、上でしっかり休むもよし。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1896, /*level*/1, /*item_detail*/"ブルーのピクニックセット。\n準備をする時間や片付けをする時間。\nそういったものも合わせて、楽しいピクニック。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1897, /*level*/1, /*item_detail*/"青色のブランコ。\n元気なアイドルたちがはしゃいでも大丈夫な、\n頑丈な作り。でも、危ない遊び方はしないでね。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1840, /*level*/1, /*item_detail*/"青森産のりんご。忍の実家から送られてきたもの。\nみずみずしい甘さにしゃきしゃきの歯触りがgood !\nアイドルたちのおやつとして大人気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1517601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1850, /*level*/1, /*item_detail*/"スペースチェア。\n無重力空間でも快適に座れる特別製の椅子。\nひとたび座れば、たちまち気分は宇宙船の司令官 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1851, /*level*/1, /*item_detail*/"スペースポッド。肉体の時を止めて眠ることができる。\n何万年に渡る宇宙の旅も、これに入ればあっという間。\nアンチエイジングとして使うのには適していない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1517801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1852, /*level*/1, /*item_detail*/"宇宙服。宇宙に出るときに着る、身体を守るための服。\nこれを着れば、そこはもう宇宙空間 !\n身も心も、宇宙にいるかのように軽くなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1853, /*level*/1, /*item_detail*/"UFO。未確認飛行物体。ただの置物かと油断すると、\nアイドルの身に驚きの事態が…。\nアイドルたちによれば、慣れると楽しいらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1854, /*level*/1, /*item_detail*/"ロケットのオブジェ。あくまでオブジェ。\nでも、その夢とわくわく感は本物。誰かに夢を与える\n存在として、アイドルたちにもひけをとらない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1855, /*level*/1, /*item_detail*/"スペースモニター。\nホログラムによる立体投影が可能。このモニターを\n囲んでいると、地球の命運を握っている気がしてくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2016);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1856, /*level*/1, /*item_detail*/"月の石。夜空に浮かぶ月の欠片。\nロマンにあふれる一品。見た目は何の変哲もない\n石のはずだが、見る人を惹きつけて離さない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1857, /*level*/1, /*item_detail*/"青色の宇宙人のぬいぐるみ。\nタコのような愛らしい見た目をしている。\nつるっとした独特の手触りが人気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1858, /*level*/1, /*item_detail*/"ピンク色の宇宙人のぬいぐるみ。\nリボンをつけてかわいく飾っている。\n脚をむぎゅっと握りたくなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501302);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1859, /*level*/1, /*item_detail*/"たくさんのスイッチがついた配線盤。\n古いSF映画のお約束。\n技術者以外は触ってはいけない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1860, /*level*/1, /*item_detail*/"コードが露出した配線盤。\nとくに何かの機能があるわけではない。\nごちゃごちゃ絡まった配線がロマンなのだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1861, /*level*/1, /*item_detail*/"宇宙ステーションの壁。\n厳しい環境からみんなを守る強固な壁。\nスマートな外観は高い合理性に基づいたデザイン。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1862, /*level*/1, /*item_detail*/"宇宙ステーションの床。\nなにげなく踏んでいる床だが、侮ってはならない。\n軽量で耐久性に優れた、ハイテク素材の塊。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1863, /*level*/1, /*item_detail*/"宇宙空間をつくりだす背景。\n時折、宇宙をさまようものが現れる。\nついに、事務所は宇宙進出を果たした…のか？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1864, /*level*/1, /*item_detail*/"金色のUFO。別の星からわざわざやってきた、極めて\n熱心なアイドルのファン。まばゆいゴールデンボディは\nペンライト代わりのつもりらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518101);
