INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1900, /*level*/1, /*item_detail*/"ハロウィンパーティで使えるティーセット。\n紅茶と、洒落たお菓子が並んでいる。\n子どもたちのおねだりには、ここからお菓子を渡そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1901, /*level*/1, /*item_detail*/"おばけソファー。\n座っていると、おばけがお菓子を運んできてくれる。\n闇の城の主になった気分。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1902, /*level*/1, /*item_detail*/"パープルのかぼちゃスツール。\n真ん中が凹んでおり、座り心地もいい。\n鮮やかな紫色が、部屋をブキミに染める。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1903, /*level*/1, /*item_detail*/"ブルーのかぼちゃスツール。\n見た目ほど硬くはないので、長時間座っても大丈夫。\n涼しげな水色は、血の気が引いたホラー色。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1904, /*level*/1, /*item_detail*/"ハロウィンクローゼット。様々な衣装が詰まっている。\nおもしろおかしくドレスアップして、\n愉快なハロウィンナイトへ繰り出そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514002);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1905, /*level*/1, /*item_detail*/"おばけベッド。\nここで眠ると、素敵な悪夢を見ることができる。\nスリリングな恐怖体験に飢えている人に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1906, /*level*/1, /*item_detail*/"かぼちゃタワー。3段に積みあがったかぼちゃたち。\nよく見ると、それぞれに個性が感じられる。\n姿かたちは違えど、みんなパーティを楽しんでいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1907, /*level*/1, /*item_detail*/"おばけのぬいぐるみ。\nお菓子を持った、愛らしいおばけのぬいぐるみ。\n小さい子供たちにも大人気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1908, /*level*/1, /*item_detail*/"ハロウィンの飾りつけに使うガーランド。\nポップな文字と蝙蝠の羽が、壁面を彩る。\n気の向くままにたくさん飾ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1909, /*level*/1, /*item_detail*/"ブルーのハロウィンラグ。\nハロウィンパーティーを楽しく飾る。\nTRICK or TREAT ! ここでお菓子を配ろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1910, /*level*/1, /*item_detail*/"おばけの館の壁。人間は通ることができないが、\n当然、おばけはすり抜ける。油断していると、\nどこから出てくるかわかりませんよ…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1911, /*level*/1, /*item_detail*/"おばけの館の床。敷かれている上質なカーペットが\n足音を殺し、静けさを作り出す。\nシンと張りつめた空気に、思わず冷や汗が流れる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1912, /*level*/1, /*item_detail*/"おばけの館の背景。\nおばけと風船が飛び回る。\nそこは、闇の住人たちのパーティーホール。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1913, /*level*/1, /*item_detail*/"イエローのハロウィンクローゼット。\n各種コスプレも充実。たくさんの衣装の中から、\nこの夜を楽しむとっておきを探そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514002);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1914, /*level*/1, /*item_detail*/"イエローのハロウィンラグ。\nハロウィンパーティを賑やかに盛り上げる。\nTRICK or TREAT ! たまにはいたずらも !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1920, /*level*/1, /*item_detail*/"銀のイルカと熱い風(ターボ)。\n銀のイルカの背にのると、熱い風で空中に舞い上がる。\n新手のドライヤーではない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1921, /*level*/1, /*item_detail*/"銀のイルカネックレス。\nイルカのネックレスを君にあげよう。\nそれは、いつまでもふたりを繋ぐ、絆の証。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1930, /*level*/1, /*item_detail*/"ハロウィンパンプキン。\n中に入ることができる、巨大なかぼちゃの飾り物。\nハロウィンの怪人も、アイドルたちの前では大人しい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1931, /*level*/1, /*item_detail*/"イベント「Halloween♥Code」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、人々の心を照らしたいと願った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1932, /*level*/1, /*item_detail*/"イベント「Halloween♥Code」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは怪人と出会い、ランタンに火を入れた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1933, /*level*/1, /*item_detail*/"イベント「Halloween♥Code」で獲得した\n純金のトロフィー。アイドルを伝説級のステージに\n導いたＰに贈られる。心のランタンを掲げ進もう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1940, /*level*/1, /*item_detail*/"精神統一セット。\n中野有香のお墨付き。精神修練の為の場所。\n姿勢を正して瞑想し、心身を鍛えよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1941, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n自らを色づけてくれる仲間と手を取り合った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1942, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは、\n訪れる冬の意味を知り、秋風に想いを馳せた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1943, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちは困難に立ち向かい、美しく色づいた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1950, /*level*/1, /*item_detail*/"ダンボール…？\nいつの間にか置かれていた、謎のダンボール。\n一見なんの変哲もない箱だが、油断していると…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1960, /*level*/1, /*item_detail*/"プラスチック製の青いベンチ。\nトレーニングで疲れたら、ベンチに座ってひと休み。\n水分補給も忘れずにね !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000109);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1961, /*level*/1, /*item_detail*/"ルームランナー。\n軽いウォーキングからランニングまでできる。\nアイドルだけでなく、運動不足のプロデューサーにも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1962, /*level*/1, /*item_detail*/"エアロバイク。\n誰でも気軽に運動ができるトレーニング器具。\nサイクリング気分で汗を流したい人にオススメ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1963, /*level*/1, /*item_detail*/"かわいらしいピンクのバランスボール。\n座るだけで体幹を鍛えることができるすぐれもの。\n子どもたちは、跳ねて転がして大はしゃぎ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1964, /*level*/1, /*item_detail*/"爽やかなグリーンのバランスボール。\nボールの上に四つんばいになっているだけでも\n初心者には意外とつらい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1965, /*level*/1, /*item_detail*/"ダンベル。\n片手でも持てる軽いものから、本気で鍛えたい人向けの\n重いものまでバリエーションはさまざま。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1966, /*level*/1, /*item_detail*/"真っ赤な自動販売機。\n飲み物を買うと挑戦できる抽選機能つき。\nアタリが出たらもう一本 ! ", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1967, /*level*/1, /*item_detail*/"クールな青い自動販売機。\n多種多様な飲み物を取り揃えている。\n残念ながら、食品やアイスの販売はありません。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1968, /*level*/1, /*item_detail*/"自動販売機用のゴミ箱。\n飲み終わったビンや缶はきちんとゴミ箱へ !\n身の回りの美しさは、みんなの心の美しさ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1969, /*level*/1, /*item_detail*/"イエローのストレッチマット。\n柔らかなマットがあれば、硬い床の上でも大丈夫。\nトレーニングの前にしっかり体を伸ばしてケガ予防 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1970, /*level*/1, /*item_detail*/"パープルのストレッチマット。\n丸めて収納できるため、事務所の隅に置いておける。\n日ごろの柔軟が、のびやかなダンスの基礎を支える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1971, /*level*/1, /*item_detail*/"スポーツジムの壁。\n熱や湿気が籠らないよう、適度に換気を行うのが大切。\nアイドルたちの努力をいつも見守っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1972, /*level*/1, /*item_detail*/"オシャレなホワイトのバランスボール。\nとあるアイドルいわく、お腹がすいた時に見ると\n白玉だんごに見えてくるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1973, /*level*/1, /*item_detail*/"真っ白な自動販売機。\nスポーツドリンクや栄養ドリンクも完備。\n休憩の場やおしゃべりの場を提供している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1980, /*level*/1, /*item_detail*/"ファストフード店セット。\n小腹が空いたらちょっと休憩。\nみんなの寄り道スポットを事務所にも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1981, /*level*/1, /*item_detail*/"イベント「Trinity Field」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nアイドルたちは、ぶつかりあって信頼の絆を深めた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1982, /*level*/1, /*item_detail*/"イベント「Trinity Field」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは、\n誓いと信念の先にたどり着く場所を見た。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1983, /*level*/1, /*item_detail*/"イベント「Trinity Field」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちの誇りと尊敬は、大きな正三角形を象る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1990, /*level*/1, /*item_detail*/"ののの聖域（サンクチュアリ）。\n中に入ると不思議と落ち着くぅ……。\nまねくぼするアイドルが増加中。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2000, /*level*/1, /*item_detail*/"おっきな猫のぬいぐるみ。\nくわえた魚と和やかな目が愛らしい。\nもふもふしていると、自然と表情が緩む。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2010, /*level*/1, /*item_detail*/"ダンスレッスンスペース。\n最高のLIVEを届けるため、\nアイドルたちは仲間と共に日々レッスンに励んでいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2011, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n満開の花を咲かせる日を夢見ていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2012, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。レッスンの日々を仲間\nと共に乗り越え、アイドルたちは大輪の花を咲かせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2013, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちは、新たなステージへと歩み続ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2020, /*level*/1, /*item_detail*/"ローストビーフが主役のクリスマスディナーテーブル。\n暖かなキャンドルの灯りの中で、特別な人と\nプレシャスなディナータイムを。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2021, /*level*/1, /*item_detail*/"ローストチキンが主役のクリスマスディナーテーブル。\n非日常のロマンに浸って、大切な仲間たちと\nハピネスな聖夜のひと時を楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2022, /*level*/1, /*item_detail*/"クリスマスメニューが並んだビュッフェテーブル。\nあれもこれもどれも全部食べたい ! 好きなものを\n好きなだけ楽しんだら、心も体も幸せいっぱい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2023, /*level*/1, /*item_detail*/"高級感あふれる本格的なグランドピアノ。\n鍵盤から奏でられる繊細なメロディーが、\nクリスマスを明るく彩ってくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2024, /*level*/1, /*item_detail*/"雪のように真っ白なクリスマスツリー。\n金色の飾りでラグジュアリーに。\nオトナカワイイ輝きで、特別な夜に花を添える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000311);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2025, /*level*/1, /*item_detail*/"クリスマス仕様のランプ。\n木の実のようなかわいらしいランプに、\nアイドルたちがひと工夫加えて自作した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2026, /*level*/1, /*item_detail*/"シックな赤い壁紙。\nオシャレな壁紙で、いつもの事務所が高級レストランに\n早変わり。アイドルたちも自然とおすまし顔。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2027, /*level*/1, /*item_detail*/"エレガントな赤い絨毯。\nふかふかの絨毯に足を取られないよう慎重に。\n気がつけば、自然とお姫様のような佇まいに。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2028, /*level*/1, /*item_detail*/"クリスマスの都会の背景。\nビルに灯る明かりも、イルミネーションの輝きと\nひとつになって幻想的な景色を作り出す。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2029, /*level*/1, /*item_detail*/"上品さがあふれるグランドピアノ。\n羽根のように軽く美しい音色で、聴く者の心を\nふわふわ夢心地にさせてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2030, /*level*/1, /*item_detail*/"モダンな緑色の壁紙。\nクリスマスカラーに囲まれて、あっという間に楽しい\nパーティ気分。仲間たちと盛り上がろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2031, /*level*/1, /*item_detail*/"クラシカルな緑色の絨毯。\n長い毛並で触り心地もバツグン。\n柔らかく、それでいて格式を感じさせる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2032, /*level*/1, /*item_detail*/"花でキレイに飾りつけられたテーブル。\nルームに置いておくと、クリスマスの夜にサンタさんが\n特別なプレゼントを置いてくれる。", /*effect_value_1*/1, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2033, /*level*/1, /*item_detail*/"イヴ・サンタクロースが連れているトナカイ、\nブリッツェンの小屋。クリスマスに限らず、\nアイドルたちに癒しを与えるマスコット。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2040, /*level*/1, /*item_detail*/"本格的なビリヤード台。\nアイドルたちが息抜きに遊んでいる。\n狙いを定めて、一発勝負 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2050, /*level*/1, /*item_detail*/"ストラックアウト。\nパネルに向かって一球入魂、エースの座は誰の手に？\n狙え、パーフェクトゲーム ! ", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2051, /*level*/1, /*item_detail*/"イベント「冬空プレシャス」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nキラめく夜を、みんなで楽しもう。Happy Holidays !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2052, /*level*/1, /*item_detail*/"イベント「冬空プレシャス」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n大切な人と過ごす、聖なる夜にハピネスを。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2053, /*level*/1, /*item_detail*/"イベント「冬空プレシャス」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。みんなの笑顔が、何よりプレシャスな贈り物。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2060, /*level*/1, /*item_detail*/"作業がしやすい低めの作業テーブル。\n明けましておめでとう ! みんなから、どんな年賀状が\n届いているかな？今年もよろしくお願いします !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2061, /*level*/1, /*item_detail*/"金運を呼び込む山吹色のざぶとん。\nふかふか仕様で正座しても足がしびれにくい。\nずっと座っていると招き猫気分が味わえるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2062, /*level*/1, /*item_detail*/"赤いこたつとお鍋のセット。\n冬と言えばみかん、こたつ、そしてカニ !\n内と外から、身体をあたためよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2063, /*level*/1, /*item_detail*/"お正月料理が並んだキッチンカウンター。\n新年のお祝いは豪勢な料理と共に。おいしそうな匂いに\n誘われて、つまみ食いしたくなっちゃう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2064, /*level*/1, /*item_detail*/"昔懐かしいレトロなテレビ。\nリモコンはついていないので、ダイヤルを回して\nチャンネルを変えよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2065, /*level*/1, /*item_detail*/"レトロな見た目のストーブ。\nゆらゆら揺れる火が心も体もあたためてくれる。\nヤカンを乗せれば、お湯を沸かせて加湿もできる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2066, /*level*/1, /*item_detail*/"戌年をモチーフにした門松。\nはっぴーにゅーいワン ! とお出迎え。\nかわいらしい柴犬がひょっこりと顔を覗かせている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2067, /*level*/1, /*item_detail*/"市松模様の布団。\n寒い夜はぬくぬくのお布団にくるまって休もう。\n今日はどんな夢が見られるかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2068, /*level*/1, /*item_detail*/"本格的な火鉢。\nぱちぱちと弾ける火で暖を取りながら、お餅や\nお魚も焼ける優れもの。ヤケドには注意しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503503);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2069, /*level*/1, /*item_detail*/"ふたつの凧が連なった飾り。\n今年も1年元気に過ごせるように、という願いを込めた\nアイドルたちお手製の正月飾り。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2070, /*level*/1, /*item_detail*/"お正月に欠かせないしめかざり。\n飾りの種類によって願いが変わるという。\nちひろもアイドルたちと一緒に用意したらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2071, /*level*/1, /*item_detail*/"温かみを感じる土壁のパーティション。\n自然の素材のみで作られているので、\n夏は涼しく冬は暖かいらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2072, /*level*/1, /*item_detail*/"市松模様がオシャレなふすま。\n和室にぴったりのシックなデザイン。市松模様には\n事業拡大などの縁起のいい意味があるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2073, /*level*/1, /*item_detail*/"市松模様のオシャレなふすま。\nちょっとの衝撃で壊れやすい。大掃除の時は\nアイドルみんなでふすまを張り替えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2074, /*level*/1, /*item_detail*/"レトロな土壁。\n昔ながらの伝統製法で作られた、和の心を感じる壁。\n土のぬくもりが、落ち着いた空間を作り出す。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2075, /*level*/1, /*item_detail*/"お正月の風景を表す背景。\n今年ものびやかに、健やかに過ごせるようにと\n高く揚げた連凧が新年の空を彩る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2076, /*level*/1, /*item_detail*/"黄色いこたつとお鍋のセット。\nカニから出るダシの良い香りに誘われて、\nアイドルたちのお腹の虫も騒ぎ出す。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2077, /*level*/1, /*item_detail*/"かわいらしい装飾の門松。\n戌年にちなんで、アイドルたちが飾り付けた\n犬のぬいぐるみがオリジナリティあふれるアイテム。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2080, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したミニステージ。\nステージに立ってHappyなポーズを決めれば、\nパーっとお祝いの花火が打ち上がる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1520701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2081, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n記念日をサプライズでお祝いした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2082, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは、\nハートに込めたメッセージで絆を繋いだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2083, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n笑顔をくれるキミへ、心からのおめでとうを贈ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2090, /*level*/1, /*item_detail*/"衣装スタンド/ブランニュー・シーズンズ\n新たな舞台に立つ少女たちへ贈られたドレス。\n季節が色づくたび、七色に輝きを変える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2100, /*level*/1, /*item_detail*/"鷹富士茄子からもらった、銅製の茄子の置物。\n初夢に見ると良いと言われる縁起物。\n部屋に飾っていると、運気が上がった気分になれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000307);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2101, /*level*/1, /*item_detail*/"鷹富士茄子からもらった、白銀製の茄子の置物。\n金銀銅。3つの茄子を集めて飾ると、強運の女神が\n味方についてくれる……かもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000307);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2102, /*level*/1, /*item_detail*/"鷹富士茄子からもらった、純金製の茄子の置物。\nただでさえ縁起のいい茄子が金色効果で更に運気倍増。\n飾るとお金が増える効果……は残念ながらついていない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000307);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2110, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\nいつでもそりで遊べる小さな雪山。\nてっぺんから下まで一気にすべり降りれば気分も爽快 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2120, /*level*/1, /*item_detail*/"羽衣小町のふたりをイメージした和傘。\n傘を掲げて微笑めば、あっという間に小町気分。\n彩り鮮やかな衣装に着替え、美しいポーズを決めよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2121, /*level*/1, /*item_detail*/"イベント「美に入り彩を穿つ」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、歩んできた美しき日々を思い返した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2122, /*level*/1, /*item_detail*/"イベント「美に入り彩を穿つ」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは、京の心を胸に故郷へ錦を飾った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2123, /*level*/1, /*item_detail*/"イベント「美に入り彩を穿つ」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。八千代の果てまで続く、新たな歴史を刻もう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2130, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した日本庭園のセット。\nぴちぴちと集まってくる鯉が癒しを与えてくれる。\nエサのあげ過ぎには注意 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2131, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\nお世話になっている人たちに感謝の気持ちを伝えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2132, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは、\n飾らない姿を見てくれる人も傍にいることを知っていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2133, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nどんな時も信じて見守ってくれるあなたに、ありがとう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2140, /*level*/1, /*item_detail*/"ビスケットの形をしたおいしそうなテーブル。\nテーブルをモチーフにしたスイーツを\n実際に作ってきたアイドルがいるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513002);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2141, /*level*/1, /*item_detail*/"ピンクのマシュマロをイメージしたスツール。\nふわもこ素材でできたチョコレート色のシートは、\n触り心地もバツグン。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2142, /*level*/1, /*item_detail*/"ブルーのマシュマロをイメージしたスツール。\nマシュマロのような座り心地で、\n甘くとろけるリラックスタイムを提供する。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2143, /*level*/1, /*item_detail*/"チョコレートフォンデュが楽しめる、とっても大きな\nマグカップ。甘い匂いに釣られてうっかり\n落ちないよう気をつけて !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521201);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2144, /*level*/1, /*item_detail*/"チョコレートクリームが詰まったシュークリーム型の\nベッド。ふんわりあま～い匂いに包まれて、\nスイートな夢を見られるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2145, /*level*/1, /*item_detail*/"チョコクッキーのフラワーアレンジメント。\nアイシングクッキーをモチーフにした\nフェイクスイーツ。残念ながら食べられません。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2146, /*level*/1, /*item_detail*/"ホイップクリームをイメージしたガーランド。\n触るとぷにぷにしている。たくさん飾ってルームを\n可愛くデコレーションしよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2147, /*level*/1, /*item_detail*/"パンケーキをモチーフにしたラグとクッション。\nふわふわなパンケーキに身を沈めれば、\nとろけるチョコのような、甘い夢が見れる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2148, /*level*/1, /*item_detail*/"ラブリーなピンクのアイシングクッキーをイメージした\nカラータイル。置くだけでルームの中がぐっと華やかに !\nかわいらしいイメージにしたい時にぴったりのアイテム。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2149, /*level*/1, /*item_detail*/"オシャレなブルーのアイシングクッキーをイメージした\nカラータイル。パステルカラーの彩りが、\nかわいらしくもキリッと引き締めてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2150, /*level*/1, /*item_detail*/"まるでクッキーのようなパーティション。\n中身はしっかりした木製なので、\nうっかり物をぶつけてしまっても砕けません。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2151, /*level*/1, /*item_detail*/"チョコクッキーのようなパーティション。\nかじりつきたくなる、美味しそうな見た目。\nそのこだわりに、スイーツ好きも思わず感心。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2152, /*level*/1, /*item_detail*/"チョコクッキーのような壁。\nステンドグラスクッキーでできた窓から見えるのは、\nファンシーでキュートなお菓子の世界♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2153, /*level*/1, /*item_detail*/"キツネ色に焼けたクッキータイルの床。\n大きく息を吸い込めば、香ばしい匂いが漂ってきそう。\n自分が甘いデコレーションになったようなキブンに。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2154, /*level*/1, /*item_detail*/"ピンクの屋根がかわいいお菓子の家の背景。\n見渡す限りのスイーツに、思わずおなかの虫も大合唱。\n甘い空間に囲まれて、ハッピーなひとときを !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2155, /*level*/1, /*item_detail*/"ホワイトチョコクリームが詰まったシュークリーム型の\nベッド。ふわモコのクリームが優しく体を包み、疲れた心\nも体も癒してくれる。おいしい夢が見られるかも♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2156, /*level*/1, /*item_detail*/"スカイブルーの屋根がさわやかなお菓子の家の背景。\n見慣れたルームがお菓子の家に大変身 ! 色とりどりの\nスイーツに囲まれて、ハピネスな時間を過ごそう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2160, /*level*/1, /*item_detail*/"ペットボトルボーリングセット。\nちいさいアイドルたちの手づくり遊び道具。\nいっぱい遊んだあとは、いっぱいレッスン !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521501);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2170, /*level*/1, /*item_detail*/"イベント「Frost」で獲得した、いつでも\n雪遊びが楽しめる夢のようなかまくら。\n一番上手に雪だるまを作れるのは誰だ？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2171, /*level*/1, /*item_detail*/"イベント「Frost」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nアイドルたちは北の大地で恋する乙女の気持ちを学んだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2172, /*level*/1, /*item_detail*/"イベント「Frost」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\n雪を溶かすような、熱気のあるLIVEが繰り広げられた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2173, /*level*/1, /*item_detail*/"イベント「Frost」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n切ない冬の恋の歌はファンの心の中で永遠に響き続ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2180, /*level*/1, /*item_detail*/"レトロモダンなテーブルセット。お茶をすれば\nいつもの紅茶が高級な味に感じられるとか。\n淑女になった気分で優雅なティータイムを楽しみたい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2181, /*level*/1, /*item_detail*/"赤の矢絣模様がかわいらしいベッド。\nその昔、女学生の間で流行った模様。\nいつの時代も、カワイイは変わらない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2182, /*level*/1, /*item_detail*/"レトロな振り子時計。\nデジタル世代のアイドルたちには目新しい。\nチクタクチクタクと、軽快に時を刻む。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2014);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2183, /*level*/1, /*item_detail*/"白い椿があしらわれた着物のかかっている衣桁。\n白い椿の花言葉は、申し分のない魅力。\n着物を着こなし、自分だけの魅力を手に入れよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2184, /*level*/1, /*item_detail*/"かわいらしい配色のレトロモダンな箪笥。\n収納力抜群で、何をしまうか悩んでしまう。\n置かれているダルマは、運気上昇を祈願したもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2185, /*level*/1, /*item_detail*/"レトロモダンなスタンドランプ。\n灯りをともせば、カラフルなステンドグラスから\n温かな光が漏れてくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2186, /*level*/1, /*item_detail*/"壁掛け仕様のステンドグラスランプ。\n灯りをともせば、いつもの事務所が\nノスタルジックなオシャレ空間に大変身 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2187, /*level*/1, /*item_detail*/"レトロな壁掛け電話。\n受話器を取ると、時空を超えて電話が繋がりそう。\n現代っ子たちは使い方が分からないが、興味津々。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2188, /*level*/1, /*item_detail*/"レトロモダンなステンドグラスの衝立。\n光がさすと、温かな光が部屋を彩る。\nガラスでも、向こうは見えない安心仕様。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2189, /*level*/1, /*item_detail*/"レトロモダンな壁。温かみのある色の壁紙に\nオシャレな窓。窓から外を見つめれば\n見慣れた景色がノスタルジックに見えるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2190, /*level*/1, /*item_detail*/"深みのある色合いがオシャレな床。\n高級感のあるじゅうたん張りで、その格式高さから\n思わず背筋を伸ばして歩きたくなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2191, /*level*/1, /*item_detail*/"レトロモダンがテーマの背景。\nいつもの事務所が趣のあるお屋敷に大変身 !\n通ってくるアイドルたちも自然と淑女に変身する。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2192, /*level*/1, /*item_detail*/"紫の矢絣模様が目立つ、かっこいいベッド。\n美しい紫色は、高貴さを表す。\nこのベッドで眠れば、しとやかなオトナ気分。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2193, /*level*/1, /*item_detail*/"赤い椿があしらわれた着物のかかっている衣桁。\n赤い椿の花言葉は、気取らない優美さ。\nたたずまいの美しさが身につく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2200, /*level*/1, /*item_detail*/"ペンキ缶ボンバー。ステキな絵に最後の仕上げをする\nつもりが……おっちょこちょいのアイドルがうっかり\nペンキをかぶれば、新たな芸術作品の出来上がり？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521701);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2210, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した学校机。\n教科書を忘れ、ふたりで授業を受けたあの日……。\nそんな何気ない時間が、かけがえのない宝物となる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521801);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2211, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\n小学校での最後の思い出にアイドルたちが華を添えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2212, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\nアイドルたちとの日々が、子どもたちの宝物になった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2213, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nタイムカプセルを開けた時、思い出は再び輝き出す……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2220, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\nストリートダンスを踊るためのスペース。\n流れてくるイカした音楽に、心も体も弾ませ踊ろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1522001);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2230, /*level*/1, /*item_detail*/"実験道具が並ぶ実験台。\n謎の液体を調合すれば、新しい薬品ができちゃうかも？\n世紀の大発見を目指して、実験を重ねるべし !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1522301);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2231, /*level*/1, /*item_detail*/"顕微鏡が置かれた実験台。\n普段は見ることのできない、\nミクロの世界を探検できる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1522401);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2232, /*level*/1, /*item_detail*/"理科室の教卓。\n安全で楽しい科学を支える場所。\n先生はついてこないが、あるとなんだか安心する。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2233, /*level*/1, /*item_detail*/"理科室のイス。\n背もたれがないので、居眠りには不向き。\n背すじをピンと伸ばして授業を受けよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2234, /*level*/1, /*item_detail*/"理科室の戸棚。\n実験器具や薬品がぎっしりと詰まっている。\n中には、驚くほど値段の高いものも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2235, /*level*/1, /*item_detail*/"理科室の流し台。\n正確な実験をするためには、丁寧な手洗いが肝心。\nせっけんは、みんなで譲り合って使おう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1522601);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2236, /*level*/1, /*item_detail*/"リアルな人体模型。\n『よく見ていると、なにかが起きるらしい』\nと、アイドルたちの間でウワサになっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2237, /*level*/1, /*item_detail*/"理科室の黒板。\n先生が書いた分かりやすい図が書かれている。\nこれを覚えれば、テストは満点 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2238, /*level*/1, /*item_detail*/"わかりやすさが自慢の骨格標本のポスター。\nこのポスターを見ているだけで\n誰でも全身の骨の名前を覚えることができる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2239, /*level*/1, /*item_detail*/"理科室テーマの壁。\n分厚いカーテンと、大きな窓がついている。\n目立たないが、みんなをしっかり守る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2240, /*level*/1, /*item_detail*/"理科室テーマの背景。\n授業に飽きた時、外を眺めると見える景色。\n流れる雲を目で追っていると眠くなるので注意 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2241, /*level*/1, /*item_detail*/"リアルな筋肉模型。\n不気味だというアイドルもいるが、\nよく見るとつぶらな目をしていてカワイイ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2242, /*level*/1, /*item_detail*/"理科室のホワイトボード。\nテストに出るところは\nカラーペンで板書されるので要チェック ! ", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0);
