INSERT INTO `room_item_detail` VALUES (/*room_item_id*/18, /*level*/1, /*item_detail*/"世界的な有名建築家がデザインしたモダンなソファー。\n本革のイタリア製で大変高価。主に応接用として\n利用されている。この上で跳ねるとかなり怒られる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000802, /*individual_flag*/0);
