INSERT INTO `room_item_detail` VALUES (/*room_item_id*/21, /*level*/1, /*item_detail*/"事務所の安らぎを演出する観葉植物。ちひろが毎日水を\nやって大切に育てており、アイドルたちがぶつかって\n倒すたびに、にこやかに微笑みながら怒っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/22, /*level*/1, /*item_detail*/"シックで格調高い、日の出型のドイツ製アンティーク\n置き時計。ウェストミンスター式で極上の音色を奏でる。\n手巻き式で、手入れを忘れると止まるのが玉にキズ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/23, /*level*/1, /*item_detail*/"スタイリッシュなミニコンポ。デンマークのオーディオ\nブランド・メーカー製で、小さいながら本格的な音質が\n楽しめる。アイドルたちがCDを持ち込んでかけている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/24, /*level*/1, /*item_detail*/"色々な小物が収納できる、汎用性の高いキャビネット。\n書類や雑誌や写真集、アイドルたちの持ちこんだ私物など\nなんでも収納可能。気づくといっぱいになっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/25, /*level*/1, /*item_detail*/"色々な小物が収納できる、汎用性の高いキャビネット。\nガラス戸がついていて鍵がかけられるため、大切な資料も\n保管できる。鍵はちひろがデスクに保管している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/26, /*level*/1, /*item_detail*/"色々な小物が収納できる、汎用性の高いキャビネット。\n観葉植物が載せられ、殺風景になりがちな事務所に癒しを\n与える。ガラス戸がついて防犯対策も万全。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/27, /*level*/1, /*item_detail*/"会議やミーティングの際に利用されるホワイトボード。\n誰でも自由に利用できるため、気が付くと誰かの落書きが\n残っている。あまり描き過ぎるとまとめて消される。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/28, /*level*/1, /*item_detail*/"清潔感のある、シンプルな白いパーティション。広すぎる\n事務所にブースを作るための間仕切り。簡単な立てつけの\nため、あまり体重をかけて寄りかかると倒れてしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2004, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/29, /*level*/1, /*item_detail*/"ピンク地に花柄をあしらった、ラブリーなデザインの\n背景素材。アイドルたちにも大人気のピンク色は、\nモチベーションアップにつながっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/30, /*level*/1, /*item_detail*/"ホワイト地に花柄をあしらった、ラブリーなデザインの\n背景素材。清潔感あふれるホワイトは、すがすがしい\n気持ちになれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/31, /*level*/1, /*item_detail*/"ピンク地を基調としたラブリーな事務所の壁紙。\nまさしく夢見る乙女に相応しいイメージ。\n他の素材とコーディネートすると効果的。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/32, /*level*/1, /*item_detail*/"ピンク地を基調としたラブリーな事務所の床用タイル。\nピンクのチェック柄で、おとぎの国の宮殿に敷かれた\n絨毯のようなイメージで、アイドルたちにも好評。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/33, /*level*/1, /*item_detail*/"パステルピンクのラブリーでシンプルなスツール。\nやわらかな素材で座り心地もフカフカ夢心地。\nソファーに合わせて、足を乗せる台としても使用できる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/34, /*level*/1, /*item_detail*/"パステルピンクのラブリーなデザインのファブリック・\nソファー。つやつやピンクにコーディネートされた\nクッション付き。アイドルたちの憩いの場になっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/35, /*level*/1, /*item_detail*/"パステルピンクがアクセントの、ラブリーなデザインの\nガラステーブル。ソファーやスツールと合わせることで\nフェミニンで可愛い空間を演出できる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/36, /*level*/1, /*item_detail*/"パステルピンクに彩られたシングルサイズのラグマット。\n毛足が長く、フワフワと肌触りがいい。寝転がっていたら\nそのまま眠ってしまうこともしばしば…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/37, /*level*/1, /*item_detail*/"ピンク色を基調としたラブリーなチェスト。写真立てと\n観葉植物が載っている。部屋が華やかになる一品。\n引き出しの中には、アイドルたちのお気に入りグッズが。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/38, /*level*/1, /*item_detail*/"ピンク色でラブリーなデザインのビーズクッション。\n大きくてフカフカで、とても快適。このクッションに\n身をゆだねると、あまりに心地よくて立てなくなる…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/39, /*level*/1, /*item_detail*/"ピンク色でラブリーなパーティション。花をあしらった\nデザインはとてもメルヘンチック。アイドルたちが\n擦りガラス越しに覗き込みあったりして遊んでいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2004, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/40, /*level*/1, /*item_detail*/"ピンク色のシェードが可愛い、シンプルなデザインの\nフロアライト。ほのかな間接照明で、事務所がムーディで\nラブリーな雰囲気に包まれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/41, /*level*/1, /*item_detail*/"白くてモコモコないぬのぬいぐるみ。みんなの人気者。\nアイドルたちは各々、自分が勝手につけた名前で\n呼んでいるので、今のところ特に決まった名前はない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/42, /*level*/1, /*item_detail*/"ピンクのモコモコないぬのぬいぐるみ。みんなに撫でられ\n抱きつかれたりしているので、定期的に手洗い・ブローを\nしている。キレイになった後は心なしか気持ちよさそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/43, /*level*/1, /*item_detail*/"ピンク＆ホワイトの薔薇の壁掛け。キレイに咲いているが\nイミテーションフラワーなので、世話をする必要がなく\nお手軽。年少のアイドルたちが遊び道具にすることも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/44, /*level*/1, /*item_detail*/"ブルー地に宝石をモチーフにしたロゴをあしらった、\nスタイリッシュなデザインの背景素材。キレイなブルーは\n心を落ち着けてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/45, /*level*/1, /*item_detail*/"ホワイト地に宝石をモチーフにしたロゴをあしらった、\nスタイリッシュなデザインの背景素材。ブルーと\nホワイトで爽やかな気分に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/46, /*level*/1, /*item_detail*/"ブルーを基調としたスタイリッシュな事務所の壁紙。\nクールな雰囲気で、仕事もテキパキとはかどりそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/47, /*level*/1, /*item_detail*/"ブルーを基調としたクールな事務所の床用タイル。\nブルーのチェック柄で、落ち着いた雰囲気のオフィスに\nピッタリ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/48, /*level*/1, /*item_detail*/"パステルブルーのクールでシンプルなスツール。\nやや硬めの材質で、この上に座ると背筋もピンと伸びる。\n姿勢が良くなること受けあい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/49, /*level*/1, /*item_detail*/"パステルブルーのクールなデザインのファブリック・\nソファー。シンプルだが機能的。皆が座りたいので、よく\n取り合いになる。幼いアイドルは、オトナの膝の上に…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/50, /*level*/1, /*item_detail*/"パステルブルーに縁どられた、クールなガラステーブル。\n観葉植物が置かれているが、テーブルを使うときは他の\n場所にどかされる。そしてどこにあるか分からなくなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/51, /*level*/1, /*item_detail*/"パステルブルーのシングルサイズのラグマット。\nひんやりとして気持ちいいと噂が立ち、夏場は裸足で座る\nアイドルが続出。しかしそれは気のせいらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/52, /*level*/1, /*item_detail*/"ブルーを基調としたクールなチェスト。明るい色調で\n爽やかなイメージ。写真立ての中身は、アイドルたちが\n代わるがわる自分の写真を入れるため、ほぼ日替わり。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/53, /*level*/1, /*item_detail*/"ブルーでクールなデザインのビーズクッション。ひんやり\n快適、レッスンで火照った体をやさしくリフレッシュ。\n冷たく包み込む感触は、一度体験したらやめられない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/54, /*level*/1, /*item_detail*/"ブルーでクールなパーティション。宝石をイメージした\nデザインがとても神秘的。深い青の夜空をイメージした\nガラス窓には、たくさんの星明かりが輝いている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2004, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/55, /*level*/1, /*item_detail*/"ブルーのシェードがスタイリッシュな、シンプルな\nデザインのフロアライト。おちついた青い間接照明は、\n事務所を安らかな空間にしてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/56, /*level*/1, /*item_detail*/"白いシンプルなデザインのマガジンラック。アイドルの\n掲載された雑誌や情報誌が、毎週欠かさずに補充される。\n中にはアイドルたちの持ちこんだ漫画雑誌なども…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/57, /*level*/1, /*item_detail*/"ダークブラウンのシックなデザインのマガジンラック。\n大人の雑誌から子供向けまで、アイドルたちのニーズに\n合わせた様々なジャンルの雑誌が充実している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/58, /*level*/1, /*item_detail*/"白いシンプルな壁掛け式の棚。写真立てと観葉植物が\n飾られている。写真に写った黒猫は、アイドルの飼い猫\nらしい。事務所につれてきたときに撮影された1枚。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/59, /*level*/1, /*item_detail*/"ダークブラウンのシックな壁掛け式の棚。観葉植物の\n水やり当番は決まっていないが、いつの間にか水が\nやってある。みんなの世話で、植物は順調に成長中。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/60, /*level*/1, /*item_detail*/"オレンジ色に太陽をモチーフにしたロゴをあしらった\nデザインの背景素材。アイドルたちも明るい気持ちに。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/61, /*level*/1, /*item_detail*/"ホワイト地に太陽をモチーフにしたロゴをあしらった、\n明るく元気いっぱいなデザインの背景素材。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/62, /*level*/1, /*item_detail*/"オレンジを基調とした明るい事務所の壁紙。\n輝くオレンジのサンシャイン・カラーで元気になれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/63, /*level*/1, /*item_detail*/"オレンジを基調とした明るく元気いっぱいな事務所の\n床用タイル。オレンジのチェック柄で、この上に\n立っているとワクワクした気分になってくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/64, /*level*/1, /*item_detail*/"オレンジの明るくシンプルなスツール。ポヨンポヨンの\n座り心地で、上に飛び乗るとトランポリンのように弾むと\n評判。しかし調子に乗って遊ぶのは厳禁。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/65, /*level*/1, /*item_detail*/"オレンジの明るいデザインのファブリック・ソファー。\n昼寝もはかどる快適な一品。寝坊してうっかり遅刻する\nアイドルも。みかんの汁がはねても、目立たない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/66, /*level*/1, /*item_detail*/"オレンジで縁どられた、明るい印象のガラステーブル。\n天板は強化ガラスなので、めったなことでは割れないが、\nもちろん上に乗ったりしてはいけない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/67, /*level*/1, /*item_detail*/"オレンジ色で明るいイメージの、シングルサイズのラグ\nマット。ほんのりポカポカお日様の匂い。寝転がると\n干したばかりの布団のような気持ちよさだと評判。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/68, /*level*/1, /*item_detail*/"オレンジを基調とした明るいイメージのチェスト。\n引き出しの中はキチンと区分けされていて、\nアイドルたちの私物やオフィス雑貨などが入っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/69, /*level*/1, /*item_detail*/"オレンジで明るいイメージのビーズクッション。座ると\n不思議な力がみなぎってくる。ダンスレッスン前に座ると\n上手く踊れるとのジンクスがある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/70, /*level*/1, /*item_detail*/"オレンジ色のパーティション。太陽をイメージした\nデザインが個性的。陽の射す方向に設置すれば、\n活気みなぎるオレンジの陽光を浴びることができる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2004, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/71, /*level*/1, /*item_detail*/"オレンジのシェードが暖かそうな、シンプルなデザインの\nフロアライト。ぼんやりとした暖色系の間接照明で、\n事務所をリラックスできる空間に変えてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/72, /*level*/1, /*item_detail*/"オレンジのボディが眩しい、イタリアメーカーの本格的な\nロードバイク。運動不足解消のためにアイドルたちが\n乗っている。インテリアとしても利用できる優秀な一品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/73, /*level*/1, /*item_detail*/"白とオレンジを基調とした、イタリアメーカーの本格的な\nロードバイク。アイドルたちに貸し出しもしており、\nサイクリング中のため、事務所にないことも多い。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/74, /*level*/1, /*item_detail*/"アルファベットをかたどった、壁掛け用のオブジェ。\n壁をオシャレにデコレーションしたいときにもってこい。\nいたずらで変な文字列になっていることも…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/75, /*level*/1, /*item_detail*/"虹色のパステルカラーでスターをちりばめたデザインの\n背景素材。スターはアイドルの象徴。キラキラ輝けるよう\n願いをこめて…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/76, /*level*/1, /*item_detail*/"黒い背景にスターをちりばめた、モノトーンの背景素材。\nシックなイメージで、大人っぽい部屋の雰囲気に\nピッタリ合う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/77, /*level*/1, /*item_detail*/"虹色のパステルカラーでスターをちりばめたデザインの\n背景素材。小さくキラキラ輝くスターがたくさん\nちりばめられている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/78, /*level*/1, /*item_detail*/"白地に黒のラインがアクセントの、モノトーンな事務所の\n壁紙。清潔感があり静かなイメージにピッタリ。事務所が\nモダンになったと、アイドルたちからの評判は上々。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/79, /*level*/1, /*item_detail*/"黒地に白のラインがアクセントの、モノトーンな事務所の\n壁紙。シックで落ち着いた大人の雰囲気にピッタリ。\n大人の雰囲気に戸惑うアイドルたちもいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/80, /*level*/1, /*item_detail*/"白と黒のモノトーンな事務所の床用タイル。チェス盤の\nような雰囲気。自分たちが駒になってチェスをやりだす\nアイドルもいるが、だいたい家具や人に阻まれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/81, /*level*/1, /*item_detail*/"白いシンプルなスツール。同色のソファーと合わせて、\n明るく落ち着いた雰囲気を演出することができる。\n時々、アイドルたちがお立ち台として使用している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/82, /*level*/1, /*item_detail*/"黒いシンプルなスツール。同色のソファーと合わせて、\nシックでオトナの雰囲気を演出することができる。\n動かせるので、席を増やしたいときなどにも便利。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/83, /*level*/1, /*item_detail*/"白いシンプルなデザインのファブリック・ソファー。\nどんな部屋にも合うイノセントなデザインだが、汚れが\n目立つため注意が必要。皆、あまり座りたがらない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/84, /*level*/1, /*item_detail*/"黒いシンプルなデザインのファブリック・ソファー。\nシックで重厚なデザインは、事務所をオトナな雰囲気に。\nなぜかアイドルたちが足を組んで、ポーズを決めたがる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/85, /*level*/1, /*item_detail*/"白がアクセントの、明るいイメージのシンプルな\nガラステーブル。下の化粧板にお気に入りの雑貨を置いて\nディスプレイしてもオシャレ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/86, /*level*/1, /*item_detail*/"黒がアクセントの、シックなイメージのシンプルな\nガラステーブル。ソファーやスツールと合わせることで\nスタイリッシュな空間を演出できる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/87, /*level*/1, /*item_detail*/"白く毛足の長いシングルサイズのラグマット。清潔感が\nあり手触りのよい一品。汚すと怒られるので、この上に\n座るとき、アイドルたちは心なしか緊張している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/88, /*level*/1, /*item_detail*/"黒く毛足の長いシングルサイズのラグマット。たまに\nテラスで日干しされているが、取り込み忘れると夜の闇に\nまぎれ放置される。気づいたアイドルが取り込んでいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/89, /*level*/1, /*item_detail*/"白を基調とした明るいイメージのチェスト。写真立てと\n観葉植物が載っている。部屋が明るくなる一品。\n引き出しの中を整理しておかないと、ちひろに怒られる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/90, /*level*/1, /*item_detail*/"黒を基調としたシックなイメージのチェスト。引き出しの\n中には、プロデューサーの私物が収納されている。たまに\nいたずらされたり、物が増えていたりする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/91, /*level*/1, /*item_detail*/"真っ白で明るいイメージのビーズクッション。「おっきな\n大福みたいでおいしそう」と一部のアイドルから大人気。\n食べようとしたアイドルがいたが、皆に止められた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/92, /*level*/1, /*item_detail*/"真っ黒でシックなイメージのビーズクッション。\nムニュムニュもっちりとした座り心地が癖になる。かなり\n大きいので、枕投げ感覚でぶつけられると結構イタイ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/93, /*level*/1, /*item_detail*/"シックでシンプルな黒いパーティション。広い事務所に\nブースを作るための間仕切り。黒板と間違えてチョークで\n字を書こうとする人が続出。落書き禁止。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2004, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/94, /*level*/1, /*item_detail*/"白のシェードがオシャレな、シンプルなデザインの\nフロアライト。落ち着いた間接照明で、事務所を\nフェミニンな雰囲気の空間にできる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/95, /*level*/1, /*item_detail*/"黒のシェードがスタイリッシュな、シンプルなデザインの\nフロアライト。シックでムーディな間接照明で、事務所を\n大人な雰囲気の空間にできる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/96, /*level*/1, /*item_detail*/"白い新型のアロマディフューザー。エッセンシャルオイル\nを一滴たらすだけで、事務所をリラックス空間に。\n上部からモクモクと蒸気が立ち上り、加湿もしてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/97, /*level*/1, /*item_detail*/"黒い新型のアロマディフューザー。使用するアロマは、\n仕事がはかどると噂のローズマリーと、リラックスできる\nラベンダーが人気。アイドルたちが周りに群がっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/98, /*level*/1, /*item_detail*/"黒い額縁のハートの描かれたポスター。モノトーンで\nシンプルなデザイン。実は有名な現代美術画家が描いた\nらしいが、興味がないのか作者の名前を誰も知らない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/99, /*level*/1, /*item_detail*/"白いボール型の外観が特徴の、モダンなデザインの\n一人掛けチェア。オシャレな空間にピッタリの未来的な\nデザインでヨーロッパのセレブも愛用されているらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000802, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/100, /*level*/1, /*item_detail*/"黒いボール型の外観が特徴の、モダンなデザインの\n一人掛けチェア。座ると周囲の音を遮断するため、静かに\n落ち着きたい時に最適。つい居眠りしてしまうことも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000802, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/101, /*level*/1, /*item_detail*/"本田未央が持ちこんだ巨大なハンバーガーのクッション。\nお腹を空かした仲間にしばしば齧られる。具はレタスと\nビーフとチーズ。ピクルスが入っているかはわからない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000802, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/102, /*level*/1, /*item_detail*/"神崎蘭子の持ちこんだ蹄鉄のレリーフ。魔除けや幸運の\nお守りになるとして知られる蹄鉄。ゴシック調のリボンは\n蘭子がつけたもの。アレンジにより可愛らしい雰囲気に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/103, /*level*/1, /*item_detail*/"渋谷凛の持ちこんだ生花と花瓶のセット。実家のフラワー\nショップで余った花を見繕ったらしいが、アレンジメント\nのおかげで売れ残りとは思えない。センスがあると評判。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/104, /*level*/1, /*item_detail*/"双葉杏の持ちこんだフワフワモコモコのうさぎちゃん\nクッション。杏が愛する逸品だけあって、その寝心地は\n殺人的に快適。人をダメにする魔性のアイテム。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/105, /*level*/1, /*item_detail*/"アナスタシアの持ちこんだ星座の描かれた天球儀。\n神秘的な雰囲気を漂わせていて、インテリアとしても◎。\nアイドルたちがお気に入りの星座を見つけて遊んでいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/106, /*level*/1, /*item_detail*/"島村卯月の持ちこんだ可愛いデコレーションのついた\nフォトフレームとスナップショット。フレームは手作りで\n写真には仲間との忘れられない思い出が詰まっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/107, /*level*/1, /*item_detail*/"緒方智絵里の持ちこんだ四つ葉のクローバーのレリーフ。\n事務所に所属する全員に素敵な幸運が訪れるよう、\n智絵里からの強い願いがこめられている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/108, /*level*/1, /*item_detail*/"赤城みりあの持ちこんだ、落書き帳とクレヨンのセット。\n落書き帳にはアイドルたちが描いた絵がたくさん\n収められており、絵を描く才能の有無が一目瞭然。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/109, /*level*/1, /*item_detail*/"諸星きらりが持ちこんだ、ソファーのイメージチェンジを\nするためのカラフルなカバー。高級ソファーが可愛らしい\n雰囲気に。白地に赤のストライプがおしゃれ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/110, /*level*/1, /*item_detail*/"諸星きらりが持ちこんだ、ソファーのイメージチェンジを\nするためのカバー。青地に白いひし形が描かれている。\nカバーをかけてからソファーの使用頻度が上がった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000802, /*individual_flag*/0);
