INSERT INTO `room_item_detail` VALUES (/*room_item_id*/202, /*level*/1, /*item_detail*/"シンデレラプロジェクトのポスター。\n精一杯輝く星を目指し、運命のドアを開いた14人は\n夢の向こうの新しい世界を目指して羽ばたいていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/203, /*level*/1, /*item_detail*/"アニメ2ndシーズンにアイドル達が着用した衣装。\n新たな自分に会いに行くためのドレス。\n困難に立ち向かう少女達の意志が込められている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/204, /*level*/1, /*item_detail*/"アニメ1stシーズンにアイドル達が着用した衣装。\nお姫様のように美しく輝く純白のドレス。\n夢を願う純粋な気持ちで形作られている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/205, /*level*/1, /*item_detail*/"new generationsの好物が並べられたテーブル。\nフィナーレのあとは打ち上げパーティーを開こう。\nアイドル達もクラッカーを鳴らして盛り上がる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/210, /*level*/1, /*item_detail*/"イベント「アタシポンコツアンドロイド」で獲得した\nクッション。ナナがウサミン星から乗ってきたという\nロケットをイメージしている。食べられません。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000803, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/211, /*level*/1, /*item_detail*/"イベント「アタシポンコツアンドロイド」で獲得した、\n銅製のトロフィー。かなり高評価のセッションを\n演出した人に贈られる。ポンコツでは入手できない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/212, /*level*/1, /*item_detail*/"イベント「アタシポンコツアンドロイド」で獲得した、\n白銀のトロフィー。名人級のセッションをやり遂げた人に\n贈られる。刻まれたシリアルはオンリーワン。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/213, /*level*/1, /*item_detail*/"イベント「アタシポンコツアンドロイド」で獲得した、\n純金のトロフィー。伝説級の超セッションを\n成し遂げた人だけに贈られる珠玉の逸品。超有機体製。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/220, /*level*/1, /*item_detail*/"Ｐが仮眠するためのカラフルなベッド。\n枕がたくさんあるので枕投げが始まることもしばしば。\n遊んだらちゃんと元に戻しましょう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/221, /*level*/1, /*item_detail*/"Ｐが仮眠するためのホワイトのベッド。\n深夜の収録や早朝のロケなどのために置かれている…\nはずだが、アイドルたちが遊ぶ場所になっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/222, /*level*/1, /*item_detail*/"Ｐが仮眠するためのブラックのベッド。\n誰かが眠っているときは静かにするのが暗黙のルール。\n時々ドッキリを仕掛けられるので安心はできない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/223, /*level*/1, /*item_detail*/"キャラクターの形をしたベッド。\n飛び込んだときの弾力は抜群だが、何だか悪いことをした\n気分になるので、皆、そっと乗ることにしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/224, /*level*/1, /*item_detail*/"ピンクの花が植えられている鉢植え。\n心優しいアイドル達が水やりをしているが、優しさが\n溢れてしまうことも。根腐れしないよう注意が必要。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/225, /*level*/1, /*item_detail*/"ブルーの花が植えられている鉢植え。\n花に詳しいアイドルが世話をするのが目撃されている。\n大きな花を咲かせたのはアイドル達の優しさの証明。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/226, /*level*/1, /*item_detail*/"オレンジの花が植えられている鉢植え。\n花好きなアイドルがこの花についてウキウキしながら\n語っていることも。育て方にはコツがいるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/227, /*level*/1, /*item_detail*/"緑が生い茂るプランター。\n目に疲れを覚えたらこのプランターの前で癒やされよう。\nきっとリフレッシュできるはず。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/228, /*level*/1, /*item_detail*/"カラフルな花が咲いているプランター。\n花の種類は相性や彩りを考えて、アイドルによって\n選ばれたもの。世話は気づいた人がしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/229, /*level*/1, /*item_detail*/"ダークブラウンのくまのぬいぐるみ。\nみんなが好き勝手呼ぶので、くま、テディ、大五郎など\n名前がたくさんあるが、文句ひとつ言わず座っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/230, /*level*/1, /*item_detail*/"ライトブラウンのくまのぬいぐるみ。\nアイドル達の会話にも黙って耳を傾ける善良なくま。\nアイドル達が人知れず流した涙も知っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/231, /*level*/1, /*item_detail*/"ダンボールの箱。かなり重い。\n何が入っているか誰も知らず、怖くて開けられないまま\n放置されている。噂によると、ちひろの……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/232, /*level*/1, /*item_detail*/"積み上げられたダンボールの箱。上の箱は開けたが\n下の箱は手つかず。あるアイドルが悪い予感がすると\n言ったので、そのままになってしまっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/233, /*level*/1, /*item_detail*/"コンクリートの壁。投球練習もできてしまうが、\n実際にやるとちひろやトレーナーに叱られるため、\n誰もやらなくなった。よく見ると的を消した跡が……。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/234, /*level*/1, /*item_detail*/"コンクリートの床。かなり頑丈で、飛び跳ねても\n下の階に音が響かない。しかし、あまり暴れすぎると\nちひろが静かに迫ってくるので、加減は大事。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/240, /*level*/1, /*item_detail*/"ハロウィン風のクラシックなソファー。\nくもの巣はプリントなので安心して座れる。\n魔女っ娘ボウシで変装ごっこもできちゃうかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/241, /*level*/1, /*item_detail*/"ハロウィン風のパンプキンチェアー。\nカボチャの顔が怖くて座れないアイドルもいるらしい。\nよくみると愛嬌のある顔をしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000104, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/242, /*level*/1, /*item_detail*/"パープルのハロウィンゴースト風チェアー。\n背もたれのおばけは、顔色は悪いがたれ目でカワイイと\nアイドル達から意外に人気があるとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000104, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/243, /*level*/1, /*item_detail*/"ハロウィンをイメージしたティーセット。\nおばけもカボチャも楽しく食べちゃおう。\nティースタンドに何が並んでいても驚かないこと。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/244, /*level*/1, /*item_detail*/"Ｐが仮眠するためのベッド。ハロウィン風。\nカボチャの王様が笑いながら寝ているように見えて\n不気味。アイドル達はクッションをそっとどかして使う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/245, /*level*/1, /*item_detail*/"ハロウィン風のナイトキャンドル。\nコウモリが飛んでいるように見えるため、\n虫とりあみで捕まえようとしたアイドルもいるとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/246, /*level*/1, /*item_detail*/"オトメの愛がつまったカボチャのバスケット。\nアイドルの手作りお菓子がたくさん入っている。\nフタを開けた瞬間からお菓子の争奪戦が始まる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/247, /*level*/1, /*item_detail*/"ハロウィン風のライトスタンド。\nカボチャの形をしており、点いたりつかなかったりするが\n夜中にひとりでに光っていることも。気分屋らしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501702, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/248, /*level*/1, /*item_detail*/"クモの巣が張った本棚。\n並んでいる本は洋書や魔導書だが、あくまで雰囲気を\n盛り上げるためのもの。掃除はまめにしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500502, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/249, /*level*/1, /*item_detail*/"かぼちゃの置物。\nイスのように座れるが、一部のアイドルは放り投げる。\nどんな扱いを受けても不気味に笑い続けている…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/250, /*level*/1, /*item_detail*/"かぼちゃのフェンス。\nカボチャとコウモリがあしらわれていてホラー風味。\nいつでも見ている…。そんな声が聞こえてきそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/251, /*level*/1, /*item_detail*/"おばけのぬいぐるみ。ハロウィン風のアイテムなのに\n不気味さを感じさせない可愛らしさ。\nホラーが苦手なアイドル達も思わず抱きしめる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/252, /*level*/1, /*item_detail*/"棺桶。中で眠ることも出来るが寝心地は保証できない。\nキノコの栽培施設だと思えば怖くないので、勇気を出して\n開けてみよう。中には夢が詰まっている…かもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/253, /*level*/1, /*item_detail*/"ハロウィンをイメージした壁紙。\nコウモリやカボチャがあしらわれている。アイドルが\n持ち込んだトカゲが張りついていることも…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/254, /*level*/1, /*item_detail*/"ハロウィンをイメージした床。舞踏会のような\n雰囲気の絨毯。事務所に入るときにスカートの裾を\n持っておじぎをすれば、まるでお姫様気分♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/255, /*level*/1, /*item_detail*/"ハロウィンな雰囲気をかもしだす背景。\nビルはおばけのすみかになり、カボチャ達が笑いだす。\n遠く見える城では甘くて素敵な舞踏会が開かれる…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/260, /*level*/1, /*item_detail*/"凸レーションwith城ヶ崎美嘉のポスター。\nCDを購入した人だけが貰える限定の一枚。\n私色の笑顔を見つけた四人の収録帰りのオフショット。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
