INSERT INTO `room_item_detail` VALUES (/*room_item_id*/910, /*level*/1, /*item_detail*/"宝石展示用のショーケース。\n最先端のセキュリティで守られている。\nマキノの集めたデータがあれば攻略可能らしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/920, /*level*/1, /*item_detail*/"カフェカウンター。\n事務所をカフェ風にできる。手の空いたアイドルが\n拭いているので、いつもキレイに保たれている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/921, /*level*/1, /*item_detail*/"コーヒーセットが置かれたカフェカウンター。\n置かれたコーヒーはブラックで、大人の味がする。\n苦味が苦手な人は無理をせず、砂糖を入れよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/922, /*level*/1, /*item_detail*/"カフェキッチン。\nこだわりの豆を挽き、味わい深いコーヒーを淹れることが\nできる。淹れるアイドルによって微妙に味が違う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/923, /*level*/1, /*item_detail*/"ケーキが並ぶショーケース。\n一番人気はガトーショコラ。一つに絞れずに、\n全種類を食べてしまう甘党のアイドルがいるとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/924, /*level*/1, /*item_detail*/"カフェチェア。\nカウンターにぴったりのカフェチェア。\n背が低いアイドルは座るのにちょっと苦労する。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/925, /*level*/1, /*item_detail*/"ホワイトのL字ソファー。\n座り心地はマシュマロのよう。\nだからといって、食べられません !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/926, /*level*/1, /*item_detail*/"モスグリーンのL字ソファー。\n事務所をシックな雰囲気に演出。\nマシュマロ触感のクッションには、なぜか歯型が…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/927, /*level*/1, /*item_detail*/"ホワイトのカフェソファー。\n普段忙しいアイドルも、ここでほっと一息。\n自分の時間を楽しむアイドルの姿が見られる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/928, /*level*/1, /*item_detail*/"モスグリーンのカフェソファー。\nここでアイドルがタブレットを操作していることもある。\n流行のファッションや音楽をチェックしているらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/929, /*level*/1, /*item_detail*/"立て看板。本日のコーヒー豆の説明が書かれている。\n幼いアイドルにもわかりやすいように、\n漢字にはふりがなが振ってある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/930, /*level*/1, /*item_detail*/"ブリキのゴミ箱。\nゴミ当番はアイドルたちで持ち回りとなっている。\n忘れないように、みんなで声をかけ合おう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/931, /*level*/1, /*item_detail*/"赤い鉢の観葉植物。\nアイドルたちが隠れてしまうくらい大きい。\nかくれんぼのとき、後ろに回ってみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/932, /*level*/1, /*item_detail*/"白い鉢の観葉植物。\n大きくて存在感がある。たくさん並べて、\nルームをジャングルみたいにするのもいいかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/933, /*level*/1, /*item_detail*/"スチールチェスト。\n引き出しがたくさんついていて、いっぱい収納できる。\nどこに何をしまったか忘れないようにね。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/934, /*level*/1, /*item_detail*/"スタンドライト。\n間接照明で演出されたおしゃれな雰囲気は、\n20代のアイドルたちにとても好評。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/935, /*level*/1, /*item_detail*/"ペンダントランプ。\n壁に取り付けるおしゃれな照明。\nぶら下がったりしないでね。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2009, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/936, /*level*/1, /*item_detail*/"2段ウォールシェルフ。\nカップやティーポットが置かれている。\n紅茶党のために、こだわりの茶葉も用意されている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/937, /*level*/1, /*item_detail*/"壁掛けメニュー。\nドリンクメニューとフードメニューが書かれている。\n本日限定メニューもあるので、要チェック。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/938, /*level*/1, /*item_detail*/"ホワイトのアートボード。\n白い部分をオレンジに塗ろうとするアイドルもいたが、\nちひろがにっこり笑ったのでやめた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/939, /*level*/1, /*item_detail*/"ブラックのアートボード。\nコーヒーの葉が描かれている。アイドルたちは、\nこれを見るとなぜかコーヒーが飲みたくなるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/940, /*level*/1, /*item_detail*/"レンガの壁。\nしっかりとした造りで、雨にも風にも負けない。\nだからといってイタズラしてはいけない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/941, /*level*/1, /*item_detail*/"ガラス窓のあるレンガの壁。窓際にいると、\nオープンカフェにいるような気分を味わえる。\nコーヒー片手に窓の外を眺めるのもいいかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/950, /*level*/1, /*item_detail*/"手作りアップルパイオーブン。\n音楽を聴きながら、アップルパイを焼こう。\n焦がさず、おいしく焼けるかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1510001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/960, /*level*/1, /*item_detail*/"スターがやってくるドア。\nCM連動キャンペーンのアイテム。\nアイドルの大先輩が期間限定で遊びにきてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/970, /*level*/1, /*item_detail*/"イベント「ラブレター」で獲得したデスク。\nレターセットやカラーペンが置かれており、\nここで色とりどりの想いをしたためることができる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/971, /*level*/1, /*item_detail*/"イベント「ラブレター」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルはふわふわっと憧れを描いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/972, /*level*/1, /*item_detail*/"イベント「ラブレター」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\nアイドルには今すぐ伝えたい想いがあった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/973, /*level*/1, /*item_detail*/"イベント「ラブレター」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルはファンにハートを伝えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/980, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した花屋セット。\n花は凛の実家の花屋から仕入れたもの。\n事務所を季節のお花で色とりどりにしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/981, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちの夢がまわり出した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
