INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1080, /*level*/1, /*item_detail*/"旅館にあるようなちゃぶ台。\nお茶と温泉まんじゅうが置いてある。\n日頃の疲れも吹っ飛ぶ美味しさ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1510901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1081, /*level*/1, /*item_detail*/"旅館に置いてあるテレビ。\nお金を入れると映るレトロ感たっぷりのアイテム。\n調子が悪いときは、あの必殺技で直してみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1082, /*level*/1, /*item_detail*/"小型の冷蔵庫。\n中には、多種多様なドリンクがいつでも揃っている。\n腰に手を当てて飲む牛乳は格別。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1083, /*level*/1, /*item_detail*/"ししおどし。\n風流な音をルームに響かせ、癒しを与える。\n規則的な動きが面白くて、つい見入ってしまうことも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2015, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1084, /*level*/1, /*item_detail*/"もみじの鉢植え。\n真っ赤に色づいて、秋の深まりを教えてくれる。\n自分の手と葉っぱのどちらが大きいか比べてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1085, /*level*/1, /*item_detail*/"旅館の夕食がのったお膳セット。\n海の幸と山の幸をふんだんに使った食事が並んでいる。\n恐れ多くて、誰も手をつけられないという…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1086, /*level*/1, /*item_detail*/"障子戸。\n間仕切りとして使える。この近くで遊ぶときは\n障子を破らないように、注意しましょう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1087, /*level*/1, /*item_detail*/"もみじ模様が描かれている障子戸。\n絵が得意なアイドルが描いたものらしいが、\n恥ずかしいので、本人は秘密にしてほしいらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1088, /*level*/1, /*item_detail*/"床の間。高そうな壺が飾られている。\nこの壺の中に硬貨を入れると、願いが叶うという\nジンクスがある。効果があるといいね。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1089, /*level*/1, /*item_detail*/"旅館風の壁。\nいつものルームが和みの空間に早変わり。\nここで日頃の疲れを癒して、またお仕事を頑張ろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
