INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1894, /*level*/1, /*item_detail*/"みけねこがお招きする、ぐうたらスペース。\n動かないためになら全力を出す、一見矛盾した意志が生\nみ出した空間。気づくと数時間経っているとのうわさ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1895, /*level*/1, /*item_detail*/"パステルカラーのロフトベッド。\nふかふかな布団は眠り心地抜群。\nベッド下でやすらぐもよし、上でしっかり休むもよし。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513801, /*individual_flag*/0);
