INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1851, /*level*/1, /*item_detail*/"スペースポッド。肉体の時を止めて眠ることができる。\n何万年に渡る宇宙の旅も、これに入ればあっという間。\nアンチエイジングとして使うのには適していない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1517801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1852, /*level*/1, /*item_detail*/"宇宙服。宇宙に出るときに着る、身体を守るための服。\nこれを着れば、そこはもう宇宙空間 !\n身も心も、宇宙にいるかのように軽くなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1853, /*level*/1, /*item_detail*/"UFO。未確認飛行物体。ただの置物かと油断すると、\nアイドルの身に驚きの事態が…。\nアイドルたちによれば、慣れると楽しいらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1854, /*level*/1, /*item_detail*/"ロケットのオブジェ。あくまでオブジェ。\nでも、その夢とわくわく感は本物。誰かに夢を与える\n存在として、アイドルたちにもひけをとらない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1855, /*level*/1, /*item_detail*/"スペースモニター。\nホログラムによる立体投影が可能。このモニターを\n囲んでいると、地球の命運を握っている気がしてくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2016, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1856, /*level*/1, /*item_detail*/"月の石。夜空に浮かぶ月の欠片。\nロマンにあふれる一品。見た目は何の変哲もない\n石のはずだが、見る人を惹きつけて離さない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1857, /*level*/1, /*item_detail*/"青色の宇宙人のぬいぐるみ。\nタコのような愛らしい見た目をしている。\nつるっとした独特の手触りが人気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1858, /*level*/1, /*item_detail*/"ピンク色の宇宙人のぬいぐるみ。\nリボンをつけてかわいく飾っている。\n脚をむぎゅっと握りたくなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1859, /*level*/1, /*item_detail*/"たくさんのスイッチがついた配線盤。\n古いSF映画のお約束。\n技術者以外は触ってはいけない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1860, /*level*/1, /*item_detail*/"コードが露出した配線盤。\nとくに何かの機能があるわけではない。\nごちゃごちゃ絡まった配線がロマンなのだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1861, /*level*/1, /*item_detail*/"宇宙ステーションの壁。\n厳しい環境からみんなを守る強固な壁。\nスマートな外観は高い合理性に基づいたデザイン。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1862, /*level*/1, /*item_detail*/"宇宙ステーションの床。\nなにげなく踏んでいる床だが、侮ってはならない。\n軽量で耐久性に優れた、ハイテク素材の塊。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1863, /*level*/1, /*item_detail*/"宇宙空間をつくりだす背景。\n時折、宇宙をさまようものが現れる。\nついに、事務所は宇宙進出を果たした…のか？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1864, /*level*/1, /*item_detail*/"金色のUFO。別の星からわざわざやってきた、極めて\n熱心なアイドルのファン。まばゆいゴールデンボディは\nペンライト代わりのつもりらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1865, /*level*/1, /*item_detail*/"青色に塗られたロケットのオブジェ。\n宇宙に思いを馳せる夢の象徴。きっといつの日か、\nはるか彼方へ飛び立っていくときがくる…はず。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1870, /*level*/1, /*item_detail*/"イベント「Twin☆くるっ★テール」で獲得した\nテーブル。美嘉がよくファッション誌を読んでいる。\nここで研究すれば、カリスマJKのセンスが手に入るかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1517901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1871, /*level*/1, /*item_detail*/"イベント「Twin☆くるっ★テール」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nユニットとなったふたりは、アイドルとして並び立った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1872, /*level*/1, /*item_detail*/"イベント「Twin☆くるっ★テール」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\n自分を見つめ直したふたりは心に浮かんだ未来を信じた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1873, /*level*/1, /*item_detail*/"イベント「Twin☆くるっ★テール」で獲得した\n純金のトロフィー。アイドルを伝説級のステージに\n導いたＰに贈られる。ふたりの物語は、きらきらと輝く。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1880, /*level*/1, /*item_detail*/"忍者御用達、隠れ身の術に用いる布。\n周囲の景色に溶け込み、姿を隠すことができる。\n悪用しようとすると、あやめに成敗される。ニンッ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1881, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nアイドルたちは、淡い恋の香りに頬を染めた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1882, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\nアイドルたちは、苦い別離と悲恋の涙を知った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1883, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちは、かけがえのない愛を贈った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1900, /*level*/1, /*item_detail*/"ハロウィンパーティで使えるティーセット。\n紅茶と、洒落たお菓子が並んでいる。\n子どもたちのおねだりには、ここからお菓子を渡そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1901, /*level*/1, /*item_detail*/"おばけソファー。\n座っていると、おばけがお菓子を運んできてくれる。\n闇の城の主になった気分。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1902, /*level*/1, /*item_detail*/"パープルのかぼちゃスツール。\n真ん中が凹んでおり、座り心地もいい。\n鮮やかな紫色が、部屋をブキミに染める。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1903, /*level*/1, /*item_detail*/"ブルーのかぼちゃスツール。\n見た目ほど硬くはないので、長時間座っても大丈夫。\n涼しげな水色は、血の気が引いたホラー色。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1904, /*level*/1, /*item_detail*/"ハロウィンクローゼット。様々な衣装が詰まっている。\nおもしろおかしくドレスアップして、\n愉快なハロウィンナイトへ繰り出そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514002, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1905, /*level*/1, /*item_detail*/"おばけベッド。\nここで眠ると、素敵な悪夢を見ることができる。\nスリリングな恐怖体験に飢えている人に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1906, /*level*/1, /*item_detail*/"かぼちゃタワー。3段に積みあがったかぼちゃたち。\nよく見ると、それぞれに個性が感じられる。\n姿かたちは違えど、みんなパーティを楽しんでいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1907, /*level*/1, /*item_detail*/"おばけのぬいぐるみ。\nお菓子を持った、愛らしいおばけのぬいぐるみ。\n小さい子供たちにも大人気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1908, /*level*/1, /*item_detail*/"ハロウィンの飾りつけに使うガーランド。\nポップな文字と蝙蝠の羽が、壁面を彩る。\n気の向くままにたくさん飾ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1909, /*level*/1, /*item_detail*/"ブルーのハロウィンラグ。\nハロウィンパーティーを楽しく飾る。\nTRICK or TREAT ! ここでお菓子を配ろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1910, /*level*/1, /*item_detail*/"おばけの館の壁。人間は通ることができないが、\n当然、おばけはすり抜ける。油断していると、\nどこから出てくるかわかりませんよ…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1911, /*level*/1, /*item_detail*/"おばけの館の床。敷かれている上質なカーペットが\n足音を殺し、静けさを作り出す。\nシンと張りつめた空気に、思わず冷や汗が流れる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1912, /*level*/1, /*item_detail*/"おばけの館の背景。\nおばけと風船が飛び回る。\nそこは、闇の住人たちのパーティーホール。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1913, /*level*/1, /*item_detail*/"イエローのハロウィンクローゼット。\n各種コスプレも充実。たくさんの衣装の中から、\nこの夜を楽しむとっておきを探そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1514002, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1914, /*level*/1, /*item_detail*/"イエローのハロウィンラグ。\nハロウィンパーティを賑やかに盛り上げる。\nTRICK or TREAT ! たまにはいたずらも !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1920, /*level*/1, /*item_detail*/"銀のイルカと熱い風(ターボ)。\n銀のイルカの背にのると、熱い風で空中に舞い上がる。\n新手のドライヤーではない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1921, /*level*/1, /*item_detail*/"銀のイルカネックレス。\nイルカのネックレスを君にあげよう。\nそれは、いつまでもふたりを繋ぐ、絆の証。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1930, /*level*/1, /*item_detail*/"ハロウィンパンプキン。\n中に入ることができる、巨大なかぼちゃの飾り物。\nハロウィンの怪人も、アイドルたちの前では大人しい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1931, /*level*/1, /*item_detail*/"イベント「Halloween♥Code」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、人々の心を照らしたいと願った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1932, /*level*/1, /*item_detail*/"イベント「Halloween♥Code」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは怪人と出会い、ランタンに火を入れた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1933, /*level*/1, /*item_detail*/"イベント「Halloween♥Code」で獲得した\n純金のトロフィー。アイドルを伝説級のステージに\n導いたＰに贈られる。心のランタンを掲げ進もう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1940, /*level*/1, /*item_detail*/"精神統一セット。\n中野有香のお墨付き。精神修練の為の場所。\n姿勢を正して瞑想し、心身を鍛えよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1941, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n自らを色づけてくれる仲間と手を取り合った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1942, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは、\n訪れる冬の意味を知り、秋風に想いを馳せた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1943, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちは困難に立ち向かい、美しく色づいた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1950, /*level*/1, /*item_detail*/"ダンボール…？\nいつの間にか置かれていた、謎のダンボール。\n一見なんの変哲もない箱だが、油断していると…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1960, /*level*/1, /*item_detail*/"プラスチック製の青いベンチ。\nトレーニングで疲れたら、ベンチに座ってひと休み。\n水分補給も忘れずにね !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000109, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1961, /*level*/1, /*item_detail*/"ルームランナー。\n軽いウォーキングからランニングまでできる。\nアイドルだけでなく、運動不足のプロデューサーにも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1962, /*level*/1, /*item_detail*/"エアロバイク。\n誰でも気軽に運動ができるトレーニング器具。\nサイクリング気分で汗を流したい人にオススメ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1963, /*level*/1, /*item_detail*/"かわいらしいピンクのバランスボール。\n座るだけで体幹を鍛えることができるすぐれもの。\n子どもたちは、跳ねて転がして大はしゃぎ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1964, /*level*/1, /*item_detail*/"爽やかなグリーンのバランスボール。\nボールの上に四つんばいになっているだけでも\n初心者には意外とつらい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1965, /*level*/1, /*item_detail*/"ダンベル。\n片手でも持てる軽いものから、本気で鍛えたい人向けの\n重いものまでバリエーションはさまざま。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1966, /*level*/1, /*item_detail*/"真っ赤な自動販売機。\n飲み物を買うと挑戦できる抽選機能つき。\nアタリが出たらもう一本 ! ", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1967, /*level*/1, /*item_detail*/"クールな青い自動販売機。\n多種多様な飲み物を取り揃えている。\n残念ながら、食品やアイスの販売はありません。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1968, /*level*/1, /*item_detail*/"自動販売機用のゴミ箱。\n飲み終わったビンや缶はきちんとゴミ箱へ !\n身の回りの美しさは、みんなの心の美しさ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1969, /*level*/1, /*item_detail*/"イエローのストレッチマット。\n柔らかなマットがあれば、硬い床の上でも大丈夫。\nトレーニングの前にしっかり体を伸ばしてケガ予防 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1970, /*level*/1, /*item_detail*/"パープルのストレッチマット。\n丸めて収納できるため、事務所の隅に置いておける。\n日ごろの柔軟が、のびやかなダンスの基礎を支える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1971, /*level*/1, /*item_detail*/"スポーツジムの壁。\n熱や湿気が籠らないよう、適度に換気を行うのが大切。\nアイドルたちの努力をいつも見守っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1972, /*level*/1, /*item_detail*/"オシャレなホワイトのバランスボール。\nとあるアイドルいわく、お腹がすいた時に見ると\n白玉だんごに見えてくるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1973, /*level*/1, /*item_detail*/"真っ白な自動販売機。\nスポーツドリンクや栄養ドリンクも完備。\n休憩の場やおしゃべりの場を提供している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1980, /*level*/1, /*item_detail*/"ファストフード店セット。\n小腹が空いたらちょっと休憩。\nみんなの寄り道スポットを事務所にも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1519201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1981, /*level*/1, /*item_detail*/"イベント「Trinity Field」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nアイドルたちは、ぶつかりあって信頼の絆を深めた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/1982, /*level*/1, /*item_detail*/"イベント「Trinity Field」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは、\n誓いと信念の先にたどり着く場所を見た。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
