INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2312, /*level*/1, /*item_detail*/"パッションな回答席。\nクイズ番組などでよく見かける回答席。\n笑いに貪欲。元気に回答を叫ぼう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1522901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2320, /*level*/1, /*item_detail*/"可愛らしいイルカのソファー。\nその背に身を沈めると、爽やかな潮騒が聴こえるよう。\n夏の海に想いを馳せて、身も心もリフレッシュ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2321, /*level*/1, /*item_detail*/"ミズクラゲのクッション。\nぷにっと不思議な弾力が、みんなを虜に。\n抱きしめていると、波間を漂う安らぎを得られる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2322, /*level*/1, /*item_detail*/"チンアナゴのぬいぐるみ。\nにゅっと突き出した、愛らしい顔が魅力。\n疲れたときは、一緒にゆらゆらしながら頭を撫でよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2323, /*level*/1, /*item_detail*/"ニシキアナゴのぬいぐるみ。\nしましま模様が目立つ、ファンキーなやつ。\nチンアナゴの仲間たちの、盛り上げ隊長。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2324, /*level*/1, /*item_detail*/"熱帯魚が泳いでいる水槽。\nお世話や掃除は持ち回り。\n事務所の子どもたちの人気者。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2325, /*level*/1, /*item_detail*/"タカアシガ二が、でんと居座る水槽。\n硬い甲羅と大きなハサミが格好いい。\nお腹が空いている人は、近寄っちゃダメ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2326, /*level*/1, /*item_detail*/"カラフルなジェリーフィッシュが漂う柱水槽。\n赤、青、白……天然のイルミネーションが、\n幻想的に事務所を彩る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2327, /*level*/1, /*item_detail*/"小型の、熱帯魚の水槽。\n掃除の手間が少なくて済む、お手軽サイズ。\n事務所の中に現れた、小さな海を楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2328, /*level*/1, /*item_detail*/"小さい熱帯魚が泳ぐ水槽。\nちょこちょこと動く熱帯魚たちは、ずっと見ていられる。\n気づいたら時間が経っているので要注意 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2329, /*level*/1, /*item_detail*/"ミズダコを観察できる水槽。\nうねうねと柔らかい身体は、実はほとんど筋肉。\nアイドルたちの憧れの的……にはならなかった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2330, /*level*/1, /*item_detail*/"タツノオトシゴに出会える水槽。\n竜を思わせるその独特な見た目がみんなに人気。\nゆったりとした泳ぎには貫録さえ感じられる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2331, /*level*/1, /*item_detail*/"ヒトデラグ。\n真っ赤なヒトデの形をしたラグ。\n明るくカラフルに、海の底を演出できる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2332, /*level*/1, /*item_detail*/"海のいきものが描かれた壁紙。\n水音の静けさに、魚のパレードが映える。\n地上の喧騒を離れ、海の安らぎを楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2333, /*level*/1, /*item_detail*/"海を思わせる床。\n広大で澄み渡った水面が、事務所を涼やかに彩る。\n溺れたりはしないので、安心しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2334, /*level*/1, /*item_detail*/"格好いいシャチのソファー。\nシャチは、海のギャングとも呼ばれる暴れん坊。\nその背に乗ると、海の王者になったキブン。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2335, /*level*/1, /*item_detail*/"赤いクラゲのクッション。\n毒々しい見た目だが、安全なのでご安心。\nかさのぷにぷににみんな癒される。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2340, /*level*/1, /*item_detail*/"イベント「Spring Screaming」で獲得したバドミントン\nのセット。気分転換に身体を動かしたいときにおすすめ。\n水分補給と運動後のクールダウンを忘れずに !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523201, /*individual_flag*/0);
