INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2342, /*level*/1, /*item_detail*/"イベント「Spring Screaming」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちの叫びは春のざわめきを吹き飛ばした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
