INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2353, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n彼女たちが繋いだ縁は、今後も途切れることはない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2360, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\n昔懐かしのテーブルゲーム機。\n迫りくる敵を撃退せよ ! 目指せ、全クリ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2370, /*level*/1, /*item_detail*/"蓮の葉の形をしたテーブル。\nゴロゴロしているブルーのカエルと一緒に\nリラックスタイムを過ごすことができる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2371, /*level*/1, /*item_detail*/"背もたれに大きくカエルの顔がプリントされたソファー。\n外に出れない雨の日も、にっこり笑ったカエルに座って、\nみんなで楽しくお話しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2372, /*level*/1, /*item_detail*/"緑色の引き出しがポイントのカエルのたんす。\n大切なものをしまえば、たんすの上にいるカエルが\n見張りとなって荷物を守ってくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2373, /*level*/1, /*item_detail*/"アイドルたちの雨具が置かれているラック。\n雨が降った街に出ると、新たな発見がいっぱい !\n冒険を終えた後は、濡れた雨具をしっかり乾かそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2374, /*level*/1, /*item_detail*/"鮮やかなオレンジ色が目を引くかたつむりソファー。\n殻を枕にゆっくりすれば、かたつむりと一緒に\nお散歩をしている気分になれるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2375, /*level*/1, /*item_detail*/"フレームがカエルの顔になっているユニークなテレビ。\nテレビの画面よりも、愛らしいカエルの顔に目が\n吸い寄せられてしまう。アイドルにも人気の一品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2376, /*level*/1, /*item_detail*/"ファンシーで可愛いカエルのベッド。\nカエルたちが喜ぶ雨の日を待ちながら、\nふわふわのベッドでゆっくり休もう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2377, /*level*/1, /*item_detail*/"グリーンのボディが可愛いカエルのぬいぐるみ。\nアイドルたちの間では「雨の日になると嬉しそうな顔を\nしている気がする」とウワサになっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2378, /*level*/1, /*item_detail*/"カエルの顔の形をした時計。\nその可愛さでアイドルたちからの注目度はバツグン !\n遅刻防止にも大活躍 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2379, /*level*/1, /*item_detail*/"蓮の葉の形をしたラグマット。\n上にちょこんと座っていると、\nカエルの気分が味わえるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000502, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2380, /*level*/1, /*item_detail*/"水たまりの形をしたラグマット。\n思いっきりジャンプをしても濡れることがないので、\n何回でも水たまりジャンプの感覚を楽しめる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2381, /*level*/1, /*item_detail*/"カエルと同じ緑色が鮮やかな壁紙。\n雨の日に窓の前に立てば、心地よい雨音が聞こえてきて\n心を落ち着かせることができる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2382, /*level*/1, /*item_detail*/"カエルのおうちテーマの背景。\n恵みの雨が咲いているあじさいへ潤いを与えてくれる。\n見頃のあじさいを事務所のみんなと見物しよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2383, /*level*/1, /*item_detail*/"ブルーの殻がクールなかたつむりソファー。\n固いように見えて、殻の部分はふかふか仕様。\n触り心地が最高なので、つい時間を忘れてしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2384, /*level*/1, /*item_detail*/"ブルーのボディがイカしたカエルのぬいぐるみ。\n天気が雨の日も、アイドルの心に雨が降っている日も\nいつでも優しく、アイドルたちのことを見守っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2390, /*level*/1, /*item_detail*/"イベント「HARURUNRUN」で獲得したベッド。\nクッションのお山に飛び込んで思いっきりもふろう。\nもふもふすれば、癒されること間違いなし !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2391, /*level*/1, /*item_detail*/"イベント「HARURUNRUN」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちの走る姿は、生徒たちに勇気を与えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2392, /*level*/1, /*item_detail*/"イベント「HARURUNRUN」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは駆け、生徒たちの胸に思い出が刻まれた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2393, /*level*/1, /*item_detail*/"イベント「HARURUNRUN」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。駆け抜けた先には、笑顔の花々が咲いていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2400, /*level*/1, /*item_detail*/"即席うさぎちゃんパペット。\nハンカチで作ったうさぎちゃんのパペット。\nどんなお仕事もキミなら大丈夫 ! 応援してるぴょん !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2410, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得したヘルメットが\n置いてあるテーブル。ふたり並んで自撮りすれば、\n気分もアガってマジアツな1枚のでき上がり☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2411, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\n真っ直ぐ前を向き、絆を武器にテッペンを目指した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2412, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。勝ちたい者が勝つ。\nアイドルたちはやりたいようにやる勇気を見せつけた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2413, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n突っ走っていこう。誰のものでもない、自分たちの道を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2420, /*level*/1, /*item_detail*/"編み物キットの置かれたテーブル。\n本もあるから、初心者も安心してチャレンジしてみよう !\n真心を込めて丁寧に編めば、大切な編み物の出来上がり♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2430, /*level*/1, /*item_detail*/"ブラックのシルクハットソファー。\nシルクハットの形をしたソファー。ここに座れば、\n帽子から跳び出るうさぎの気持ちになれるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000802, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2431, /*level*/1, /*item_detail*/"イリュージョンルーム。\n入った人が忽然と消えてしまう、不思議な空間。\n消えた人がどこから現れるかは…見てのお楽しみ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2432, /*level*/1, /*item_detail*/"デンジャラスボックス。\n中に入ると問答無用で斬られてしまう危険な箱。\nアイドルたちの体を張ったイリュージョンをご覧あれ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2433, /*level*/1, /*item_detail*/"不思議なドレス。\nピンク色の華やかなドレス。試着したアイドルたちも\n大満足のキレイさ。どこが不思議なのかは…今はヒミツ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2434, /*level*/1, /*item_detail*/"トランプマジック。\nマジックの定番、トランプマジック。\nキミはアイドルたちのマジックを見破れるかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2435, /*level*/1, /*item_detail*/"浮遊ベッド。\n寝ていると宙に浮く不思議なベッド。安眠はできないが、\nアトラクションとしてアイドルたちに人気の逸品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2436, /*level*/1, /*item_detail*/"キラメキライト。\n華やかな空間を演出してくれるライト。\nアイドルの舞台にはキラキラ輝くライトがなくっちゃね♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2437, /*level*/1, /*item_detail*/"ドアを模したトリックアート。\nこんなところにドアなんてあったっけ…？\n思わず入りそうになるうっかりさんは意外と多いらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2438, /*level*/1, /*item_detail*/"崩壊した壁のトリックアート。\n壊れた壁から覗くもうひとつの世界…。\nフォトジェニックスポットとして、密かに人気だとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2439, /*level*/1, /*item_detail*/"落とし穴？\nぽっかりと空いた穴にご用心。落ちないとわかって\nいても、この上に立つにはちょっと勇気がいるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2440, /*level*/1, /*item_detail*/"ガイドポール。\nイリュージョンを安全に楽しんでもらうために必要な\nポール。ルールを守って、楽しく騙されちゃおう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2441, /*level*/1, /*item_detail*/"イリュージョンに華を添える煌びやかな壁紙。\n今にもショーが始まりそうな壁紙に、気分はワクワク♪\n一夜限りのイリュージョンを、正夢に変えよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2442, /*level*/1, /*item_detail*/"華やかなイリュージョンを演出する床。\n歪んで見えるのは目の錯覚によるもの。それが不思議で、\nアイドルたちはあっちへこっちへと歩き回っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2443, /*level*/1, /*item_detail*/"シルクハットソファー。\nシルクハットの形をしたソファー。座れば気分は一流の\nイリュージョニスタ♪不可能さえも超えて魅せよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000802, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2444, /*level*/1, /*item_detail*/"ドアを模したトリックアート。\n突如現れた不思議なドア。ドアを覗くと不思議な空間が\n広がっているが、あちら側へは行けないのでご安心を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2450, /*level*/1, /*item_detail*/"イベント「エチュードは1曲だけ」で獲得した\nミニステージ。雨で濡れたあじさいが心を癒すように、\nアイドルたちの優美な踊りは人々を和ませてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2451, /*level*/1, /*item_detail*/"イベント「エチュードは1曲だけ」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは高鳴る緊張を、恋するトキメキに変えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2452, /*level*/1, /*item_detail*/"イベント「エチュードは1曲だけ」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちの演じる恋物語は、みんなの心を震わせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2453, /*level*/1, /*item_detail*/"イベント「エチュードは1曲だけ」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。アイドルたちは恋する瞬間を演じきった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2460, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白い木枠の窓。\nいつでも季節の移ろいを楽しむことができる不思議な窓。\n時には歩みを止めて、流れゆく景色に想いを馳せよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2461, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\n仲間との絆を頼りに、行き当たりばったりの旅に出た。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2462, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちはどんな\n時も純粋に楽しみ、輝きを見つけ、全力で追いかけた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2463, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちの熱い夏は、思い出とともに始まる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2470, /*level*/1, /*item_detail*/"ピンクのキュートなL字ソファー。\nアイドルたちが集まると、可愛くなるための\n美容情報交換会が始まるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2471, /*level*/1, /*item_detail*/"ピンクのねこのベッド。\nふかふかで、抜群の寝心地のよさ。横たわったら数秒で\nねこさんみたいな顔でスヤスヤ眠ってしまうらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2472, /*level*/1, /*item_detail*/"ブルーのクールなL字ソファー。\nアイドルたちが集まると、クールな表情に隠された\n熱い胸の内を語ってくれるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2473, /*level*/1, /*item_detail*/"はちわれがお招きする、ぐうたらスペース。\nいつもはクールな表情でキメているアイドルだって\nお疲れの日はあるはず。ぐうたらして気分転換しよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2474, /*level*/1, /*item_detail*/"オレンジのパッションなL字ソファー。\n情熱の色に、座るだけで気分がアガってくる。\n楽しくおしゃべりすると、もっと元気になれるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2475, /*level*/1, /*item_detail*/"ねこ好きにはたまらない、ねこタワーゲーム。\n簡単なゲームとあなどることなかれ !\n真剣勝負が今、幕を開ける… !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1513601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2476, /*level*/1, /*item_detail*/"グリーンの小型冷蔵庫。\n小さくてもハイパワー。中には、アイドルたちのお弁当に\nご褒美のプリン。遠方ロケのお土産が詰めこまれている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2477, /*level*/1, /*item_detail*/"旅館に置いてあるテレビ。\nお泊りロケに出かける時、アイドルたちは\nご当地ローカル番組を見るのを楽しみにしているらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2478, /*level*/1, /*item_detail*/"スケジュール黒板。\n時代はデジタルでも、誰がどこにいるか一目でわかる\nアナログな黒板が事務所では大活躍 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2479, /*level*/1, /*item_detail*/"緑色の扉のロッカー。\n収納力抜群で、掃除用具だけではなく\nかくれんぼ中のアイドルたちも中に入ってしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1505501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2480, /*level*/1, /*item_detail*/"ランドセルをしまうロッカー。\n文房具や楽器といったみんなの個性や、\nたくさんの元気が一緒に詰まっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2481, /*level*/1, /*item_detail*/"オリーブ色のハンモック。ゆらゆら揺れるハンモックに\n身を預けると、いつの間にか眠りの世界へ…。\nアイドルたちの話では、ステキな夢が見られるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2482, /*level*/1, /*item_detail*/"黒いイスがシックでオシャレなサークルベンチ。\n真ん中の木には、アイドルたちが交代で水をあげている。\n木も、アイドルたちのようにすくすく育っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000105, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2483, /*level*/1, /*item_detail*/"青い薔薇の花壇。\n青い薔薇の花言葉は「夢叶う」。\nアイドルたちは薔薇のように咲き誇る日を夢見ている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2484, /*level*/1, /*item_detail*/"青い薔薇のアーチ。\n寄りかかってポーズを決めるだけでオシャレな写真が\n撮れると、アイドルたちの間で話題になっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1507001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2485, /*level*/1, /*item_detail*/"青い薔薇のリース。\n珍しい青い薔薇が贅沢にあしらわれている。\n壁にかけておくだけで、部屋中に薔薇のいい匂いが漂う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2486, /*level*/1, /*item_detail*/"パープルのモザイクタイルがあしらわれた壁。\nあじさいのような配色が涼しげで、\nルームを色鮮やかにしたい時に大活躍 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2487, /*level*/1, /*item_detail*/"青色のベタの水槽。\n色鮮やかなベタが泳いでいる。\nときどき、アイドルたちが話しかけている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1503101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2488, /*level*/1, /*item_detail*/"プルメリアの水盤。\n気品溢れるいい匂いを漂わせるプルメリアが\nルームを常夏のハワイにしてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2489, /*level*/1, /*item_detail*/"蓮の水盤。\nぷかぷかと浮いている蓮の花を見ていると、\n極楽浄土にいる気持ちになれるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2490, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\n舞踏会の舞台セット。仮面を着ければ、貴方はもう別人。\n普段は見せない、思い切ったポーズにも挑戦してみよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1525001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2500, /*level*/1, /*item_detail*/"見る者の心をくすぐる、格好いいランプ。\nゆらめく炎から現れたインディヴィーズが、\nろうそくの周りを楽しく戯れる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2018, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2510, /*level*/1, /*item_detail*/"イベント「SUN♡FLOWER」で獲得したミニステージ。\n大輪のひまわりが咲き乱れるステージを、アイドルたちの\n笑顔が彩る。元気にハジけろパッショニスタ☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1525101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2511, /*level*/1, /*item_detail*/"イベント「SUN♡FLOWER」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。仲間と\n集まってアツい季節を笑顔で楽しもう ! Feel so nice☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2512, /*level*/1, /*item_detail*/"イベント「SUN♡FLOWER」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。夏の海に\n咲くのは満開の笑顔 ! みんなでEnjoy summer time☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2513, /*level*/1, /*item_detail*/"イベント「SUN♡FLOWER」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。キラめく笑顔と一緒に、Summer has come☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2520, /*level*/1, /*item_detail*/"古びたテレビ。\nテレビをつけようとしても、何も映らない。よく見ると\n電気が通っていないようだ。あれ、勝手に動き始めた…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2521, /*level*/1, /*item_detail*/"柳の木。\nおどろおどろしい雰囲気を演出するのに欠かせない木。\nそこに立っているのはアイドル？それとも…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2522, /*level*/1, /*item_detail*/"順路看板①。\n入った人が迷わないようにと備え付けられた看板。\nこの矢印に従って進めば、絶対に大丈夫である…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2523, /*level*/1, /*item_detail*/"順路看板②。\n入った人が迷わないようにと備え付けられた看板。\nこの矢印に従って進んだはずの、あの人はどこだろう…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2524, /*level*/1, /*item_detail*/"ろうそく立て。\n弱々しい灯りで暗闇を照らすろうそく。この小さな炎が\n消えてしまわぬよう、近寄るときは慎重に…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2525, /*level*/1, /*item_detail*/"宙吊りこんにゃく。\nただのこんにゃくも、宙に吊るせば立派な仕掛けに。\n冷たくてぬるりとした感触が、アイドルたちを驚かせる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2526, /*level*/1, /*item_detail*/"朱色の唐傘おばけ。\n古くから愛されている妖怪。最初は驚かされるが、愛嬌の\nある表情に、次第に愛着が湧いてくる…かもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2527, /*level*/1, /*item_detail*/"古びた井戸。\n今は使われていない古びた井戸。中を覗き見る時は注意。\nこちらが見る時、あちらもまた見ているのだから…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2528, /*level*/1, /*item_detail*/"おばけちょうちん。\nおばけ屋敷をコミカルに演出してくれるアイテム。それに\nしても、動力もないのにどうして動いているのだろう…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2529, /*level*/1, /*item_detail*/"木の柵。\n寄りかかってもビクともしない、丈夫な木の柵。\nおばけに驚いて飛びついても、これなら安全だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2530, /*level*/1, /*item_detail*/"破れた障子。\nこういう物なので、破れた部分を修繕する必要は\nないのだが、直そうとするアイドルが後を絶たない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2531, /*level*/1, /*item_detail*/"古びた障子。\n今にも破れそうな古びた障子。障子の向こうになにかが\nいそうだと、見たくないのについそちらを見てしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2532, /*level*/1, /*item_detail*/"おばけ屋敷の床。\nところどころ床が剥がれそうになっているので要注意。\nただ、慎重に歩くだけでなく、前もしっかりと見よう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2533, /*level*/1, /*item_detail*/"おばけ屋敷の壁紙。\n暗い色合いのせいか、見ているだけで不安な気持ちに…。\nただ一部のアイドルからは「むしろ落ち着く」と好評。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2534, /*level*/1, /*item_detail*/"おばけ屋敷の背景。\nおどろおどろしい空気が、外にも内にも漂っている。\n時折、ルーム内に「なにか」が出現するらしい…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2535, /*level*/1, /*item_detail*/"紫色の唐傘おばけ。\n古くから愛されている妖怪。想像以上に大きいので遭遇\nすると怖い。なぜ舌を出しているのかは定かではない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1524801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2536, /*level*/1, /*item_detail*/"曰くつきの古びた障子。\n化け猫が映ると噂だが、猫耳をつけたアイドルが映り\n込んだだけとも言われている。さて、真相は…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2540, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得したUSAポン。\n色とりどりのカプセルから出てくるのは、魔法アイテムの\n数々 ! ？何が出るかは回してみてからのお楽しみ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1525301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2541, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。勇気を出して手を\n伸ばした先に、かつて夢見た舞台がそこにはあった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2542, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。どんなに厳しい現実も\nアイドルたちは受け止め、自分たちの力に変えていった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2543, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nどんなに険しい凸凹道も、少女たちは笑顔で走った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2550, /*level*/1, /*item_detail*/"癒しの場所、しんでれらの赤ちょうちん。\nふと顔を上げれば、やわらかな灯りが瞳に映り、\nアイドルたちの心をホッとひと息つかせてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2551, /*level*/1, /*item_detail*/"癒しの場所、しんでれらの店先。\n料理の匂いに誘われてふらりと立ち寄れば、気の置けない\n仲間が迎えてくれるかも。今日もお仕事お疲れさま。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2560, /*level*/1, /*item_detail*/"衣装スタンド/夏色かりゆしワンピース。\n大きな花柄がカワイイ、涼しげなワンピース。\nみんながトリコになること間違いなし♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2561, /*level*/1, /*item_detail*/"衣装スタンド/ひまわり柄の浴衣。\n太陽のように、元気をくれるデザインの浴衣。\n暑さに負けず、みんなで楽しく盆踊り♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2562, /*level*/1, /*item_detail*/"衣装スタンド/アロハなセットアップ。\nパーカーを羽織ったアロハ柄のセットアップコーデ｡\n夏の終わりに､潮風を感じるような一着。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/2563, /*level*/1, /*item_detail*/"フローラル夕美が持っている魔法のコンパクト。\nお花の妖精さんの力が宿っている。\n呼び出される妖精さんたちは可愛らしく、そして自由。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1525901, /*individual_flag*/0);
