INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3141, /*level*/1, /*item_detail*/"魔法図書館の本棚①。\n数々の魔導書が収められている。\n貴重品も多いので、大切に読もう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500503, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3142, /*level*/1, /*item_detail*/"魔法図書館の本棚②。\n梯子のおかげで、高い棚にある本も手に取りやすい。\nただし、梯子の上り下りは慎重に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500503, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3143, /*level*/1, /*item_detail*/"魔法のランタン。\n広い図書館も、このランタンがあれば問題なし。\n読書が捗るよう、優しい明かりで照らしてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
