INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3180, /*level*/1, /*item_detail*/"リクガメの甲羅チェア。\nしっかりとした座り心地で、ゆったり、のんびり気分。\nアイドルたちに万年の幸あれ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000110, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3181, /*level*/1, /*item_detail*/"シマウマのスツール。\n背中に乗れば、草原の景色と同化できる…ということは\nないが、シマウマに乗るという珍しい乗馬体験ができる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000804, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3182, /*level*/1, /*item_detail*/"カバがお招きする、ぐうたらスペース。\nどっしりとしたカバの体にもたれかかろう。\n仲良くぐうたらすれば、陸へ上がりたくなくなるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1521401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3183, /*level*/1, /*item_detail*/"草原に自生するアカシアの木。\n気高くそびえ立ち、いきものたちを見守っている。\n葉はキリンの餌になるらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3184, /*level*/1, /*item_detail*/"草原に自生する背の高い茂み。\n小動物なら隠れることができる。茂みに隠れようとした\nアイドルがいたようだが、さすがに無理だったらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3185, /*level*/1, /*item_detail*/"ワニの抱きまくら。\n背中に寝転べば野生を感じられて、\nワイルドな夢が見られそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1508102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3186, /*level*/1, /*item_detail*/"オスライオンのぬいぐるみ。\n威厳たっぷり（？）の百獣の王。\nふわふわのたてがみは、撫で心地バツグン !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1529801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3187, /*level*/1, /*item_detail*/"キリンのぬいぐるみ。\n長～い首をやさしく撫でてあげよう。\nゆったり頭を振って、まるで生きているよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1529701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3188, /*level*/1, /*item_detail*/"フラミンゴのぬいぐるみ。\nスマートに片足立ちをする姿には美しさすら感じる。\nフラミンゴのその姿勢には、見習うことも多い。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3189, /*level*/1, /*item_detail*/"草原のいきものテーマの壁。\n背の高い草の茂みは、まさに大自然の壁だ。\n緑に包まれて、アイドルたちの野生が目覚めたり…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3190, /*level*/1, /*item_detail*/"草原のいきものテーマの床。\n癒しの水辺では、アイドルもいきものも関係なく\nみんなで仲良く過ごす ! それがここでのルールだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3191, /*level*/1, /*item_detail*/"草原のいきものテーマの背景。\n雄大な自然の中、気ままな野生を謳歌しよう。\n今日も一日、太陽と大地に感謝 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3192, /*level*/1, /*item_detail*/"チーターが休んでいるアカシアの木。\n草原のスピードスターも、いまは足を休め、\nのんびりまどろむ時間だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3193, /*level*/1, /*item_detail*/"メスライオンのぬいぐるみ。\n猛獣さを感じさせないキュートさに、\nアイドルたちもついつい撫でにやってくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1529801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3200, /*level*/1, /*item_detail*/"緑色の柏餅をかたどったクッション。\nもっちりとした触感がクセになり、\n飛び込んでしまうアイドルが続出しているとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501202, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3201, /*level*/1, /*item_detail*/"こいのぼり②。\n空中を堂々たる姿で泳ぐこいたち。その迫力に目を\n奪われ、つい時間を忘れて見入ってしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3210, /*level*/1, /*item_detail*/"いつでも桜を楽しめるお花見セット。\n見事に咲き誇る桜に、アイドルたちもうっとり。\n今だけはお仕事を忘れ、桜の花に癒されよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1529601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3220, /*level*/1, /*item_detail*/"イベント「きゅん・きゅん・まっくす」で獲得した\nモニター。女の子はいつだって、トキメク準備ができて\nいる。きゅんとした顔は見る人の心をもきゅんとさせる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2014, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3221, /*level*/1, /*item_detail*/"イベント「きゅん・きゅん・まっくす」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、きゅんきゅんな瞬間を想像した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3222, /*level*/1, /*item_detail*/"イベント「きゅん・きゅん・まっくす」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは、ファンの心をきゅんきゅんさせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3223, /*level*/1, /*item_detail*/"イベント「きゅん・きゅん・まっくす」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。胸のトキメキは、きっと何かが始まる予感。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3230, /*level*/1, /*item_detail*/"障子と龍が現れる背景。\nいつもの事務所風景を雅やかに彩ってくれる。障子が\n開くと、アイドルたちを見守る巨大な龍が現れる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3240, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したスペースシャトル。\n縮小模型と侮るなかれ。内部も忠実に再現されており、\nその精巧な作りは専門家もうなるほどの出来栄えだとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3241, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\n少女たちは力を合わせて、数々の試練に挑んだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3242, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。\n少女たちはそれぞれの持ち味を生かし、戦い抜いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3243, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちはついに、ステージに立つ切符を手に入れた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3250, /*level*/1, /*item_detail*/"母の日ギフトが並んでいる商品棚。\n日頃の感謝を込めたギフトを選ぼう。時間がかかっても\n大丈夫。あれこれ悩んだ時間も、大切な贈り物。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1529901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3260, /*level*/1, /*item_detail*/"高級なティーセット一式が揃ったテーブル。\nテーブルを囲み、優雅な午後のひとときを楽しもう。\nもちろんお望みなら、さわやかな朝や静かな夜にも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1516601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3261, /*level*/1, /*item_detail*/"しっかりとした木造りのベンチ。\n優しい木のぬくもりとうららかな日差しに包まれれば、\n誰もが時を忘れて夢見心地に…♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000111, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3262, /*level*/1, /*item_detail*/"庭園の中に建てられた憩いの場。\nゆったりとした時間をベンチに座って過ごし、\n心身を休ませて明日への活力にしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3263, /*level*/1, /*item_detail*/"水の流れが幾筋も折り重なる、芸術的な噴水。\nとある資産家が、懇意にしている高名な芸術家に\nデザインしてもらった噴水…の、レプリカだとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3264, /*level*/1, /*item_detail*/"暖色系の鮮やかさが目を引く、庭園の花。\n可愛らしい見た目だけでなく、ほのかに漂う香りが\nアイドルたちの心に安らぎをくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3265, /*level*/1, /*item_detail*/"寒色系の爽やかさが目に涼しい、庭園の花。\nその可憐な姿と、花を愛でる少女たちの笑顔は、\nまるで一枚の名画のように美しい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3266, /*level*/1, /*item_detail*/"暖色系の鮮やかさが目を引く、背の高い庭園の花。\n注いでもらえた愛情の分、元気に育った花は、\n見る人の心も明るくしてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3267, /*level*/1, /*item_detail*/"寒色系の爽やかさが目に涼しい、背の高い庭園の花。\n気品のある落ち着いた色で、訪れた人を迎えてくれる。\n他の花と合わせて、庭を色とりどりに飾ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3268, /*level*/1, /*item_detail*/"きれいに整えられた生垣。\n高さも厚みもあるので、向こう側は見通せない。\n庭にたくさん並べれば、緑の迷路ができるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3269, /*level*/1, /*item_detail*/"庭園をぐるりと取り巻く、木造りの柵。\n箱入りお嬢さまを守るように、しっかりと立っている。\nでも…あなたの来訪は、歓迎いたします♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3270, /*level*/1, /*item_detail*/"洋風庭園の床。\n青々とした芝の上にどんな景色を描き上げるか、\nガーデン職人としての腕の見せ所。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3271, /*level*/1, /*item_detail*/"洋風庭園の背景①。\n庭を見守るように、立派な洋館が建てられている。\n静かな庭は賑やかな街中とは違い、まるで別世界のよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3272, /*level*/1, /*item_detail*/"青色の屋根が特徴の、庭園の中に建てられた憩いの場。\n澄み切った青空を思わせるガゼボで過ごせば、\nたとえ曇り空でも、心さわやかになれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3273, /*level*/1, /*item_detail*/"洋風庭園の背景②。\n耳をすまして聴こえてくるのは、風に揺れる木々の音、\n湖に立つさざ波の音、そして少女たちの笑い声も…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3280, /*level*/1, /*item_detail*/"イベント「Max Beat」で獲得した壁掛けモニター。\nアイドルたちが感じているドキドキを、克明に映し出す。\nクールに気取っていても、胸の鼓動は隠せない… !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2014, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3281, /*level*/1, /*item_detail*/"イベント「Max Beat」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\n刺激的な体験に、胸の高鳴りを感じた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3282, /*level*/1, /*item_detail*/"イベント「Max Beat」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは\n趣向を凝らした舞台で、熱い鼓動を伝えあった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3283, /*level*/1, /*item_detail*/"イベント「Max Beat」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n燃え上がる挑戦心が、少女たちを次の未来へといざなう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3290, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得したベンチとポンポン。\nポンポン持って元気に踊れば、キミも立派なチアガール !\nその応援はきっと、誰かの力になる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3291, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n相手を好きになることが、応援の第一歩だと知った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3292, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは、\n応援の大切さを知り、力の限りエールを送った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
