INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3300, /*level*/1, /*item_detail*/"ビデオカメラ。\nアイドル同士で、PR動画を撮りあおう。アピールは\nニコニコ、自分らしく。いつものみんながいいね !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3310, /*level*/1, /*item_detail*/"ボウリング練習セット。\n目指せストライク !\nたくさん練習して、みんなを驚かせちゃおう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3330, /*level*/1, /*item_detail*/"空中に浮かぶ、小さなくものかたまり。\n椅子のように腰掛けることができる。\nふわふわな座り心地に、心もふわふわ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000112, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3331, /*level*/1, /*item_detail*/"空中に浮かぶ、ピンク色の小さなくものかたまり。\nわたあめのように見えるが、ちゃんと座ることができる。\nその座り心地は、人をダメにするかもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000112, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3332, /*level*/1, /*item_detail*/"赤い熱気球。\nみんなの情熱を詰めこんで、くもの上までやってきた。\nいつか、もっと高く昇る日を待っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3333, /*level*/1, /*item_detail*/"くもにかかった虹のアーチ。\n虹の上を歩いて渡ることができる。てっぺんで\nポーズをとったりして、夢のような気持ちになろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3334, /*level*/1, /*item_detail*/"空中に浮かぶ、大きなくものかたまり。\nもこもこした感触は、触り心地バツグン !\nついつい時間を忘れて、むにむにっと触り続けてしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3335, /*level*/1, /*item_detail*/"三日月をモチーフにしたベッド。\nもこもこの枕に、もこもこの布団。\n月とくもに抱かれて、まさに、天にも昇る寝心地だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3336, /*level*/1, /*item_detail*/"おいしそうなくものかたまり。\nかわいいデコレーションは、わたあめのよう。\n食べられるくもなので、安心して食べちゃおう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3337, /*level*/1, /*item_detail*/"くもでできた、もこもこした大きなクジラ。\n海ではなく、空を泳ぐその姿は、\nどこか気持ちよさそうに見える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3338, /*level*/1, /*item_detail*/"くもでできた、もこもこした白い羊。\n1匹、2匹と数えていると、なんだか眠くなってくる…？\nやすらぎに満ちたくもの世界を、一緒に満喫しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3339, /*level*/1, /*item_detail*/"もこもこした、くもの壁。\nあのくものむこうに何があるのか…？\n手を伸ばしてもつかめない、そんなもどかしさ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3340, /*level*/1, /*item_detail*/"もこもこした、くもの床。\n憧れのくもの上を、思う存分歩きまわろう。\n飛び跳ねても床が抜けることはない、安心設計。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3341, /*level*/1, /*item_detail*/"もこもこしたくもが、一面に広がる背景。\nまるで雲海の上を散歩しているかのよう。\nどこまでも広がる空は、雄大そのもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3342, /*level*/1, /*item_detail*/"青い熱気球。\nみんなの希望を詰めこんで、くもの上までやってきた。\nいつか、もっと遠くまで飛ぶ日を待っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
