INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3320, /*level*/1, /*item_detail*/"イベント「TAKAMARI☆CLIMAXXX!!!!!」で獲得した\n壁掛けモニター。胸のドキドキは、高まりっぱなし !\nアイドルたちの情熱は、ファンの心も熱くさせる !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2014, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3321, /*level*/1, /*item_detail*/"イベント「TAKAMARI☆CLIMAXXX!!!!!」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\n少女たちは、むふむふな場面の連続に胸を高まらせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3322, /*level*/1, /*item_detail*/"イベント「TAKAMARI☆CLIMAXXX!!!!!」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\n少女たちは見事に演じ切り、ファンのココロも高まった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3323, /*level*/1, /*item_detail*/"イベント「TAKAMARI☆CLIMAXXX!!!!!」で獲得した\n純金のトロフィー。アイドルを伝説級のステージに導いた\nＰに贈られる。むふ、むふふ…♪むふふ…むふふふふ～♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3350, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得したミニステージ。\nステージに現れるアイドルに声援を送ろう !\n姿は現実でなくても、その熱意は間違いなく本物だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3351, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n少女たちは、お祭りのような時間をファンと楽しんだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3352, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n少女たちは、たくさんの可能性を見せていった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3353, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。バイバイ。だけど、また会おう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3354, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した七色の\nトロフィー。いくつもの輝きを束ねたＰに贈られる。\n未来を信じること。それが、可能性の輝き。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3360, /*level*/1, /*item_detail*/"ビーチパラソルと浮き輪、タオルなどのセット。\nここでくつろげば、気分はリゾート。\n事務所の中でも爽やかな潮騒が聞こえてくる…気がする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3370, /*level*/1, /*item_detail*/"癒しの場所、しんでれらの壁。\nシミひとつない、綺麗な壁。\nそれなのに、ほのかにいい匂いがしてくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3371, /*level*/1, /*item_detail*/"癒しの場所、しんでれらの床。\n掃除が行き届いているので、いつだって綺麗。\nアイドルたちが来るのを、今か今かと待ちわびている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3380, /*level*/1, /*item_detail*/"トロピカルなフルーツとジュースが載っているテーブル。\n甘くて美味しそうな香りがアイドルたちを惹きつけ、\nつかの間の南国気分を味わわせてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3381, /*level*/1, /*item_detail*/"ウッドチェア。\nラウンドテーブルにピッタリのウッドチェア。\n腰を落ち着けて景色を眺めたい時にどうぞ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1528101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3382, /*level*/1, /*item_detail*/"ガーデンベンチ。\n木の温もりを感じられるガーデンベンチで、\nゆったりとした時間を過ごそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1531301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3383, /*level*/1, /*item_detail*/"数種類のジュースを用意してあるドリンクカウンター。\nリンゴ・オレンジ・ぶどう、そしてラムネ。\n好きな飲み物を選んで、のどを潤そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3384, /*level*/1, /*item_detail*/"鮮やかな赤色が目を引くパラソル。\nこれがあるだけで、華やかさがグッと増す。\nルームに華を咲かせたいと思ったら是非このパラソルを。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3385, /*level*/1, /*item_detail*/"ウッドフェンス。\nちょっとだけ目隠ししたい場所に置くと便利なフェンス。\nたまにアイドルが、隙間から覗いてくることも…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3386, /*level*/1, /*item_detail*/"屋上のフェンス。\n安全面から、高めの金網が設置してある。\n景観が悪そうだが、金網越しに見る景色もなかなか良い。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3387, /*level*/1, /*item_detail*/"「屋上花火大会」テーマの床。\nあたたかみを感じる木目調の床に和むアイドルも多い。\n身も心もほっとするようなルームにするのもいいかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3388, /*level*/1, /*item_detail*/"夜空に花火が打ち上げられる背景。\n色とりどりの大輪の花が、夜空を彩る。アイドルたちに\n関係のあるマスコットも、花火になっているとか…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3389, /*level*/1, /*item_detail*/"白色の外観が特徴のドリンクカウンター。\nドリンクを用意した人たちのオススメはラムネ。\nただ、泡の噴き出しには気を付けよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1530901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3390, /*level*/1, /*item_detail*/"植物の柄を施した白色のパラソル。\n日差しや紫外線を遮るだけでなく、\n見た目も可愛らしいのでアイドルたちも気に入っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3391, /*level*/1, /*item_detail*/"植物の柄を施した緑色のパラソル。\n日よけだけでなく、空間演出としても重宝する。\nどこに置くのか、アレコレ悩むのも楽しみ方のひとつ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3400, /*level*/1, /*item_detail*/"イベント「バベル」で獲得した公園のブランコ。\nふたりで並んで座れば、自然と会話が生まれてくる。\nどんなことを話しているのかは…ふたりだけのヒミツ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1531101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3401, /*level*/1, /*item_detail*/"イベント「バベル」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。\nアイドルたちの在り方に、人々は魅了された。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3402, /*level*/1, /*item_detail*/"イベント「バベル」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。痛みが伴おうとも、\nアイドルたちは自らが選んだ道を進み続けていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3403, /*level*/1, /*item_detail*/"イベント「バベル」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n退屈な楽園を捨て、彼女たちは禁断の果実を齧った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3410, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した、手作りの無人島\nハウス。丸太に大きな葉っぱを乗せただけなので、座ると\n結構痛い。なのに、不思議ととても居心地がいい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1531001, /*individual_flag*/0);
