INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3420, /*level*/1, /*item_detail*/"アフレコ体験セット。\nいろいろな映像に合わせて、声優のお仕事が体験できる。\n発声OK ! 演技もOK ! タイミングも、OK…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1531401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3430, /*level*/1, /*item_detail*/"特大お注射の形をしたウォーターガン。\n大胆に、セクシーに…リズムに乗って撃ちまくり、\nみんなのハートもビショビショにしちゃおう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1531501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3440, /*level*/1, /*item_detail*/"イベント「comic cosmic」で獲得した、すいか。\nキミもすいか割りに挑戦だ ! 棒を使っても、素手で\n挑んでもよし。自分のスタイルですいか割りを楽しもう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1531601, /*individual_flag*/1);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3441, /*level*/1, /*item_detail*/"イベント「comic cosmic」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n少女たちは熱い夏の海を、ポップに駆け抜けた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3442, /*level*/1, /*item_detail*/"イベント「comic cosmic」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nかけがえのない時間を、少女たちは作り出していった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
