INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3450, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した、たくさんの花が\n咲く花畑。吹き渡る風の感触や、優しい花の香りを\n楽しみながら、思い思いの時間を過ごそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3451, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した、花畑と青空が\n広がる背景。そこには、鮮やかな虹が架かっている。\n遠くの空にも、そしてアイドルたちの心にも…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3452, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n少女たちは交流を通して、心にそれぞれの色を見た。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
