INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3714, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した七色の\nトロフィー。いくつもの輝きを束ねたＰに贈られる。\nアイドルたちは、ファンにも同じ夢を見せていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3720, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\n勉強道具が置かれたテーブル。心のこもった差し入れで\n元気を注入すれば、勉強もぐんぐんはかどります !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1534701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3730, /*level*/1, /*item_detail*/"熱い魂の宿ったゴールポスト。\nCM連動キャンペーンのアイテム。設置すると、\n熱血先生がルームにやって来てくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3740, /*level*/1, /*item_detail*/"イベント「幸せの法則 ～ルール～」で獲得した蓄音機。\nインテリアとしても魅力的。音楽を聴きながら、\nゆったりとした時間を過ごすのも、ひとつの幸福の形。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1535201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3741, /*level*/1, /*item_detail*/"イベント「幸せの法則 ～ルール～」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、「幸せ」とはなにかと、思いを馳せた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3742, /*level*/1, /*item_detail*/"イベント「幸せの法則 ～ルール～」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは、己に課せられた運命に向き合った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
