INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3841, /*level*/1, /*item_detail*/"レトロな雰囲気の、下りの螺旋階段。\n階下の暗がりが、どこへ続いているのかはわからない。\nだが食事時になると、明るい声が聞こえてくる気がする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3842, /*level*/1, /*item_detail*/"ペコリーヌのぬいぐるみ。\n「やばいですね☆」が口癖の、お腹ペコペコ王女さま。\n耳を澄ますと、お腹の虫の鳴き声が聞こえてくるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3843, /*level*/1, /*item_detail*/"コッコロのぬいぐるみ。\nエルフ族の少女。何故か母性を感じさせる、不思議な子。\n子どもたちに懐かれると、ちょっと嬉しそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3844, /*level*/1, /*item_detail*/"キャルのぬいぐるみ。\nネコミミの獣人族。ついついナデナデしたくなる一品。\n人によっては、美味しそうに見えるらしい…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3845, /*level*/1, /*item_detail*/"ギルドハウスの壁。\n温かみのあるデザイン。窓の向こうに目を凝らすと、\nファンタジーな街並みが見える…かもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3846, /*level*/1, /*item_detail*/"ギルドハウスの木の床。\n落ち着ける木目のデザインが、くつろぎの時間をくれる。\n食べこぼしや油跳ねしたときは、すぐ掃除しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3847, /*level*/1, /*item_detail*/"【美食殿】のギルドハウスがある、ランドソルの背景。\nここではないどこかの異世界。ヒューマンだけでなく、\nエルフ族や獣人族、魔族などが通りを闊歩している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3850, /*level*/1, /*item_detail*/"りんごの精・りんごろうさんの着ぐるみ。\nこれを着てアピールすれば、一儲けできるかも？\nなお、ファスナーに不備があり、ひとりでは着られない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3860, /*level*/1, /*item_detail*/"イベント「Athanasia」で獲得したフロアマッピング。\n朽ちかけた石畳に、褪せない花が咲き誇る。\n色とりどりにきらめく美しさは、まるで本物のよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3861, /*level*/1, /*item_detail*/"イベント「Athanasia」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\n遠い非日常を救うべく、困難なミッションに挑んだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3862, /*level*/1, /*item_detail*/"イベント「Athanasia」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは\nそれぞれの役割を果たし、連携する強さを手に入れた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3863, /*level*/1, /*item_detail*/"イベント「Athanasia」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちは不滅の絆を結び、流れる時の中を歩む。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3870, /*level*/1, /*item_detail*/"パレットの形をしたまないた。\n載せるのは絵の具…ではなくビチビチ動く活きのいい魚。\n華麗な包丁捌きを披露して、料理上手を可愛くアピール♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1535801, /*individual_flag*/1);
