INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3890, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\n地図制作スペース。お気に入りの場所を描くもよし、\n飾り付けるもよし。みんなで楽しく地図を描こう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536401, /*individual_flag*/0);
