INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3904, /*level*/1, /*item_detail*/"「乃々を探せ ! レベル41 ! 」のゲーム筐体。\nアイドルたちの助けを借りながら、\n不思議な世界をどこまでも冒険しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1534001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3905, /*level*/1, /*item_detail*/"青いボディのカプセルトイ。\n取っ手をガシャガシャ回して、いざ運試し。\nたまに出てくる開きにくいカプセルも、楽しみのひとつ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536202, /*individual_flag*/0);
