INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3912, /*level*/1, /*item_detail*/"エアホッケーゲームの筐体。\n見つめ合って、いざ1対1の真剣勝負 !\n相手の動きを見切って、パックを力強く打ち込もう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543502, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3913, /*level*/1, /*item_detail*/"格闘ゲームの筐体。\n対人戦もCPU戦もできる、人気の格闘ゲーム。\n椅子の座り心地もよく、長時間のプレイでも安心。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544002, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3914, /*level*/1, /*item_detail*/"格闘ゲームの筐体その②。\n巧みなレバー捌きでコンボを決めて、勝利をつかめ !\n手強い相手との一戦は、手に汗握るものになるだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3915, /*level*/1, /*item_detail*/"2人プレイができるシューティングゲームの筐体。\n目まぐるしく現れる標的をどんどん撃ち抜き、\n力を合わせて、目指せハイスコア !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543602, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3916, /*level*/1, /*item_detail*/"ダンスゲームの筐体。\nアイドル活動で鍛えた腕前をゲームでもお披露目 !\nただし、油断は禁物。意外と手強いと評判だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1537902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3917, /*level*/1, /*item_detail*/"赤いボディのお菓子キャッチャー。\n色とりどりのアクリルストーンがお菓子を彩る。\nタイミングよくストーンを落として、景品を押し出そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1538702, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3918, /*level*/1, /*item_detail*/"青いボディのお菓子キャッチャー。\nぐるぐる回るお菓子やストーンには、魅力がたっぷり。\nついついメダルを投入してしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1538702, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3919, /*level*/1, /*item_detail*/"緑のボディのお菓子キャッチャー。\n豊富な本体の色で、ゲームセンター内を彩る存在。\n鮮やかな筐体から出てきたお菓子は、一層美味しそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1538702, /*individual_flag*/0);
