INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3939, /*level*/1, /*item_detail*/"「鋼鉄公演 きらりんロボ 外伝」にまつわるアイテム。\nエネルギーが足りていないときの姿。\n寝転がる姿は、働いたら負けとでも言わんばかり。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3940, /*level*/1, /*item_detail*/"ビィ専用カスタマイズのケッタギア。\n親友（マブ）と育んだツッパリ魂を胸に、\nビィは今日も行く。どんな時でも安全運転で夜露死苦ッ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3941, /*level*/1, /*item_detail*/"黒銀を宿す、プロトバハムートの召喚石。\n召喚石にむけて、切なる祈りを捧げよう。枷から解き\n放たれた原初の竜が、呼びかけに応えてくれるだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1535701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3942, /*level*/1, /*item_detail*/"正式名称は、ゴリラ・ゴリラ・ファータゴリラ。\n触っていると体の内側から力が湧き、口調が段々と\n…ゴリラに ! ？ うほっ ! うほほ…うほうほ～っ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3943, /*level*/1, /*item_detail*/"ラカムのぬいぐるみ。\nグランサイファーの操舵士で、頼れる兄貴分。\n艇に寄りかからせてあげると、心なしか嬉しそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3944, /*level*/1, /*item_detail*/"イオのぬいぐるみ。\n一人前の魔導師を目指す、活発で明るい少女。\n子供扱いはせず、大人のレディへの対応を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3945, /*level*/1, /*item_detail*/"魔鯛のクッション。\n非常に美味だが、獰猛で滅多に釣り上げられない高級魚。\nエヴィを側に置くといつの間にかなくなっているらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3946, /*level*/1, /*item_detail*/"バルツ公国の壁。\n火山と滾る炎により形作られる工業都市の壁。\n触れたら火傷しそうなほどの熱気が伝わってくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3947, /*level*/1, /*item_detail*/"バルツ公国の床。\n砂埃と錆、汗と熱気が染みついた、硬く丈夫な床。\nワイルドでたそがれた雰囲気が堪能できる…気がする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3948, /*level*/1, /*item_detail*/"バルツ公国を模した背景。\n砂煙にも似た厚い雲が覆う空。\n立ち込める雲は工業国として発展してきた証でもある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3950, /*level*/1, /*item_detail*/"小春が愛用している、ヒョウくんお出かけセット。\nヒョウくんが寂しくないよう、チャックはあけている。\n撫でて抱っこして、楽しいふれあいは憩いの時間。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1535901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3960, /*level*/1, /*item_detail*/"癒しの場所、しんでれらの大きいのれん。\nのれんの先は、お客様の出入りは厳禁。\n店の裏側を見せないのも、店員さんのおもてなし。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3961, /*level*/1, /*item_detail*/"癒しの場所、しんでれらの小さいのれん。\n趣のあるしんでれらに欠かせないもの。ひっそりと佇み、\nアイドルたちの楽しげな様子を見守っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3962, /*level*/1, /*item_detail*/"癒しの場所、しんでれらのおすすめ黒板。\nお店イチオシの飲み物や旬の食材を使った料理は\n期間限定。来店したら、まずは黒板を要チェック。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3963, /*level*/1, /*item_detail*/"癒しの場所、しんでれらのメニュー短冊。\n定番おつまみや飲み物を、リーズナブルなお値段で提供。\nメニューになくても可愛く頼めば作ってくれる…かも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3970, /*level*/1, /*item_detail*/"イベント「不埒なCANVAS」で獲得したドッキリ看板。\n看板を元気よく掲げて、ドヤっとキメ顔 !\nとびきりカワイく、ドッキリ大成功を伝えよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3971, /*level*/1, /*item_detail*/"イベント「不埒なCANVAS」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n少女たちのイタズラが、たくさんの笑顔を描いていった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3972, /*level*/1, /*item_detail*/"イベント「不埒なCANVAS」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n少女たちが考えた新鮮な刺激が、退屈な日々を彩った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3973, /*level*/1, /*item_detail*/"イベント「不埒なCANVAS」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。少しだけ不埒に少女たちは世界を塗り替える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3980, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したピクニックセット。\nレジャーシートに座って、ピクニック気分を楽しもう。\nふたりでお茶を味わいながら、心安らぐひとときを♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1537101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3981, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n穏やかに流れるあたたかなひとときを、大切に過ごした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3982, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは\n思い出を重ねて、「やってみたいこと」を叶えていった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3983, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nふたりが伝える幸せは、どこまでも広がっていくだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3990, /*level*/1, /*item_detail*/"神尾観鈴のぬいぐるみ。\nいつも元気でよく笑い、そしてとても強い子でもある。\n抱き上げると、何故か海と青空が見える気がする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533002, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3991, /*level*/1, /*item_detail*/"ぷろとん君のぬいぐるみ。\nつぶらな瞳が可愛らしい。触れ合っていると、観鈴と\n同じように、アイドルたちも恐竜の魅力に気づくかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3992, /*level*/1, /*item_detail*/"武田商店にあるのと同じ自動販売機。\nどろり濃厚ピーチ味のジュースを買うことができる。\n飲み方にコツがいるせいか、好んで飲む人は少ない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536601, /*individual_flag*/1);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3993, /*level*/1, /*item_detail*/"古河渚のぬいぐるみ。\n「古河パン」を営む、古河家のひとり娘。渚にならって\n好きな食べ物の名前を口にすれば、勇気が出てくるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3994, /*level*/1, /*item_detail*/"ブルーのだんご大家族クッション。\n撫で心地バツグン。\nまるで本物のだんごのように、もっちり肌触り。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3995, /*level*/1, /*item_detail*/"ピンクのだんご大家族クッション。\n愛らしい見た目に、アイドルたちもニッコリ。\n誰も座ろうとはせず、みんなで愛でている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3996, /*level*/1, /*item_detail*/"イエローのだんご大家族クッション。\nお仕事で忙しいアイドルたちの心を和ませてくれる。\nその姿はまさに、ルームの癒し系アイドル。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3997, /*level*/1, /*item_detail*/"ロボットのぬいぐるみ。\n原形は、ここではないどこかにいる少女が作り上げた。\n無機質なはずなのに、ほのかにあたたかさが感じられる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3998, /*level*/1, /*item_detail*/"生徒会長・立華かなでのぬいぐるみ。\n麻婆豆腐が大好きな、心の優しい少女。\nその愛らしさは、まるで天使のよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/3999, /*level*/1, /*item_detail*/"なんだか神々しいピアノ。\nその鍵盤に指を走らせれば、物語が始まる音がする。\n戦い続ける魂たちに、祝福と安寧を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4000, /*level*/1, /*item_detail*/"死んだ世界戦線のロゴが刻まれたタペストリー。\nこれを飾れば、ルームが死んだ世界戦線本部に早変わり。\n仲間たちと共に戦い抜こう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4001, /*level*/1, /*item_detail*/"鳴瀬しろはのぬいぐるみ。\n人見知りをする、クールで料理上手な少女。\n他のぬいぐるみと一緒に並べてあげよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4002, /*level*/1, /*item_detail*/"島の治安を守る水鉄砲。\nとんでもない飛距離と命中精度を誇る。\nルーム内で遊ぶのはほどほどに。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1537001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4003, /*level*/1, /*item_detail*/"秘密基地におかれている卓球台。\n暑苦しい鍛練や、くだらない笑い話に、恋の話。\n少年たちの夏が染みついている。 ", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1536701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4010, /*level*/1, /*item_detail*/"オシャレキュートなアメリカンダイナーのカウンター。\n本場の味が、心をサンフランシスコへ運んでくれるよう。\nいろんな料理と一緒に、ヤミーなひとときを召し上がれ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1537201, /*individual_flag*/0);
