INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4066, /*level*/1, /*item_detail*/"IBN5100。一見何の変哲もない、旧型のコンピュータ。\nしかし…その隠された機能は、世界を変える鍵となる。\n必要とする人が現れるまで、大事に保管しておこう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
