INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4230, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\n秋スイーツのショーケース。季節の実りを友だちと\n味わえば、幸せがもっと増えるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1539501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4240, /*level*/1, /*item_detail*/"願いを叶えてくれる魔人を喚ぶ、夢のようなランプ。\nランプをこすれば、魔人ぴにゃがBON ! っと飛び出す。\n自分で願いを叶えるアイドルに魔人から声援が送られる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4250, /*level*/1, /*item_detail*/"イベント「さよならアンドロメダ」で獲得した、\n銀河列車の座席。星の海を眺めていると、切符を拝見と\n声がかかる。星めぐりの旅を、ゆったりと楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1539601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4251, /*level*/1, /*item_detail*/"イベント「さよならアンドロメダ」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、失っていたものに気づきはじめた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4252, /*level*/1, /*item_detail*/"イベント「さよならアンドロメダ」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは、それぞれのこれまでとさよならをした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4253, /*level*/1, /*item_detail*/"イベント「さよならアンドロメダ」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。さよならの先には、新しい日々が続いている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4260, /*level*/1, /*item_detail*/"小型の月面基地。\n中に入って宇宙服に着替えたら、いざ月面探査に出発 !\nぽよんとジャンプして、無重力体験を楽しもう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1539701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4261, /*level*/1, /*item_detail*/"探査ミッションに欠かせない月面車。\n座席に乗り込めば、ミッション中の調査員になったよう。\n硬そうな見た目に反して、座り心地はなかなかのもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4262, /*level*/1, /*item_detail*/"月面着陸船。\n月面に着陸するために、司令船から射出された宇宙船。\n頑丈だが使い捨てのため、その姿は儚さも秘めている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4263, /*level*/1, /*item_detail*/"月面にも取り付け可能なソーラーパネル。\n太陽の光だけではなく、アイドルの輝きも集めて\n作られるエネルギーは、眩く煌めいているだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4264, /*level*/1, /*item_detail*/"天文台などに置かれているパラボラアンテナ。\n電波を送受信し、衛星通信や無線通信などに用いられる。\n話にしか出てこない、あの星の電波も受信できるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4265, /*level*/1, /*item_detail*/"無人の資源探査機。\n石などの資源を拾い集め、物質を解析してくれる。\n解析用紙には、あっと驚く結果が書かれているかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1539801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4266, /*level*/1, /*item_detail*/"ぴにゃこら太の顔が大きく描かれた旗。\n地面にならどこにでも立てられるすぐれもの。\n好きな場所に立てて、到達の証を掲げよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4267, /*level*/1, /*item_detail*/"餅つきの杵を持った、白いウサギのぬいぐるみ。\n月ではもう一匹とお餅をついていたのかもしれない。\n飾ったり抱き上げたりして、月の住人を歓迎しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4268, /*level*/1, /*item_detail*/"月面をモチーフにした床。\n表面に刻まれたクレーターが、月の歴史を物語る。\n未知に溢れた神秘的な地を、思うままに歩いてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4269, /*level*/1, /*item_detail*/"宇宙にぽつりと浮かぶ、月面の背景。\n遠い彼方から、母なる地球が優しく見守っている。\n瞬く星々は、ここから何億光年先にあるのだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4270, /*level*/1, /*item_detail*/"ピンクぴにゃこら太の顔が大きく描かれた旗。\n見慣れた表情に癒されること間違いなし。\nキュートな色合いでインテリアとしても活躍する。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4271, /*level*/1, /*item_detail*/"餅つきの杵を持った、ブラウンのウサギのぬいぐるみ。\nしっかりと抱きしめた杵は決して離さない。\nもしかしたら、一緒にお餅をつきたいのかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4280, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した光の階段。\n実は投影されたAR映像。きらきら舞う羽根に彩られる\n様は、触れられないからこその、夢のような美しさ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4281, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\n感謝の想いを、画面を通して伝えていった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4282, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは\n世界中のファンに、夢を追う輝きを魅せた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4283, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちの夢は現実となり、次の夢へと繋がっていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4290, /*level*/1, /*item_detail*/"豪華な料理がたくさん並んだこたつ。\nチキンやピザをほおばって、クリスマス気分を満喫☆\n幸せの味で、身も心もあたたかくなろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4300, /*level*/1, /*item_detail*/"シロイルカのソファー。\n見た目だけではなく、ふかふか手触りでも癒してくれる。\n腰かけてまどろめば、海をたゆたう気分になるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1523101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4301, /*level*/1, /*item_detail*/"氷でできたクリスマスツリー。\nオーナメントで飾り付ければ、さらに幻想的に。\n室内でも溶けない特別仕様。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000303, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4302, /*level*/1, /*item_detail*/"セイウチのぬいぐるみ。\n鋭い目つきだが、不思議と包容力を感じる。\n硬い牙も綿でできているので安心安全。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4303, /*level*/1, /*item_detail*/"シロクマのぬいぐるみ、おとなのすがた。\n四つ足のバランスは抜群 !\nずっと抱き着いていたくなるほど、もふもふあったか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4304, /*level*/1, /*item_detail*/"シロクマのぬいぐるみ、こどものすがた。\n小さなおてての肉球がとってもキュート。\nぎゅーっと抱きしめれば、自然とニコニコ笑顔に♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4305, /*level*/1, /*item_detail*/"皇帝ペンギンのぬいぐるみ。\nその名にふさわしい王者の風格が漂う。\n失礼がないよう、丁重に可愛がろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4306, /*level*/1, /*item_detail*/"子どもペンギンのぬいぐるみ。\n無条件に守ってあげたくなる可愛さ。\nふわふわのお腹に顔を埋めると、至福の時が訪れる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4307, /*level*/1, /*item_detail*/"アザラシのぬいぐるみ。\n丸い体に、どこか凛々しくもあるくりっとした瞳。\n一緒にゴロゴロ転がれば、いつの間にか夢の世界へ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4308, /*level*/1, /*item_detail*/"氷河に空いた穴。\n底が深いので、近づきすぎに注意 !\nたまにひょっこりと、アザラシが顔を出してくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4309, /*level*/1, /*item_detail*/"氷の盛り上がりを表現した壁。\n大自然の厳しさと雄大さを体感できる。\n尖っているところが多いので壁に触れる時は気を付けて。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4310, /*level*/1, /*item_detail*/"氷山の硬質な地面を再現した床。\nつるつるすいーっとどこまでも滑っていけそう。\n勢いのまま遊んでいると、転んでしまうので注意。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4311, /*level*/1, /*item_detail*/"寒い海を気ままに流れる氷山の背景。\n見ているだけで体が震えてきそうな光景。\nだがこの絶景は、寒さに耐える価値がある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4312, /*level*/1, /*item_detail*/"白アザラシのぬいぐるみ。\nチャームポイントの眉は、思わずいじりたくなる。\n雪のように白く、氷山の床にたまに同化している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4313, /*level*/1, /*item_detail*/"氷河に空いた穴。\n白アザラシがたまに顔を出す。\n海中から現れたキュートさ全開の存在に、きゅん♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4320, /*level*/1, /*item_detail*/"たくさんの豆乳パックが並ぶ、特設のホッ豆乳Stand。\nカップに豆乳を注いだら、レンジで温めよう。\n普段とひと味違う美味しさに、アイドルもホッとひと息。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1539901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4330, /*level*/1, /*item_detail*/"イベント「Snow＊Love」で獲得した大きなトナカイの\nイルミネーション。冬の夜にピカピカ映える。\nその煌めきは、温かな気持ちを運んでくるだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4331, /*level*/1, /*item_detail*/"イベント「Snow＊Love」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n特別なラブに包まれたホワイトクリスマスを楽しんだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4332, /*level*/1, /*item_detail*/"イベント「Snow＊Love」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちの\nラブは、みんなの心に温かな雪を降らせていった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4333, /*level*/1, /*item_detail*/"イベント「Snow＊Love」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちのラブは、いつまでもキラキラと輝いている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4340, /*level*/1, /*item_detail*/"氷でできたミニチュアハウス。\n小さなおうちの前で、雪だるまが何かを待っている。\nクリスマスを迎えたら、そっと覗いてみよう。", /*effect_value_1*/1, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4341, /*level*/1, /*item_detail*/"凛々しく佇むりんごろうの氷像。\nつややかさや丸みも、氷でしっかりと再現されている。\n独特な表情で、いったい何を見つめているのだろうか…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4350, /*level*/1, /*item_detail*/"お正月の縁起物にぴったりな、宝船のおきもの。\n煌びやかな宝物をぎっしり乗せた豪華な造り。\n船頭の猫が、福に溢れた素晴らしい年を招くだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4351, /*level*/1, /*item_detail*/"金の意匠が鮮麗さを引き立てる、黒い和傘。\n上品で美しい姿が、アイドルたちの興味を惹く。\n思い思いに傘をさして、優雅な時間を過ごそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4352, /*level*/1, /*item_detail*/"職人の技が光る、白いしだれ梅の盆栽。\n和の心の赴くままにじっくり鑑賞していると、\n思わずそっと手を差し伸べてしまうほどの美しさ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4353, /*level*/1, /*item_detail*/"牛の飾りをあしらった門松。\nひょっこり現れたような牛が愛らしい。\nつぶらな瞳が、新しい一年の成長を見守ってくれている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4354, /*level*/1, /*item_detail*/"シックなデザインの、背の高い行燈。\nほんのりと灯る光が足元をしっかり照らしてくれるので、\n暗い場所でも安心とあたたかさをくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4355, /*level*/1, /*item_detail*/"丸窓のある衝立。\n窓の周りに花の模様が描かれた、華やかな造り。\nオシャレで和風な部屋作りに最適な逸品。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4356, /*level*/1, /*item_detail*/"豪華絢爛な和室の壁。\n金の装飾は、まるで高級旅館のよう。豪華さのせいか、\nアイドルたちの姿勢がいつもよりピシッとなっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4357, /*level*/1, /*item_detail*/"豪華絢爛な和風の床。\n綺麗に敷き詰められた畳は、なんと金色に輝いている。\n歩くだけでゴージャスな気分になること間違いなし♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4358, /*level*/1, /*item_detail*/"豪華絢爛なお城の背景。\n金色に染まった富士山や波が、なんとも縁起が良い。\nここで過ごす一年が、素晴らしいものになる予感がする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4359, /*level*/1, /*item_detail*/"職人の技が光る、紅いしだれ梅の盆栽。\n見た目も香りも、もちろん一流品。\n思う存分わびさびを感じて、心を豊かにしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4360, /*level*/1, /*item_detail*/"茶色い牛の飾りをあしらった門松。\nちょこんと顔を覗かせている姿は愛らしく、\n立派な角は不幸や不運を跳ね飛ばしてくれそうだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4370, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した餅つきセット。\nぺったんぺったん、あうんの呼吸でお餅をつこう。\n新年をお祝いしながらつくと、さらに美味しくなるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4371, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、集まったファンと新年を祝った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4372, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは、ファンとの繋がりを実感した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4373, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。少女たちは、たくさんの人の幸せを願った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4374, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した七色の\nトロフィー。いくつもの輝きを束ねたＰに贈られる。\nこの一年も、少女たちは歌い踊り、歩み続けていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4380, /*level*/1, /*item_detail*/"「ようこそ ! シンデレラキッチン ! 」のゲーム筐体。\nいろんなお店を開いて、あまーい幸せをお届け !\n注文を受けても慌てずに、美味しいスイーツを作ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1534001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4390, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\n焼きソーセージの屋台。焼きたてアツアツを召し上がれ♪\nその美味しさに、笑顔がはじけること間違いなし。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1540901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4400, /*level*/1, /*item_detail*/"イベント「君のステージ衣装、本当は…」で獲得した\nハンガースタンド。PVで着た衣装がかかっている。\n一度しか訪れない、輝かしくも時には苦い、青春の象徴。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541101, /*individual_flag*/0);
