INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4420, /*level*/1, /*item_detail*/"カップケーキを製造するマシーン。\nスイッチを押すと、カップケーキが全自動で作られる。\n分量、温度、もちろん味も完璧な一品を召し上がれ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4421, /*level*/1, /*item_detail*/"チョコレートを製造するベルトコンベア。\n型にさまざまな味のチョコレートを流し込んでいく\n機械は、みんなのもとへ甘い幸せを運ぶだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4422, /*level*/1, /*item_detail*/"チョコレートドーナツを製造するベルトコンベア。\n流れてくるドーナツを、さまざまな味でコーティング。\n色とりどりに生まれ変わる様子は、まさに芸術。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4423, /*level*/1, /*item_detail*/"業務用の大きな冷蔵庫。\nたくさんのチョコが並び、美味しく冷やされている。\n完成までもう少し。お腹が減っても、ゆっくり待とう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4424, /*level*/1, /*item_detail*/"溶けたチョコレートがたっぷり入った大きな釜。\nチョコレートのくちどけと風味を上げるため、\n絶え間なくかき混ぜ続けるその姿は、工場一の働き者。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4425, /*level*/1, /*item_detail*/"チョコレートタンク。\nカラフルなチョコレートがぎっしりと詰まった\n夢のような筒に、大人も子どもも心がくすぐられる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4426, /*level*/1, /*item_detail*/"チョコレート工場をイメージした壁。\n窓枠がカラフルなチョコレートのように彩られていて、\nいろんな味を想像するアイドルも多いのだとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
