INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4470, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したスケートリンク。\nリンクに降り立ったら、気分はまるで冬の妖精。\n慎重に、時に大胆に ! スケートを楽しんじゃおう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4471, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\n多くの人々と触れ合い、あたたかい笑顔を届けた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4472, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちの\n想いは冷え切った心をも解かし、優しく包み込んだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4473, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n季節が巡っても、少女たちのぬくもりは残り続ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4480, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n熱帯魚鑑賞スペース。疲れた時はコーヒーで一息ついて、\nゆるりと揺蕩う熱帯魚たちに癒されよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4490, /*level*/1, /*item_detail*/"鋼鉄製の頑丈な作業台。\nメンテナンスにも、製作にも、書き物机にも使える。\n耐荷重はオートマトン10体分だとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4491, /*level*/1, /*item_detail*/"タイプライターが置かれた書斎机。\nレトロな気分に浸り、たまには手紙で言葉を届けたい。\nそんな時は、小気味よい音に合わせて、想いを綴ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4492, /*level*/1, /*item_detail*/"鋼鉄製のラック。\n頑丈で、本棚としても使用できる優れもの。\nダークブラウンの色味は、無骨ながらも味わいがある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4493, /*level*/1, /*item_detail*/"歯車が詰まった樽状の駆動装置。\n何かの測定に使う機械のようだが、詳細は不明。\n耳を澄ませば、中で回り続ける歯車の音が聞こえるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4494, /*level*/1, /*item_detail*/"蒸気のエネルギーを使い、回転を生む機械。\n蒸気都市では必要不可欠な機関のひとつであり、\n人々の豊かな生活を支えているらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4495, /*level*/1, /*item_detail*/"とても巨大な歯車。\n床下に動力を送るため、ひたすら回り続ける。\nさまざまな機構が機能するために欠かせない部品だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4496, /*level*/1, /*item_detail*/"蒸気を貯めておくタンク。\n地下に住まう人類は、衰弱した生命を特殊な蒸気で補って\n生活していたらしいが…これはただの水蒸気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4497, /*level*/1, /*item_detail*/"蒸気用の配管。\nさまざまな動力源となる蒸気の輸送に用いられる。\nとても熱いので、うっかり手を触れないように。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4498, /*level*/1, /*item_detail*/"蒸気用の配管の形状違い。\nかつての蒸気機関を身近に感じられる、古い作り。\nこの蒸気はどこから来て、どこへ運ばれるのだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4499, /*level*/1, /*item_detail*/"蒸気都市をイメージした壁。\n丈夫な鉄板が何層にも重ねられ、組み上げられている。\nそれは人を閉じ込める檻か、はたまた人を守る盾か。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4500, /*level*/1, /*item_detail*/"蒸気都市をイメージした床。\nつぎはぎに張り付けられた鉄板は相当な年季もの。\n長い間、人類が地下で生活していたことが伺える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4501, /*level*/1, /*item_detail*/"蒸気都市をイメージした背景。\n無機質なこの場所では歯車が絶えず回り、蒸気が満ちる。\nいつか花咲く地上を見に行くのだと、誰かが語った世界。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4502, /*level*/1, /*item_detail*/"生産終了した、旧型の蒸気タービン。\nもはや見かけることも滅多にないものだが、\n今でも人々の生活を人知れず支えている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4503, /*level*/1, /*item_detail*/"生産終了した、旧型の巨大歯車。\nかつては都市中枢へと動力を送っていたとか。\n止める者がいない機関は、ただひたすらに回り続ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4510, /*level*/1, /*item_detail*/"採石場をイメージしたセット。\n最高にカッコイイポーズを決めて、爆発を巻き起こそう !\nしかし、ヒーローを狙うイタズラな手にはご用心…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4520, /*level*/1, /*item_detail*/"イベント「躍るFLAGSHIP」で獲得した夕焼け背景。\n沈みゆく夕日を横切る印象的な白い飛行機雲。それは、\n悩んで立ち止まって、それでも進み続ける勇敢さの象徴。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4521, /*level*/1, /*item_detail*/"イベント「躍るFLAGSHIP」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは一歩進むために、本音をぶつけあった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4522, /*level*/1, /*item_detail*/"イベント「躍るFLAGSHIP」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n少女たちはアイドルに、そして未来に恋焦がれている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4523, /*level*/1, /*item_detail*/"イベント「躍るFLAGSHIP」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。それぞれの恋の答えが芽吹く時を待っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4530, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した卒業看板。\n人生の通過点、そして別れを象徴するもの。\n隣に立って、色あせない思い出の一枚を撮ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4531, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\nとりどりのアイドル観を語り、自分らしさを示した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4532, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。それぞれ違う輝きを\n持つ少女たちの世界は、これからも広がっていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4533, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちはアイドルとして、それぞれの人生を歩む。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4540, /*level*/1, /*item_detail*/"サガンシップZの入ったダンボール。\nどんなに辛い肩こり腰痛も、これ一枚で撃退 !\n※ゾンビィでない人間が貼ると医療行為にあたります。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4541, /*level*/1, /*item_detail*/"ドライブイン鳥印のフィッティングルーム。\nコッコ君と一緒にお店の宣伝をしよう !\nやき鳥一番鳥めし二番、ドライブイン鳥♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4542, /*level*/1, /*item_detail*/"フランシュシュの超初期物販販売Tシャツ。\n6号も思わずダサいとツッコミを入れた。\nしかし着続けていると、そのうち愛着がわいてくる…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4543, /*level*/1, /*item_detail*/"フランシュシュ1号・源さくらのぬいぐるみ。\nトラックに跳ねられて、気付いたらゾンビィに ! ？\nアイドルで佐賀を救えなんて…どやんすどやんすー ! ？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4544, /*level*/1, /*item_detail*/"フランシュシュ2号・二階堂サキのぬいぐるみ。\n九州を制覇した暴走族「怒羅美」の伝説の特攻隊長。\n夢はでっかく佐賀から全国制覇。夜露死苦ゥ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4545, /*level*/1, /*item_detail*/"フランシュシュ3号・水野愛のぬいぐるみ。\nライブ中に電撃引退をした元・伝説の平成のアイドル。\nその瞳は、常に最高の自分を信じている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4546, /*level*/1, /*item_detail*/"フランシュシュ4号・紺野純子のぬいぐるみ。\n80年代に一世を風靡した、伝説の昭和のアイドル。\n古き良き清純さをバッチリ再現。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4547, /*level*/1, /*item_detail*/"フランシュシュ5号・ゆうぎりのぬいぐるみ。\n廓詞をはんなりと操る、幕末の伝説の花魁。\n仲間の鼓舞には迷わずビンタ。そして…舞います。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4548, /*level*/1, /*item_detail*/"フランシュシュ6号・星川リリィのぬいぐるみ。\nテレビで見ない日はなかった元・伝説の天才子役。\nまさお君？誰それ？リリィちゃんはリリィちゃんだよ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4549, /*level*/1, /*item_detail*/"フランシュシュ0号・山田たえのぬいぐるみ。\n伝説の山田たえ。伝説っていったら伝説なんじゃーい !\n色んなところがもろいので、取り扱いには要注意。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1541601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4550, /*level*/1, /*item_detail*/"謎のアイドルプロデューサー巽幸太郎のぬいぐるみ。\n7人の少女たちを現代に蘇らせ、佐賀を救うのが目的。\nサングラスとゲソがトレードマーク。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4551, /*level*/1, /*item_detail*/"巽幸太郎が飼っているゾンビィ犬・ロメロのぬいぐるみ。\nチャーミングポイントは、右前足の佐賀県形の痣。\n抱っこした時にジャストフィットなサイズ感。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4552, /*level*/1, /*item_detail*/"キレのある踊りを披露するゾンビィ人形。\n襲い掛かってこない紳士的なゾンビィ。\n一緒に踊って、振り付けをマスターしよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4553, /*level*/1, /*item_detail*/"ゾンビランドサガプロジェクトを発表した黒板。\n文字が反対なのは、も、もちろんわざとじゃい !\n裏返しを想定してなかった訳じゃないと主張している。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4560, /*level*/1, /*item_detail*/"なぎなたの稽古を行えるスペース。\n安全性に配慮されているので、思う存分稽古しよう。\nさまざまな型を会得し、己の心身を磨き上げるべし !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4570, /*level*/1, /*item_detail*/"イベント「イケナイGO AHEAD」で獲得した真っ赤な\nトートバッグ。バッグを片手に可愛いポーズを決めよう。\nそれはさながら、キャンバスに描かれた恋模様。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4571, /*level*/1, /*item_detail*/"イベント「イケナイGO AHEAD」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、悩める人を鼓舞し背中を押した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4572, /*level*/1, /*item_detail*/"イベント「イケナイGO AHEAD」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは、挑戦を経て一回り大きく成長した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4573, /*level*/1, /*item_detail*/"イベント「イケナイGO AHEAD」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。少女たちは新たな世界への一歩を踏み出す。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4580, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した射撃練習スペース。\nゴーグルよし、耳当てよし、照準を定めて…BANG !\n狙い澄ましたショットを、カッコよく決めよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4581, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n激しい特訓を経て、一皮剥けた自分を手に入れた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4582, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルたちは、\nチームになることで、何倍もの力を発揮した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4583, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちの強さ。それは、自分の正義を貫く勇気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4590, /*level*/1, /*item_detail*/"青空の下で映えるクッキングスペース。\nトントン野菜を切って、炒めて、煮込んで。\n最後に愛情を入れれば、美味しいカレーの出来上がり♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4600, /*level*/1, /*item_detail*/"癒しの場所しんでれらの座敷席。\n掘りごたつが嬉しい常連アイドルたちの定番席。\nイチオシメニューを頼んだら、一日お疲れ様の乾杯を♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4610, /*level*/1, /*item_detail*/"ダークブラウンの本棚。\nシックな装いが、大人な空間を演出してくれる。\n並んだ本も、インテリ気分を味わえるラインナップだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4611, /*level*/1, /*item_detail*/"黒の冷蔵庫。\n部屋に置けば、まるでおしゃれなモデルルームのよう。\nソーダ、レモン水、コーヒー、好きな飲み物を入れよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1511301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4612, /*level*/1, /*item_detail*/"可愛いピンクの、くもでできたもこふわクジラ。\n夕暮れの赤い空がお気に入りらしい。\n夜への道を、すいすい優雅に泳ぐ姿に癒される。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4613, /*level*/1, /*item_detail*/"白い装いのウッドブランコ。\nぶーらんぶーらん、のんびり揺られよう。\n悲しいことがあっても、ブランコが寄り添ってくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4614, /*level*/1, /*item_detail*/"オレンジ色をした宇宙服。\n着替えて踏み出すのは、小さくても大きな一歩。\nさぁ行こう、楽しくてわくわくする宇宙旅行へ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1518001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4615, /*level*/1, /*item_detail*/"黒いスペースモニター。\nホログラムでの映像投影は、さながら本物のSF技術。\nみんなで集まって、作戦会議スタート !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2016, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4620, /*level*/1, /*item_detail*/"「クイズ de シンデレラ」で使うモニター。\nぴにゃこら太がアイドルたちと遊ぶために用意したもの。\nいっぱい正解して優勝を目指そう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4621, /*level*/1, /*item_detail*/"「クイズ de シンデレラ」のゲーム筐体。\nキミの知識を総動員して、クイズに挑戦しよう !\n気付けば、謎のクイズ空間に立っている…かも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1534001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4630, /*level*/1, /*item_detail*/"ゲームセンターをイメージした壁。\n無機質さよりも、あたたかさを重視したデザイン。\nたくさんの楽し気な笑顔が、よく映える色。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4631, /*level*/1, /*item_detail*/"ゲームセンターをイメージした床。\n明るくポップな色合いが、賑やかな雰囲気を醸し出す。\nこの上を歩くだけで、なんだか楽しくなってくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4632, /*level*/1, /*item_detail*/"街の一角にあるゲームセンターの背景。\n昼も夜も、活気に満ちた楽しい空間が広がっている。\nたまには羽目を外して遊んでみるのも、悪くないかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4640, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\n学校の机。和気藹々と談笑しながら、給食を楽しもう。\n食材と作り手への感謝も忘れずに、いただきます。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4650, /*level*/1, /*item_detail*/"イベント「とんでいっちゃいたいの」で獲得した\n空飛ぶクジラ島のミニチュア。夢のように\nふわりと飛ぶ神秘的な姿に、夏の風を感じる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2022, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4651, /*level*/1, /*item_detail*/"イベント「とんでいっちゃいたいの」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは自由気ままに、思い出を紡いでいった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4652, /*level*/1, /*item_detail*/"イベント「とんでいっちゃいたいの」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは終わる時間を惜しみ、心に魔法をかけた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4653, /*level*/1, /*item_detail*/"イベント「とんでいっちゃいたいの」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。キラキラ輝く思い出は、いつだって真新しい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4660, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した館の壁。\n管理された箱庭の檻は、少女たちの自由を奪う。だが、\n小さな星は輝く事を忘れず、飛び出す瞬間を待っていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4661, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n少女たちは、競う事に悩みながらも、全力で戦い抜いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4662, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n少女たちは大人の期待を超え、ただひとつの輝きとなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4663, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。少女たちの好奇心こそ、未来を掴む力となる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4664, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した七色の\nトロフィー。いくつもの輝きを束ねたＰに贈られる。\n自分の道は自分で掴む。貪欲な星々の輝きは、未知数だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001402, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4670, /*level*/1, /*item_detail*/"DyDoの自動販売機。\n充実のラインナップに、どれを買おうか悩んでしまう。\n選ぶなら、こころとからだに、おいしいものを。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4680, /*level*/1, /*item_detail*/"タロット占いができる占いテーブル。\nカードをめくっていけば、運勢が導き出される。\n占いの結果をどう受け取るかは、あなた次第。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4690, /*level*/1, /*item_detail*/"心惹かれるデザートが並んだビュッフェテーブル。\n美味しいジュースで乾杯すれば、会話も弾む。\nたくさん並んだ中から、好きなデザートを堪能しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4691, /*level*/1, /*item_detail*/"ベンチフラワーが施されたガーデンベンチ①。\nバージンロードの左右に配置されるベンチだが、\n会場デザインは自由。好みの場所に配置しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1531301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4692, /*level*/1, /*item_detail*/"ベンチフラワーが施されたガーデンベンチ②。\n腰かければ来賓者のような気分を味わえる。\n胸を高鳴らせながら、拍手の準備を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1531301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4693, /*level*/1, /*item_detail*/"純白のスイングベル。\nその鐘が奏でる幸福な報せは、\n美しい音色とともに、どこまでも響き渡っていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2021, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4694, /*level*/1, /*item_detail*/"美しい花々で彩られたガゼボ。\nこの下で愛を誓えば、一生住むところに困らないという。\nあるかもしれない未来に思いを馳せつつ、今はひと休み。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1542901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4695, /*level*/1, /*item_detail*/"ウェディングには欠かせない装花。\n愛や希望を象徴する花々に彩られれば、\n会場も一層明るく、華やかになること間違いなし。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4696, /*level*/1, /*item_detail*/"キュートなウェディングバルーン。\n青と白の上品な色合いで会場を彩る装飾。\n膨らむ想いには、祝福の心が詰まっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4697, /*level*/1, /*item_detail*/"キャンバス風の華やかなウェルカムボード。\n可憐な花々と、手作りのキャンバスがお出迎え。\n愛に満ちた会場はこの先です♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4698, /*level*/1, /*item_detail*/"花びらが絨毯のように敷き詰められたフラワーロード。\nたくさん並べれば、バージンロードのできあがり。\n重ねた年月を思い返しながら、道を往こう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4699, /*level*/1, /*item_detail*/"綺麗に飾り付けをされた、ガーデンウェディングの柵。\n会場に馴染む、優しい色合い。\n大切なひとときを守ってくれる、頼もしい存在でもある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4700, /*level*/1, /*item_detail*/"ガーデンウェディングテーマの床。\nところどころにちりばめられた黄色の花びらが、\nここへ訪れた誰しもを祝福してくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4701, /*level*/1, /*item_detail*/"開放感のあるガーデンウェディングの背景。\n森の奥で開かれるのは、可愛らしいウェディング。\n優しい緑に包まれた、愛の園へようこそ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4702, /*level*/1, /*item_detail*/"デザートの種類が入れ替わったビュッフェテーブル。\n楽しい祝宴は、まだまだ終わらない。\n幸せの味をお腹いっぱい召し上がれ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4703, /*level*/1, /*item_detail*/"幸せを呼ぶ青いスイングベル。\n上品な美しさに、奏でる音も心なしか澄んで聞こえる。\n祝福の音色に、耳を傾けよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2021, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4710, /*level*/1, /*item_detail*/"イベント「いとしーさー♥」で獲得した青いシーサー。\n沖縄の守り神が、のほほんとしたいとしい姿で登場。\n口を開いたシーサーは、魔除けの役割を担っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4711, /*level*/1, /*item_detail*/"イベント「いとしーさー♥」で獲得した赤いシーサー。\nいとしい笑顔で幸せを呼び、口の中に閉じ込める。\n青いシーサーと対にして置けば、効果が増すという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4712, /*level*/1, /*item_detail*/"イベント「いとしーさー♥」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n少女たちは、穏やかでのんびりとした時間を楽しんだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4713, /*level*/1, /*item_detail*/"イベント「いとしーさー♥」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n少女たちは輝く思い出を胸に、人々と心を通わせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4714, /*level*/1, /*item_detail*/"イベント「いとしーさー♥」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。少女たちのいとしい姿は、誰もを癒していく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4720, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した洋館のテーブル。\n落ち着いたデザインが、シックな雰囲気を醸し出す。\nけれど、ときどき物がひとりでに動きだすことも…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4721, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\nホラーの魅力を理解し、ファンたちにその魅力を伝えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4722, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。多くのファンたちへ\nとびきりの恐怖と、ホラーを共に楽しむ時間を届けた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4723, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちはこれからも、仲間と共に恐怖を楽しむだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4750, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した、\n射的屋セット。ちょいとお嬢さん、試しに一発どうだい？\n狙うは大きなりんごろう ! 見事落とせば拍手喝采 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4760, /*level*/1, /*item_detail*/"イベント「パ・リ・ラ」で獲得したトゥッカーノの\nぬいぐるみ。鮮やかな色はまさにアマゾンの宝石。\n本物さながらの毛並みに、ウキウキ気分♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4761, /*level*/1, /*item_detail*/"イベント「パ・リ・ラ」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n旅する中でもお祭り気分でファンを明るく魅了した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
