INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4771, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n互いを思いやる心で支え合い、役を見事に演じてみせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4772, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。行動と結果によって、\n少女たちは決断の先にある自分の道の意味を示した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4773, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nすれ違いを乗り越えて、少女たちの絆はより固くなった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4780, /*level*/1, /*item_detail*/"大事な戦利品の入ったバッグが置かれているテーブル。\n「好き」の気持ちが詰まった同人誌は、何度読んでも\n尊みで満たされる。さあ君も、レッツ布教活動 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1545201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4790, /*level*/1, /*item_detail*/"SOS団団長の机。\n涼宮ハルヒの指定席であり、コンピ研から譲り受けたPC\nが置いてある。SOS団では、この世の不思議を募集中 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4791, /*level*/1, /*item_detail*/"SOS団の部室にある、パイプイス。\n睡眠薬みたいな名前のSF小説が置いてある。\nユニークな内容で、一読する価値アリ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1545001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4792, /*level*/1, /*item_detail*/"『朝比奈ミクルの冒険』撮影時に使われた撮影セット。\n監督の指示は絶対。何故なら、超監督の指示だから。\nみんなの力を合わせれば某映画賞も夢じゃない…かも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4793, /*level*/1, /*item_detail*/"フィッティングルーム。\nバイト代としてもらったカエルのキグルミを着られる。\n作ると高額なので、バイト代としては破格…とも言える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4794, /*level*/1, /*item_detail*/"SOS団団長の腕章セット。\nコラボなので特別に、団長から腕章の装着許可が下りた。\n気分に合わせて、好きな腕章を着けよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4795, /*level*/1, /*item_detail*/"SOS団の部室にある、ハンガースタンド。\nハルヒがどこからか調達してきた衣装が掛かっている。\nどの衣装も、みくるに凄まじく似合っていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4796, /*level*/1, /*item_detail*/"おめんやさん。\n正体不明ながら、何故かどれも宇宙人ぽさを感じさせる。\n繰り返される日常に飽きたら、被ってみてもいいかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4797, /*level*/1, /*item_detail*/"まっがーれ↓スプーン。\n超能力者になりきって、スプーン曲げを体験できる。\n今はムムムンッではなく、まっがーれ↓と念じてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4798, /*level*/1, /*item_detail*/"願い事が書かれた短冊が吊るされている笹の葉。\n団員各2枚ずつなのは、それぞれ織姫と彦星宛て。\n25年後と16年後、団員たちの願い事は叶うのだろうか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4799, /*level*/1, /*item_detail*/"涼宮ハルヒのぬいぐるみ。\nSOS団団長。言動は奇抜だが、常識的な精神の持ち主。\nそして彼女は今日も、団員たちと不思議を探していく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4800, /*level*/1, /*item_detail*/"キョンのぬいぐるみ。\nSOS団団員。ハルヒを止めることができる唯一の人物。\n平凡な日常が続くことを願いつつ非日常を楽しんでいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4801, /*level*/1, /*item_detail*/"朝比奈みくるのぬいぐるみ。\nSOS団副々団長兼書記。ロリ巨乳萌えマスコットキャラ。\nなお、年齢は禁則事項です。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
