INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4821, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した宇宙船の床。\n疑似的な重力が再現され、地上と同じ活動が可能。\nこれは縛るためではなく、帰ってくるための地。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
