INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4856, /*level*/1, /*item_detail*/"バスケットボールゲームの筐体。\n見かけは簡単そうだが、触ってみると難しい。\n華麗にシュートが決まった時の爽快感はたまらない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4857, /*level*/1, /*item_detail*/"エアホッケーゲームの筐体。\n見つめ合って、いざ1対1の真剣勝負 !\n相手の動きを見切って、パックを力強く打ち込もう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4858, /*level*/1, /*item_detail*/"格闘ゲームの筐体。\n対人戦もCPU戦もできる、人気の格闘ゲーム。\n椅子の座り心地もよく、長時間のプレイでも安心。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4859, /*level*/1, /*item_detail*/"格闘ゲームの筐体その②。\n巧みなレバー捌きでコンボを決めて、勝利をつかめ !\n手強い相手との一戦は、手に汗握るものになるだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4860, /*level*/1, /*item_detail*/"2人プレイができるシューティングゲームの筐体。\n目まぐるしく現れる標的をどんどん撃ち抜き、\n力を合わせて、目指せハイスコア !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4861, /*level*/1, /*item_detail*/"ダンスゲームの筐体。\nアイドル活動で鍛えた腕前をゲームでもお披露目 !\nただし、油断は禁物。意外と手強いと評判だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1537901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4862, /*level*/1, /*item_detail*/"赤いボディのお菓子キャッチャー。\n色とりどりのアクリルストーンがお菓子を彩る。\nタイミングよくストーンを落として、景品を押し出そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1538701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4863, /*level*/1, /*item_detail*/"青いボディのお菓子キャッチャー。\nぐるぐる回るお菓子やストーンには、魅力がたっぷり。\nついついメダルを投入してしまう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1538701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4864, /*level*/1, /*item_detail*/"緑のボディのお菓子キャッチャー。\n豊富な本体の色で、ゲームセンター内を彩る存在。\n鮮やかな筐体から出てきたお菓子は、一層美味しそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1538701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4865, /*level*/1, /*item_detail*/"盛り効果のあるプリントシール機。\nふわ甘愛され系フェイスならこれでキメ♪\nたくさんのカワイイで自分をデコっちゃおう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4866, /*level*/1, /*item_detail*/"整え効果のあるプリントシール機。\n美顔効果もあり、うるツヤ透明感を演出。\nいつもより一段上のオトナカワイイは、もう君のもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4867, /*level*/1, /*item_detail*/"青いボディのパンチングマシン。\n懐かしいゲームが、新色になって再登場。\n鋭い拳をお見舞いして、記録を塗り替えよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4868, /*level*/1, /*item_detail*/"ゲームセンターのカウンター。\n景品等を取り扱ったりする場所。\nわからないことがあったら、スタッフさんに尋ねよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4869, /*level*/1, /*item_detail*/"豪華な景品がずらりと並んだケース。\n景品を手にするのは、多くのメダルを集めた者だけ。\nガラスのむこうに、小さな憧れが詰まっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4870, /*level*/1, /*item_detail*/"ゲームセンターのメダル販売機。\nお金をメダルに換えたら、たっぷり遊ぶ準備は完了 !\n満足するまで、ゲームをやり尽くそう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1543701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4880, /*level*/1, /*item_detail*/"星環世界をイメージした浮遊ステージ。\n自由な未来と夢を願って、この場所から届けよう。\n誰かの心を動かす、アイドルの「歌」を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1545401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4890, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n松ぼっくりツリー工作デスク。心のままに彩れば、\nあたたかさに満ちた、小さなツリーのできあがり。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1545501, /*individual_flag*/0);
