INSERT INTO `room_item_detail` VALUES (/*room_item_id*/4911, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\nひとつひとつ丁寧に、これまでの軌跡をたどった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
