INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5310, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\nごちそう。お仕事を頑張った褒美に贈られた高級カニ。\n上手に殻をむいて、たっぷり詰まった身を味わおう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549201, /*individual_flag*/1);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5320, /*level*/1, /*item_detail*/"フラワーショップのレジカウンター。\nレジに立ってキーを叩けば、それだけで店員さん気分♪\nただし、早打ちは故障の原因となるのでご注意ください。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5321, /*level*/1, /*item_detail*/"フラワーショップのラッピング作業台。\nラッピングペーパーとリボンで可愛い花束を作ろう。\n上手にできたら、素敵な笑顔の花が咲くこと間違いなし。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5322, /*level*/1, /*item_detail*/"フラワーショップの商品棚。鉢物の花が中心。\n色とりどりの花たちから漂ういい香りは、\nアイドルたちもふと足を止めて楽しむほど。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5323, /*level*/1, /*item_detail*/"フラワーショップの商品棚。暖色系の切り花が中心。\n情熱的に咲き誇る花々を眺めていると、\n日々を頑張るエネルギーが湧いてくると評判。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5324, /*level*/1, /*item_detail*/"フラワーショップの商品棚。寒色系の切り花が中心。\n凛々しく咲き誇る花々を眺めていると、\n気が引き締まってお仕事やレッスンに身が入ると評判。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5325, /*level*/1, /*item_detail*/"ハーバリウムやプリザーブドフラワーが並んだ商品棚。\n枯れることなく咲き続ける花たちを見て、\n密かに憧れを抱くアイドルも多いらしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5326, /*level*/1, /*item_detail*/"アネモネが植わったプランターボックス。\n色とりどりのアネモネが、事務所の雰囲気を華々しく\n飾ってくれる。豊かな色彩を心ゆくまで楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5327, /*level*/1, /*item_detail*/"花桶に入ったチューリップ。\n近くに寄ると、個性豊かな香りが微かに漂ってくる。\n互いを高め合う香りのハーモニーがアイドルたちに人気。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5328, /*level*/1, /*item_detail*/"黄色を中心としたマーガレットの鉢植え。\n生命力豊かに活き活きと咲き誇る姿はまるで太陽。\nその明るい色合いは、みんなに元気をくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5329, /*level*/1, /*item_detail*/"ピンク色を中心としたマーガレットの鉢植え。\n鉢植えの中に仲良く並んで、可憐に咲いている。\nその可愛らしい姿に、みんなが癒やされている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1500201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5330, /*level*/1, /*item_detail*/"フラワーショップの壁。\n花の色が映えるように、白色を基調としている。\n丈夫な作りで、雨風から中の花々を守ってくれる存在。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5331, /*level*/1, /*item_detail*/"フラワーショップの背景。\n石やレンガで造られた建物が建ち並ぶオシャレな街。\n街の人々は美しい花を活かし、花の美しさに活かされる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5332, /*level*/1, /*item_detail*/"フラワーショップのレジカウンター。\n目印となる白色は花の色をより際立たせるため。\nここからは、みんなの花のような笑顔もよく見える。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5333, /*level*/1, /*item_detail*/"フラワーショップのラッピング作業台。\n心を込めてラッピングして、綺麗な花束を作ろう。\n花と共に束ねられた想いも、きっと届くから。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5340, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得したお花畑。\n季節の彩りに包まれた空間は、時間を忘れて楽しめる。\n幸せを呼ぶ四葉のクローバーも、見つけられるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5341, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは、溢れるロマンスで皆をときめかせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5342, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nアイドルたちは、心をあたたかな気持ちで満たした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5343, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。少女たちの未来は、笑顔に満ちている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5350, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得したサーバーラック。\n発光しているのは絶え間なく稼働し続けている証拠。\n取り扱いはくれぐれも慎重に。電源コードは抜かないで !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5351, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。仮想現実の舞台に\n立ったアイドルたちは、電脳の世界で輝きを放つ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5352, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。知られたくない者と、\n知りたい者。両者は少しずつ、歩み寄っていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5353, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n新たな絆、新たな技術。少女たちは未来を今にしていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5360, /*level*/1, /*item_detail*/"魚のパイを焼ける、大きなオーブン。\n焼き上がるまで、パイと一緒に期待を膨らませよう。\nできたての味はso tasty♪UK気分でいただきます。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549401, /*individual_flag*/1);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5370, /*level*/1, /*item_detail*/"病院で見かける視力検査用器具一式。\nみんなはどこまで見えるかな？ちなみに手に持つ\n黒いやつは、スプーンではなく遮眼子といいます。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5380, /*level*/1, /*item_detail*/"イベント「チカラ ! イズ ! ぱわー ! ! 」で獲得した\nブロックアスレチック。自分のチカラとぱわーを信じて\nブロックを奥まで押し込もう。ムムムーン、ボンバー !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5381, /*level*/1, /*item_detail*/"イベント「チカラ ! イズ ! ぱわー ! ! 」で獲得した\n銅製のトロフィー。イベントで活躍したＰに贈られる。\nアイドルたちは学園を笑顔でいっぱいにしてみせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5382, /*level*/1, /*item_detail*/"イベント「チカラ ! イズ ! ぱわー ! ! 」で獲得した\n白銀のトロフィー。アイドルを輝かせたＰに贈られる。\n苦難を乗り越え、ふたりの絆はもっともっと強くなった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5383, /*level*/1, /*item_detail*/"イベント「チカラ ! イズ ! ぱわー ! ! 」で獲得した\n純金のトロフィー。アイドルを伝説級のステージに\n導いたＰに贈られる。万能ではなくても、相棒がいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5390, /*level*/1, /*item_detail*/"玉入れ競技で使用する籠。\n赤組用の製品で、見た目よりも丈夫な造りをしている。\n今のところは赤組がリード？頑張ってください !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5391, /*level*/1, /*item_detail*/"玉入れ競技で使用する籠。\n丈夫で長持ち。こちらは白組仕様のカラーリング。\n白組も頑張って、まだまだ勝負はわかりませんよ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5392, /*level*/1, /*item_detail*/"上からパンが吊るされた、パン食い競争スペース。\n走って跳んで食らいついて、ガッツを見せよう !\n美味しくても、他コースの分には手を出さないように。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5393, /*level*/1, /*item_detail*/"校長先生がありがたいお話をする朝礼台。\nくれぐれも、お話が長いからって寝ないように。\nその後は互いの健闘を祈って、選手宣誓を行いましょう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5394, /*level*/1, /*item_detail*/"大玉転がし競技に使用する塩ビの玉。赤組用。\n驚くなかれ、大量生産品でないため結構なお値段。\nなかなかまっすぐ転がらないのは仕様です。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5395, /*level*/1, /*item_detail*/"大玉転がし競技に使用する塩ビの玉。白組用。\n膨らませるのも一苦労の大きさ。\n屋外で使用したらきちんとキレイに拭いてあげよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5396, /*level*/1, /*item_detail*/"競技エリアへの入場ゲート。\nみんなで作ったお手製のゲートで、カラフルな装飾。\n次の競技に出る選手は、早めに集まりましょう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5397, /*level*/1, /*item_detail*/"運動会で使用される等賞旗。\n見事入賞した選手が手に出来る栄誉の旗。\n全力で頑張った自分に、胸を張ろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5398, /*level*/1, /*item_detail*/"街中でもよく見かける三角コーン。\nエリア規制にも通路掲示にも使用される。\nみなさん、決められた場所を通行してくださいね。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5399, /*level*/1, /*item_detail*/"運動会のグラウンド仕様の床。\nライン引きで引かれた石灰の白線が目を引く。\n熱い勝負の舞台に足を踏み出そう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5400, /*level*/1, /*item_detail*/"運動会が開催中の背景。\nグラウンドで繰り広げられる、さまざまな勝負。\nでも一番大事なのは、思いきり体を動かし楽しむこと !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5401, /*level*/1, /*item_detail*/"校長先生が大事なお話をする朝礼台の青色版。\n今日は絶好の運動会日和になりましたね。\nみなさんの精一杯の頑張りに期待します。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1549601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5402, /*level*/1, /*item_detail*/"大玉転がし競技に使用する塩ビの玉。青組用。\n赤白青が競り合って、運動会は混戦の予感。\n青組のみなさんも優勝目指して頑張ってください !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5410, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した川床。\n桟敷の上で、団扇を片手に夕涼みを楽しもう。\n優しい蛍の光と一緒に、穏やかなひとときをどうぞ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5411, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは\n互いに向き合い、言葉を交わして歩み寄っていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5412, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。美しき古都でも、\nアイドルたちは宵の明星のように輝いてみせた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5413, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nどうか、これからもみんなと一緒にいられますように。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5420, /*level*/1, /*item_detail*/"竹細工のベンチ。\n軽くて丈夫なので、安心して座っていられる。\n夜空に煌めく星をあしらった飾り傘もついてくる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000113, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5421, /*level*/1, /*item_detail*/"天の川にかかる橋。\n雅で華やかな天女であった織姫をイメージした色。\nふたりで一緒に星を眺めるのに絶好のスポットだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5422, /*level*/1, /*item_detail*/"七夕の定番、笹飾り。\n枝葉を伸ばした笹にはたくさんの短冊が飾られている。\nアイドルのみんなは何をお願いしたのかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5423, /*level*/1, /*item_detail*/"織姫が使用する機織り機。\n織姫の持つ『天梭』と併せて使用すると言われる。\n産み出された織物は天界にかかる彩雲であったとも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5424, /*level*/1, /*item_detail*/"彦星が乗る乗り物。\n七夕の日に、織姫を迎えに行くためのもの。\n豪華な装飾が施され、年に一度の逢瀬を彩ってくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5425, /*level*/1, /*item_detail*/"七夕をモチーフにした衣装スタンド。\n天の羽衣を模した衣装が飾られている。\n願いをかなえる不思議な力が宿っているかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000312, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5426, /*level*/1, /*item_detail*/"七夕をモチーフにした衣装スタンド。\n牛飼いの服を模した衣装が飾られている。\n意外としっかりした作りで、野良仕事もできるほど。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000312, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5427, /*level*/1, /*item_detail*/"七夕に飾る行燈。\n夜空に瞬く星々が描かれている。\nその灯りを目印に、天の川を渡っていこう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5428, /*level*/1, /*item_detail*/"七夕に飾る行燈。\nこちらは紫のカラーリング。\n深みのある色合いが、逸る心を落ち着かせてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5429, /*level*/1, /*item_detail*/"七夕をモチーフにした床材。\n透明な床にキラキラと光る星が散りばめられている。\nその輝きのひとつひとつに物語が宿る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5430, /*level*/1, /*item_detail*/"七夕をモチーフにした背景。\n夜空に広がる天の川の輝きを模したもの。\n忘れられないひとときを演出しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5431, /*level*/1, /*item_detail*/"竹細工のベンチ。\nこちらの飾り傘は紫色で、落ち着いた風合いがある。\n静かな七夕の夜を、大切な人と一緒に過ごそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000113, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5432, /*level*/1, /*item_detail*/"天の川にかかる橋。\n星を越えて巡り合うふたりの絆を結んでくれる。\nたとえ離れてしまっても、心はずっと一緒。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5440, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n思い出の詰まったサーフボードたち。\nさぁ、新しい思い出を作りに、海へ出かけよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5450, /*level*/1, /*item_detail*/"イベント「サマーサイダー」で獲得したサイダー。\nボトルに詰まっているのは、夏の日の青春。\nキラキラの思い出たちが、はじけてあふれる !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550301, /*individual_flag*/1);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5451, /*level*/1, /*item_detail*/"イベント「サマーサイダー」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n向かい風の中、少女たちは想いを抱え駆け出していく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5452, /*level*/1, /*item_detail*/"イベント「サマーサイダー」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nふたりで走れば、キラキラの景色まできっと辿り着ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
