INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5460, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得したいけす。\n大きなものから小さなものまで、お魚たちが泳いでいる。\n眺めて楽しむもよし、新鮮な海の幸を味わうもよし。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/1);
