INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5510, /*level*/1, /*item_detail*/"LIKE4LIVE #cg_ootd 公式Tシャツの衣装スタンド。\nLIKE4LIVE「#cg_ootd」のTシャツが飾られている。\n大好きだと胸を張って、お気に入りを発信しよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5520, /*level*/1, /*item_detail*/"ピンクのハート型のオブジェ。\nフォトスポットとして大人気。友だちにいろんなポーズを\n撮ってもらおう。もちろん、ふたりで自撮りも超映える☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5521, /*level*/1, /*item_detail*/"ぴにゃこら太のモニュメント。\nぴにゃこら太の大きな頭が可愛らしくも迫力満点。\nその存在感に、誰もが思わず目を向けてしまうだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5522, /*level*/1, /*item_detail*/"可愛らしい見た目のキッチンカー。\n定番から変わり種まで、いろんな種類のクレープが\n売られている。スペシャルなアイス入りがオススメ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5523, /*level*/1, /*item_detail*/"ポップな雰囲気のジューススタンド。\n新鮮なフルーツをふんだんに使ったジュースを提供中。\n元気になるバナナ味と、笑顔になるイチゴ味が大人気だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5524, /*level*/1, /*item_detail*/"みんなの目を引く大きな噴水。\n憩いの場としてアイドルたちに大人気のスポット。\n流れる水の音と浮かぶ花の彩りが人々を癒やしてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5525, /*level*/1, /*item_detail*/"マーガレットが植えられたプランターボックス。\nみんなで育てた色とりどりのマーガレットの花。\n花とともに育まれた、真実の愛がそこにある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5526, /*level*/1, /*item_detail*/"カラフルでオシャレな案内板。\n気の赴くままに歩き回るのも悪くはないけど、\nどこに行くのか迷ってしまったらまずはこれを見よう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5527, /*level*/1, /*item_detail*/"7th Anniversaryテーマの壁。\nみんなを歓迎するように、広い間隔で置かれたフェンス。\n誰もが来られて、誰もが憩えるみんなの空間。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5528, /*level*/1, /*item_detail*/"7th Anniversaryテーマの床。\nタイルは規則正しく並び、よく整備が行き届いている。\nみんなが快適に過ごせるスポットになるだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5529, /*level*/1, /*item_detail*/"7th Anniversaryテーマの背景。\n流行の移ろいやすい街はアイドルたちに馴染み深い場所。\nこれからも、彼女たちは街と共に変わり続けるだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5530, /*level*/1, /*item_detail*/"紫色をしたハート型のオブジェ。\nフォトスポットとして大人気。ロマンティックな色の\nハートを背景に、とびきりの1枚を撮影しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5531, /*level*/1, /*item_detail*/"みんなの目を引く大きな噴水。\n待ち合わせ場所としてアイドルたちにも人気のスポット。\n友だちを待っている間、流れる水と花を眺めていよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5540, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\nシステムキッチン。きみだけの揚げドーナツを作ろう。\n仕上げにはお砂糖をたっぷりかけて、召し上がれ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5550, /*level*/1, /*item_detail*/"イベント「UNIQU3 VOICES ! ! ! 」で獲得した\n撮影セット。カメラの前で思うままにポーズを決めよう。\n何気ない瞬間にだって、キラリと輝く個性がある。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1550901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5551, /*level*/1, /*item_detail*/"イベント「UNIQU3 VOICES ! ! ! 」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\nバラバラの悩みを抱え、少女たちは自らと向き合った。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5552, /*level*/1, /*item_detail*/"イベント「UNIQU3 VOICES ! ! ! 」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nありのままでいられるから、この居場所はあたたかい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5553, /*level*/1, /*item_detail*/"イベント「UNIQU3 VOICES ! ! ! 」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。個性の化学反応は、ひとつの輝きに結実した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5560, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したネオンボード。\nネオンがさまざまな色に切り替わり、華やかに場を彩る。\nその煌めきは、ひとつの枠には収まらない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5561, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\n新しい自分を探して奔走し、自分らしい答えを得る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5562, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。自由に、好きな色で。\n少女たちはとりどりの色を纏い、煌いていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5563, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n認められなくてもいい。それが自分を貫くということ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5570, /*level*/1, /*item_detail*/"みんなで一緒に入れるこたつ。\n寒い日にはこたつの中で温まろう。アイスを食べて\nお口はひえひえ、体はぬくぬく。贅沢なひとときを共に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5580, /*level*/1, /*item_detail*/"サーカスの演目を披露するためのお立ち台。\nここに立てば、誰もが主役。\n自信たっぷりに、ジャグリングをお披露目しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5581, /*level*/1, /*item_detail*/"設置用のミニサーカステント。\n演目で使用する小道具などが置かれているらしい。\n外観だけでもワクワクさせる、それがサーカスの掟。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5582, /*level*/1, /*item_detail*/"サーカスの演目のひとつ、玉乗りの玉。\n乗りこなすにはコツがいるが、アイドルならばお手の物。\nバランスを崩して落ちないように気をつけよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5583, /*level*/1, /*item_detail*/"大きなカボチャが描かれたトランポリン。\n子どもも大人も目いっぱい楽しめる人気のアイテム。\n元気に楽しく跳ねながら、いろんなポーズを決めよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5584, /*level*/1, /*item_detail*/"ハロウィンサーカスの目玉のひとつである大砲。\nハロウィンらしい意匠で、舞台を賑やかに演出する。\n何が発射されるのか…想像が膨らむ大道具。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5585, /*level*/1, /*item_detail*/"空中アクロバット用のチェア。\nうずたかく積み上げられた椅子は絶妙なバランスを誇る。\nその姿は、サーカスの熱気も高めてくれるだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5586, /*level*/1, /*item_detail*/"ハロウィンサーカスの目玉、火の輪くぐりの輪。\nメラメラと燃えているように見えるが、実は火ではない。\n触っても大丈夫なので、アイドルたちも安心だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5587, /*level*/1, /*item_detail*/"大きな前輪が特徴的な、ユニークな自転車。\nとても軽く、想像以上の速度が出るらしい。\nその存在感は絶大で、上からの眺めもいいとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5588, /*level*/1, /*item_detail*/"ハロウィンサーカスを彩るバルーン。\n妖しくも可愛らしいデザインが施されている。\nひとりでにゆらゆら揺れるのは、風のせい？それとも…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5589, /*level*/1, /*item_detail*/"ハロウィンサーカスをイメージした壁。\n壁に描かれたかぼちゃの口は、演者用の出入り口。\n関係者以外の出入りはご遠慮ください…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5590, /*level*/1, /*item_detail*/"ハロウィンサーカスの円形ステージ。\n多彩な個性が煌めく舞台では、誰もが主役。\n胸を張って歩こう。ここは楽しいハロウィンの世界だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5591, /*level*/1, /*item_detail*/"ハロウィンナイトの遊園地。\n夜闇の中、あたりを照らすのは、空に浮かぶ妖しい月。\n奇妙で楽しい一夜が、口を開いて君を待ちわびている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5592, /*level*/1, /*item_detail*/"大きなカボチャが描かれた、色違いのトランポリン。\n同じ動きでも、色が違えば見え方も変わるかも？\nいろんな魅力を研究しつつ、思いっきり楽しもう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5593, /*level*/1, /*item_detail*/"ハロウィンサーカスの目玉、色違いの火の輪くぐりの輪。\nこちらも、触っても熱くない安全設計。\nとはいえ実際にくぐるには、やはり勇気がいるようだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5600, /*level*/1, /*item_detail*/"「拾ってください。」と書かれたダンボール箱。\nつぶらな瞳の猫に、通りかかった少女の心が揺れ動く。\n出会ってしまった運命に、抗うことなど出来はしない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5610, /*level*/1, /*item_detail*/"イベント「ダンシング・デッド」で獲得したDJブース。\n人だってゾンビだって楽しいことが大好き。\n刺激的な音楽に合わせて、朝まで踊り続けよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5611, /*level*/1, /*item_detail*/"イベント「ダンシング・デッド」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n布を合わせるように心を重ね、少女たちは絆を深めた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5612, /*level*/1, /*item_detail*/"イベント「ダンシング・デッド」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nオシャレはまるで人生。自由に楽しめばそれが正解だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5613, /*level*/1, /*item_detail*/"イベント「ダンシング・デッド」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。らしくない自分を纏い、世界がさらに輝いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5620, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得したミニステージ。\n秋の葉が色づくように、少女たちも日々成長している。\nその移ろいの瞬間を切り取った、ときめく表情にご注目。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5621, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。夢を追う人々と\n触れ合い、アイドルたちは自身の将来にも思いを馳せた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5622, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。アイドルもまた、\n夢を追う側。その道はこれからも続いていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5623, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちの夢も未来も、まだ色づき始めたばかりだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5630, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n温泉卵の湯つぼ。とろける半熟卵がいつでも作れる。\n殻を剥いたら…出来たてほやほやを、いただきまーす !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5640, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得したミニステージ。\nサイバーでクールなダンスをするアイドルから\n目が離せない。彼女たちの軌跡はやがて、永遠へと至る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1551901, /*individual_flag*/1);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5641, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n言葉はなくとも、彼女たちのダンスが全てを語っていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5642, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n磨かれた星はさらに煌きを放ち、未来へと繋がっていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5643, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。また一緒に踊ろう。鳴り続ける音楽の中で。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5650, /*level*/1, /*item_detail*/"収穫コンテナに板を貼りつけて作ったイス。\nちょっと座って、ひと休み。\n秋の空、流れる雲に、ほっとひと息。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000104, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5651, /*level*/1, /*item_detail*/"さつまいもの収穫コンテナ。\n頑丈で、大きさは充分。\nいっぱい収穫して、いっぱい詰めこもう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5652, /*level*/1, /*item_detail*/"積み重ねられた、さつまいもの収穫コンテナ。\nたくさん詰めこまれていく、紅紫色のファンタジー。\nさつまいも好きにとっては、夢のような光景。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5653, /*level*/1, /*item_detail*/"さつまいも出荷用のダンボール。\n紅紫色の夢をいっぱいに乗せ、\nみんなのもとへ旅立つときを待っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5654, /*level*/1, /*item_detail*/"さつまいもが埋めてあるたき火。\nそろそろ焼けたかな……？\nふたりではんぶんこすれば、ホクホク笑顔もおそろいに !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5655, /*level*/1, /*item_detail*/"びっしりと育っている、さつまいも畑。\n土の中で、アイドルたちに収穫されるのを待っている。\nよいしょ、よいしょ。どんなさつまいもが出てくるかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5656, /*level*/1, /*item_detail*/"穫れたてのさつまいもを満載にした手押し車。\n山積みにして上手に運ぶには、\nコツと経験と、そして心に山積みの愛が必要だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5657, /*level*/1, /*item_detail*/"いも掘り体験の宣伝をしているのぼり。\n遠くからでもよく目立つ。\nわんぱくも、乙女も、みんな集まれ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5658, /*level*/1, /*item_detail*/"さつまいもが育つ、養分いっぱいの土壌。\n土をお世話し、土と語りあおう。\n土はきっとみんなの願いに応えてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5659, /*level*/1, /*item_detail*/"秋色に染まった、さつまいも農園の風景。\nみんなの心のふるさとだ。\n温かい笑顔と、たまらない秋の味覚が出迎えてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5660, /*level*/1, /*item_detail*/"穫れたてのさつまいもを満載にした、緑色の手押し車。\nたくさん運んで収穫のお手伝い。\n終わったあとに食べるさつまいもは、きっと絶品だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5661, /*level*/1, /*item_detail*/"いも掘り体験の宣伝をしている、さつまいも色ののぼり。\n焼き芋、サラダ、揚げ物…さつまいもの可能性は無限大。\nただし、美味しいからって食べ過ぎには注意しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5670, /*level*/1, /*item_detail*/"Twinkle LIVE 公式Tシャツの衣装スタンド。\nTwinkle LIVE「Constellation Gradation」のTシャツが\n飾られている。星々が手を取り、輝くステージが始まる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5680, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したスクリーン。\nキラキラと輝く水のカーテンに浮かび上がるのは、\nももクロを象徴するクローバーのヴィジョン。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5681, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したスクリーン。\nきらめく水のカーテンにペガサスマークが映し出される。\nももクロスクリーンとセットで夢の共演を演出しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5682, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n夢を追うアイドルたちの日々は、いつもきらめいている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5683, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\nひとつの夢を叶えたアイドルたちは、また新たな夢へと。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5684, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。永遠に輝く想いと歩む、彼女たちに祝福を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5690, /*level*/1, /*item_detail*/"フルーティーな水が飲めるドリンクサーバー。\nカラフルな果物は、見ているだけでも癒されそう。\n自然な酸味とほのかな甘さに、心も体もリラックス♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5700, /*level*/1, /*item_detail*/"プラスチックの青いベンチ。\nそっと腰を下ろせば、ひんやりと少しだけ冷たい。\nでも星を見上げているうちに、すぐにあったまるはず。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000107, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5701, /*level*/1, /*item_detail*/"屋上への出入口がある建物。\n梯子を使って、その上へのぼろう。\nほんのわずかだけど星に近づけたみたい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5702, /*level*/1, /*item_detail*/"天体観測を楽しむためのブルーシート。\n星座を知るためのアイテムが揃っているから、\n詳しくなくても大丈夫。楽しむ心を持って集まろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5703, /*level*/1, /*item_detail*/"星のオーナメントが飾られたクリスマスツリー。\nたくさんの星と星を繋いで飾りつけ。\nてっぺんには、ひときわ目を引く大きな星を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5704, /*level*/1, /*item_detail*/"ベーシックな天体望遠鏡。\n少しずつピントを合わせて、準備万全。\nどこか遠くの天体が、目の前で鮮やかな光を放つ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5705, /*level*/1, /*item_detail*/"紫色のパンジーが植えられたプランターボックス。\n冬の寒さにも負けず、すくすく育って咲き誇る。\n見ているだけで、元気をいっぱいもらえそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5706, /*level*/1, /*item_detail*/"学校の屋上をイメージした緑色のフェンス。\nどこか懐かしい背の高いフェンスが、\nいつでもみんなの安全を守ってくれている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5707, /*level*/1, /*item_detail*/"学校の屋上をイメージした緑色の床。\nどこからかチャイムの音が聴こえてきそう。\n青春とロマンを感じる世界が、そこに広がる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5708, /*level*/1, /*item_detail*/"無数の星が瞬く夜空。\n運がよければ、流れ星だって見られるかもしれない。\n満天の星を見上げて、今夜はみんなで語り合おう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5709, /*level*/1, /*item_detail*/"屋上への出入口がある建物。\n色が変わっても、やっぱりのぼりたくなってしまう。\n少しでも近くで、星々の輝きを見るために。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5710, /*level*/1, /*item_detail*/"青い天体望遠鏡。\n少しだけ性能がアップ。さらなるロマンに満ちた\n星空の世界へ、みんなを連れて行ってくれる…かも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5720, /*level*/1, /*item_detail*/"ブリッツェンを模した大きなクッション。\n再現度はイヴと本トナカイのお墨付きらしい。\nふわふわのお腹に寝転んだら、いい夢が見られるかも？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5730, /*level*/1, /*item_detail*/"イベント「チョコレート？レモネード？どっち？？」で\n獲得したキッチンカー。甘くて苦いチョコと、甘酸っぱい\nレモネード。恋と青春の味を、どこへなりともお届け。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5731, /*level*/1, /*item_detail*/"イベント「チョコレート？レモネード？どっち？？」で\n獲得した銅製のトロフィー。イベントで活躍したＰに\n贈られる。少女たちは、苦く甘酸っぱい恋心に寄り添う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5732, /*level*/1, /*item_detail*/"イベント「チョコレート？レモネード？どっち？？」で\n獲得した白銀のトロフィー。アイドルを輝かせたＰに\n贈られる。高鳴る鼓動、等身大の熱。きっとそれが、恋。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5733, /*level*/1, /*item_detail*/"イベント「チョコレート？レモネード？どっち？？」で\n獲得した純金のトロフィー。アイドルを伝説級のステージ\nに導いたＰに贈られる。選んだ景色はあなただけのもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5740, /*level*/1, /*item_detail*/"初日の出キャンプのために用意したアウトドアセット。\nあたたかいお茶を飲みながら、のんびりと過ごそう。\n待つ時間も、初日の出キャンプの醍醐味だから。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5741, /*level*/1, /*item_detail*/"軽くて丈夫なアウトドア用チェア。\n持ち運びに便利で、座り心地もなかなかのもの。\n気の合う仲間と椅子を並べて、日の出を待とう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1528101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5742, /*level*/1, /*item_detail*/"初日の出キャンプのために用意したアウトドアセット。\nテーブルセットにタープもついた豪華仕様。\nただお茶を飲む時間も、きっと特別な思い出になる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5743, /*level*/1, /*item_detail*/"ひとり用のテント。\nしっかりした造りながら、ひとりでも扱える安心設計。\n中には冬用シュラフがあるので、寒い夜もぐっすり♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5744, /*level*/1, /*item_detail*/"防火シートつきのたき火台。\n薪は着火剤などを使って細いものから火をつけよう。\nみんなで囲んで語らえば、忘れられない夜になるはず。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5745, /*level*/1, /*item_detail*/"たき火に使う薪を一時保管しておくスタンド。\nよく乾いた薪が使われている。\n重たいので持ち運びには気をつけて。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5746, /*level*/1, /*item_detail*/"キャンプ場の入口に設置された案内看板。\n最初に客を出迎えるキャンプ場の顔と言える存在。看板を\n見た客たちは一様に、キャンプへの期待で胸を弾ませる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5747, /*level*/1, /*item_detail*/"兎年仕様の特注門松。\n真っ白な毛並みに、つぶらな瞳のうさぎが飾られている。\n新たな干支とともに、初日の出を見届けよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5748, /*level*/1, /*item_detail*/"キャンプ場内の各サイトを区切る木製の柵。\n年季は感じるものの、丁寧な加工がされている。\n行き届いた管理は、キャンパーたちにも好評。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5749, /*level*/1, /*item_detail*/"キャンプ場の地面を再現した床。\nどこにテントを設置するかは、キミの自由 !\nただし、直火は禁止など、ルールはしっかりと守ろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5750, /*level*/1, /*item_detail*/"初日の出キャンプをテーマにした背景。\n朝靄の中、明るい色を帯び始めた空が印象的。\nかけがえのない仲間たちと、最高の瞬間を。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5751, /*level*/1, /*item_detail*/"ひとり用のテント。\n初級キャンパーから上級キャンパーまで御用達の有名\nメーカー品。豊富なカラーバリエーションも人気の理由。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5752, /*level*/1, /*item_detail*/"兎年仕様の特注門松。\n中央にブラウンの毛並みのうさぎが飾られている。\nうさぎのような軽い足取りで新たな年を駆けて行こう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5760, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した\nポップコーンワゴン。にぎやかな飾りのワゴンを置けば、\nそこはまるで遊園地。弾けるポップコーンを楽しもう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1553001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5761, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n少女たちは遊園地で笑い合い、仲を深めた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5762, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n楽しかった時間は、いつまでも心の中で輝いている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5763, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。感じた喜びを届けるため、歌い続けよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5764, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した七色の\nトロフィー。いくつもの輝きを束ねたＰに贈られる。\n大きな観覧車は、みんなの夢を乗せて回り続ける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5770, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n餅つき大会のセット。おめでたい紅白幕をバックに、\nふたりでナイスコンビネーションな餅つきを楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1553101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5780, /*level*/1, /*item_detail*/"イベント「ハートボイルドウォーズ」で獲得したテーブル\nセット。アタッシュケースの中身はいったい…？\nあなたは目撃するかもしれない。秘密の取引現場を… !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1553301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5781, /*level*/1, /*item_detail*/"イベント「ハートボイルドウォーズ」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n心に弾丸を込め、危険なパーティーに挑め !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5782, /*level*/1, /*item_detail*/"イベント「ハートボイルドウォーズ」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n守りたいものがある。だから、心をひとつに…BANG !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5783, /*level*/1, /*item_detail*/"イベント「ハートボイルドウォーズ」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。仁義に、正義に、勇気に…乾杯 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5790, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した\nガラスの花。永遠に朽ちることのないその花は、\n人々の希望を宿して、気高く美しく咲き誇る。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1553401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5791, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n仲間の心に触れて、少女たちは強さを知っていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5792, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n憧れは深く根を張り、やがて大きな夢を咲かすだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5793, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。さあ紡ごう。お伽噺のような私たちの物語。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5800, /*level*/1, /*item_detail*/"和風カフェのテーブルセット。\n甘いどら焼きとあったかい緑茶で、ほっとひと息。\nリラックスしながら、おしゃべりに花を咲かせよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1553201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5801, /*level*/1, /*item_detail*/"和風カフェのレジカウンター。\nときめくハートの装飾つき。お土産にオススメの\n和菓子をセッティングして…いざ、営業開始♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1512801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5802, /*level*/1, /*item_detail*/"和風カフェのディスプレイテーブル。\nイチオシの和菓子を、お花とハートで可愛く飾りつけ。\nみんなの目を引く、一番目立つ場所に置いちゃおう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5803, /*level*/1, /*item_detail*/"和風カフェのショーケース。\n可愛くて華やかな和菓子に、思わずうっとり。\n味にも見た目にも、こだわりがたくさん詰まっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5804, /*level*/1, /*item_detail*/"和風カフェの陳列台。\n定番品から限定品まで、自慢の和菓子がずらり。\nサンプルで中身をチェックして…これに決めた !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5805, /*level*/1, /*item_detail*/"和風カフェの陳列台。\n小ぶりで可愛らしい和菓子が並んでいる。\nちょっとした贈り物や、食べ比べにもぴったり♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5806, /*level*/1, /*item_detail*/"和風カフェの飾り棚。\n綺麗な菓子折りが、丁寧に飾られている。\nいずれも、包み紙の素材にまでこだわり抜いた逸品だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5807, /*level*/1, /*item_detail*/"和風カフェの壁。\n大きな窓で開放感はバツグン。\n美しい景色を眺めながら、美味しいお茶をいただきます♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5808, /*level*/1, /*item_detail*/"和風カフェの背景。\n竹林に囲まれた石造りの一角は、少しだけ非日常的。\n忙しい毎日を忘れて、ゆったり過ごすことができそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
