INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5830, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した、メタルな崖。\n曲のジャケットでよく見かける、憧れの景色を再現。\n熱い想いを、怒りを、欲望を、魂のままにさらけ出せ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1553601, /*individual_flag*/0);
