INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5840, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したなりきり小道具。\n豪奢な刺繍と漆黒の生地で、上流階級が持つ優雅さと\n悪役令嬢の気の強さを表現。誰がいちばん似合うかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1553701, /*individual_flag*/0);
