INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5871, /*level*/1, /*item_detail*/"フライドポテトを売っている屋台。\nアツアツでサクサクの揚げたてが一番 !\n美味しくて、ペロリと食べられちゃう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1554001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5872, /*level*/1, /*item_detail*/"わたあめを売っている屋台。\n袋には見覚えのあるキャラクターたち。\n中には、夢と希望と優しい甘さがふんわり詰まってる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5873, /*level*/1, /*item_detail*/"お花見用の赤い座敷。\nお花見をたっぷり楽しめる、見晴らしのいい席を確保 !\n屋台グルメを味わいながら、お喋りにも花が咲く。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1553801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5874, /*level*/1, /*item_detail*/"満開の桜の木。\n鮮やかに花開き、見ごろはまさに今。\n美しく咲き誇る姿を、心に焼きつけよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5875, /*level*/1, /*item_detail*/"桜まつりの灯籠。\n桜の模様が浮かび上がり、お祭りムードが高まる。\nみんなの笑顔を柔らかく照らしてくれている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5876, /*level*/1, /*item_detail*/"桜まつりののぼり。\n可愛らしいピンク色が目印。\n春らんまんの楽しいお祭り、始まります !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/5877, /*level*/1, /*item_detail*/"桜まつりの床。\n舞い散る花びらが、ささやかに地面を染める。\n桜の花は、散ってもなお美しい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
