INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6000, /*level*/1, /*item_detail*/"イベント「Nightwear」で獲得したジェットバス。\nここは、オシャレもスイーツも欲張った夢の空間。\n甘い香りに包まれて、友だちとの時間を楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1555601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6001, /*level*/1, /*item_detail*/"イベント「Nightwear」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。退屈なんてさせない。\n世界中に愛を届ける時間は、これから始まる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6002, /*level*/1, /*item_detail*/"イベント「Nightwear」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。仲間と一緒だから、\n熱狂的に魅せられる。アナタを満足させつづける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6003, /*level*/1, /*item_detail*/"イベント「Nightwear」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nどこまでも昇りつめよう。唇の熱は、ずっと引かない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6010, /*level*/1, /*item_detail*/"おとぎの森にある大きな切り株。\n森の仲間たちは、大きなテーブルとして使っている。\n仲良しのみんなで集まって、楽しいお茶会をしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6011, /*level*/1, /*item_detail*/"おとぎの森にある小さな切り株。\n腰掛けて休憩するのにちょうどいいサイズ。\n森に吹く優しい風を感じながら、ほっとひと息。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1515302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6012, /*level*/1, /*item_detail*/"おとぎの森にある丸太のベンチ。\n倒れてから時間が経って、いろんな草花が咲いている。\nときどき迷い込む旅人も、ここで疲れを癒すのかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000107, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6013, /*level*/1, /*item_detail*/"妖精が住んでいるという綺麗な泉。\n神秘的な力でいつも美しい花が咲いている。\n幸運な旅人は、泉の妖精と出会えるかも…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1555501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6014, /*level*/1, /*item_detail*/"妖精が住んでいるという家。\n大きな木をそのまま利用して作られている。\nどんな妖精が住んでいるのかな？ノックしてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1555301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6015, /*level*/1, /*item_detail*/"妖精が住んでいるという家。\n森に生える大きなキノコをくりぬいて作られている。\n妖精と仲良しになれたら訪ねてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1555201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6016, /*level*/1, /*item_detail*/"おとぎの森に生えている大きな木。\n妖精たちだけでなく、鳥や動物たちにとっても憩いの場。\n飾りつけをしたのは妖精たちかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6017, /*level*/1, /*item_detail*/"おとぎの森にある井戸。\nずっと昔に、森に暮らす妖精が掘ったものらしい。\n長い時が経った今も、綺麗な水が湧きつづける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6018, /*level*/1, /*item_detail*/"おとぎの森の地面。\nさまざまな花が咲き誇り、訪れた者の心を癒してくれる。\n寝転んで目を閉じれば、妖精たちの歌声がきこえるかも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6019, /*level*/1, /*item_detail*/"世界のどこかにずっと昔からあるという不思議な森。\n時折迷い込む旅人以外、人が訪れることはほとんどない。\n妖精たちが鳥や動物たちと仲良く暮らしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6020, /*level*/1, /*item_detail*/"おとぎの森にある丸太のベンチ。\n倒れて時間が経っても、白く美しい幹は残りつづけた。\n今では草花が根を張り、小動物も遊び場にしているとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000107, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6021, /*level*/1, /*item_detail*/"妖精が住んでいるという家。\n白い幹の大きな木を利用して作られている。\nここにはどんな妖精が暮らしているのかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1555301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6030, /*level*/1, /*item_detail*/"旅館の看板猫に気に入られた、黄色のキャリーケース。\n新しい旅に出るのは、もう少し後で。\n今は三毛猫の気が済むまで、のんびりと過ごそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1555801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6040, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したやぐら。\nてっぺんには、ゴキゲンな法被のぴにゃこら太。\nピカピカ輝く提灯と一緒に、お祭りをアツく盛り上げる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6041, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。お祭りの夜は、\nまるで夢のよう。キラキラまぶしいひとときを過ごした。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6042, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。夏の魔法にかけられて\n背伸びをしたり甘えたり。キミは知らない顔を覗かせる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6043, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n胸に響くのは祭り囃子と笑い声。夏を越えてどこまでも。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6050, /*level*/1, /*item_detail*/"きらめくサマーライブのミニステージ。\n元気いっぱいに、笑顔を弾けさせて !\nここはゴールじゃなくて、もっと輝くための場所だから。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1555901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6060, /*level*/1, /*item_detail*/"癒しの場所、しんでれらの本日の座敷席。\nつるんとアツアツな水餃子を、醤油ダレで召し上がれ。\n少し気の早いお鍋で、心も体もあたためて♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1555701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6061, /*level*/1, /*item_detail*/"三船美優のぬいぐるみ。\n癒しの場所、しんでれらで出会った。\nお酒を飲んで、自然な笑顔。あなたもきっと酒あわせに。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6070, /*level*/1, /*item_detail*/"燿城夜祭 公式Tシャツの衣装スタンド。\n「燿城夜祭」のTシャツが飾られている。かけがえのない\n一瞬を、ずっと残る思い出を。燿く祭りの夜を始めよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6080, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\nウェディングブーケ。持つと幸せな甘い香りが広がる。\n心からの笑顔が咲かせた花束で、素敵な時間を彩ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1556001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6090, /*level*/1, /*item_detail*/"不思議なしゃぼん玉のセット。\nストローからぷくぷく、ふわふわ…。かわいいねこの\nしゃぼん玉が生まれて、おとなもこどもも、みんな夢中 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1556201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6100, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得したかざぐるま。\n色とりどりのかざぐるまが並ぶ姿は花畑のよう。\nくるくる回せば、今でも祭囃子が聞こえる気がする。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1556401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6101, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。きらめく夏の日々が\n過ぎ去っても、思い出はアイドルたちの胸の中に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6102, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。思い出はひとつでは\n終わらない。アイドルたちの熱い夏は始まったばかり。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6103, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nアイドルたちの輝く思い出は、永遠に色褪せない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6110, /*level*/1, /*item_detail*/"夏まつりのやぐら。\nてっぺんには、キラキラな黄金色のぴにゃこら太。\n盛り上がりは最高潮 ! もっともっとアツくなろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6111, /*level*/1, /*item_detail*/"バラエティ豊かなおめん屋さん。\n夏まつり会場限定 !\nキミが選ぶのはどんな顔？こっちむいてポーズ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1556501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6112, /*level*/1, /*item_detail*/"夏まつりの、いか焼きの屋台。\n香ばしいおしょうゆの匂いに、つい引き寄せられそう。\nタコちゃんもいいけど、イカちゃんもね !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1527601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6113, /*level*/1, /*item_detail*/"夢がいっぱいの輪投げ屋さん。\n狙うのはどの景品？\nわっかをどんどん投げれば、つながるチャンスは∞ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1556301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6114, /*level*/1, /*item_detail*/"気になる景品がずらりと並んだ射的屋さん。\nお気に入りに狙いを定めて一撃必中…？\n自慢の腕前を見せつけよう。目指せ、シャテキング !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1544501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6115, /*level*/1, /*item_detail*/"色とりどりの提灯飾り。\nチカチカ光って、\n夏まつりにときめく心を映しているよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6116, /*level*/1, /*item_detail*/"提灯が吊るされた門。\nキラキラと燿くその向こうで、\n祭囃子が呼んでいる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6117, /*level*/1, /*item_detail*/"夏まつり会場に、ずらりと吊り下げられた提灯。\nおまつり気分に境界はない。\nいつまでも、どこまでも踊り明かそう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6118, /*level*/1, /*item_detail*/"夏まつり会場の地面。\nいっぱい踊ったアイドルたちの、\n足跡が残っているとかいないとか…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6119, /*level*/1, /*item_detail*/"夏まつり会場の夜。\nあちらにもこちらにも、ひと夏の物語があった。\nアイドルたちの思い出を、花火が空から照らしている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6120, /*level*/1, /*item_detail*/"夏まつりの、焼きとうもろこしの屋台。\n甘くてしょっぱい青春の味。\nおいしそうに食べるキミの笑顔を、ずっと眺めていたい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1527601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6121, /*level*/1, /*item_detail*/"紅白提灯が吊るされた門。\nこの夏も、アイドルたちに幸せが訪れるだろう。\n祭囃子はいつまでも終わらない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6130, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得したフィルム映写機。\nセットになっている大きなスクリーンで、\n友だちと一緒に上映会を楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1556601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6131, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。ハッピーエンドは\n約束されていない。だからこそ、この手で紡いでいこう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6132, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。一歩を踏み出した\n先に見えるのは、美しく眩い、新たな景色。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6133, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n少女たちがともに過ごす日々は、もどかしくも愛おしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6140, /*level*/1, /*item_detail*/"きらめくサマーライブのモニター。\n元気いっぱいの笑顔を、もっとみんなへ !\n映し出されるのは、キラキラ輝く無限の可能性。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6150, /*level*/1, /*item_detail*/"癒しの場所、しんでれらの本日の座敷席。\nこごみ、アスパラ、ふきのとう。旬の春野菜に舌鼓。\nサクサクの衣と一緒に、春の香りを召し上がれ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1554401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6151, /*level*/1, /*item_detail*/"柊志乃のぬいぐるみ。\n癒しの場所、しんでれらで出会った。\n居酒屋メニューによくあうワイン、教えてくれるかな？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6160, /*level*/1, /*item_detail*/"幻想的な雰囲気が楽しめる仕掛けの座敷。\n行燈をつけると水中をたゆたう金魚の映像が投影される。\n涼しげで風情あるひと夏を演出しよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1557001, /*individual_flag*/1);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6170, /*level*/1, /*item_detail*/"イベント「Night Time Wander」で獲得したステッキ。\nルビーの深紅の輝きが、多くの人々を魅了する。\n自分がここに在ることを、力強く華麗に示してみせて。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1556901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6171, /*level*/1, /*item_detail*/"イベント「Night Time Wander」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n競い合い、認め合いながら、より高みを目指した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6172, /*level*/1, /*item_detail*/"イベント「Night Time Wander」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n三者三様の輝きがひとつとなり、鮮やかな光を放つ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6173, /*level*/1, /*item_detail*/"イベント「Night Time Wander」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。少女たちは誇り高く、美しく輝きつづける。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6180, /*level*/1, /*item_detail*/"ひとり用のテント。\nおしゃれなデザインとお値段以上の機能を併せ持つと\n評判のロングセラー。忘れられない思い出を作ろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1552901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6181, /*level*/1, /*item_detail*/"イエローカラーが眩しいハンモック。自立式で周囲に木が\nなくても気軽に設置できる。散策したあとの休憩によし、\n食後のお昼寝によし。気持ちよくて寝すぎないようにね♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1506401, /*individual_flag*/0);
