INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6191, /*level*/1, /*item_detail*/"橘ありすと市原仁奈と赤城みりあのポスター。\nTVアニメ「U149」BD1巻を購入した人が貰える一枚。\n可愛く、元気に、輝いて、3人はステージへ飛び出す。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6192, /*level*/1, /*item_detail*/"櫻井桃華と的場梨沙と結城晴のポスター。\nTVアニメ「U149」BD2巻を購入した人が貰える一枚。\n優雅に、おませに、キメて、3人はステージへ飛び出す。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6193, /*level*/1, /*item_detail*/"古賀小春と龍崎薫と佐々木千枝のポスター。\nTVアニメ「U149」BD3巻を購入した人が貰える一枚。\n夢を見て、明るく、憧れて、3人はステージへ飛び出す。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
