INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6292, /*level*/1, /*item_detail*/"ラブファクトリーのベルトコンベア。\n色とりどりの大きなハートが流れてくる。\nお互いの好きを重ねれば、ラブはどんどん大きくなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
