INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6294, /*level*/1, /*item_detail*/"ラブファクトリーのハートの梱包機。\nハートを梱包した段ボールが流れてくる。\nそれは、アイドルたちからのとっておきのプレゼント。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6295, /*level*/1, /*item_detail*/"ラブファクトリーのハート加工機。\nフィラメントの瞬きはハートが高鳴っている証。\nどんなふうに加工されているのかは…企業秘密です♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
