INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6342, /*level*/1, /*item_detail*/"セレクトショップ・プリヴェのハンガースタンド。\nシックな洋服が並んでいる。\n大人っぽく魅せて、みんなを振り向かせちゃおう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6343, /*level*/1, /*item_detail*/"衣装スタンド/リボンブラウスデート。\n甘く愛らしいお出かけコーデ。\n大切な人と出かけるときは、とびっきり可愛い自分で♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6344, /*level*/1, /*item_detail*/"衣装スタンド/オトナのアーバンカジュアル。\nスタイリッシュなジャケットコーデ。\nたくさんの人が行き交う街を、胸を張って歩こう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6345, /*level*/1, /*item_detail*/"衣装スタンド/スキあり☆スウェットワンピ。\nスキだらけのゆるかわコーデ。\nさらっと着こなして、不意打ちでドキッとさせちゃって☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6346, /*level*/1, /*item_detail*/"衣装スタンド/ドリーミーリボンサロペット。\nパステルカラーが可愛いガーリーコーデ。\n女の子の憧れを身にまとったら、早速お出かけしよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6347, /*level*/1, /*item_detail*/"衣装スタンド/清純派きれいめフリルブラウス。\n誰もが清純派になれるフェミニンコーデ。\n爽やかな印象とさりげない仕草で、ドキドキさせちゃお♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6348, /*level*/1, /*item_detail*/"衣装スタンド/淑女の辛口ハイブラコーデ。\nデキる女たちが着ている上品コーデ。\n纏うのは、完璧な女になる自分の未来。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6349, /*level*/1, /*item_detail*/"衣装スタンド/ゴシックワンピ・夜薔薇姫の誘い。\n闇に棲まう姫に相応しいゴシックコーデ。\n穢れなき憧れは、少女を美しい闇の世界へと誘う。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6350, /*level*/1, /*item_detail*/"衣装スタンド/ガーリィ・ドーリィ・ロリィタ。\n着たらお人形さん気分になれるロリィタコーデ。\n愛くるしい姿で、みんなを夢中にさせちゃおう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6351, /*level*/1, /*item_detail*/"衣装スタンド/肌見せカットアウト＆デニム。\nほんの少しの肌見せがクールなストリートコーデ。\n身も心もアクティブに、街へと飛び出そう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6352, /*level*/1, /*item_detail*/"衣装スタンド/麗しのツイードワンピース。\n高貴な雰囲気が人々を惹きつけるコーデ。\n麗しく、優雅に、街中の視線を独り占めいたしましょう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6353, /*level*/1, /*item_detail*/"衣装スタンド/一緒に過ごす休日パンツスタイル。\nきれいめだけど親しみやすいお出かけコーデ。\n街でも公園でも、好きなところへ大切な人と出かけよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6354, /*level*/1, /*item_detail*/"衣装スタンド/がんばろ ! フィットネスウェア。\n動きやすさとオシャレを両立した運動着。\n健康的で美しい体を目指して、トレーニングに励もう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6355, /*level*/1, /*item_detail*/"衣装スタンド/自分史上最高ワンピ。\n自分史上最高を更新するためのワンピース。\n胸を張って歩こう。いま一番の輝きを見せつけながら。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6356, /*level*/1, /*item_detail*/"衣装スタンド/なかよしチャイルドスモック。\nゆったりふんわり、純真な心になれるコーデ。\nおててをふって、げんきいっぱいおうたをうたおうね !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6357, /*level*/1, /*item_detail*/"衣装スタンド/ひらひら金魚のミニ浴衣。\n短い丈がかわいい、はじける夏の浴衣コーデ。\n今も鮮明に思い起こせる、祭囃子と笑い声。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6360, /*level*/1, /*item_detail*/"Shout out Live ! ! ! 公式Tシャツの衣装スタンド。\n「Shout out Live ! ! ! 」のTシャツが飾られている。\n心の底から愛を叫ぼう。滾る思いを、届けるために !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6370, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した、ふわふわの\nおしゃべりスペース。ふたりで座って仲良く話そう。\n何を話しているかは…乙女たちの秘密です♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6371, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。アイドルたちは、\nたくさんのチャレンジを通して恋のドキドキを体験した。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6372, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。ドキドキは大事な\nスパイス。恋を、人生を、アイドルたちを彩るのだから。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6373, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n恋のようなドキドキを、これからもみんなに届けよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6380, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n不思議な少女の傘。ふわふわ浮いて、宙に足場を作る。\nアン・ドゥ・トロワ…空で一緒に踊りましょう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6390, /*level*/1, /*item_detail*/"黒と白を基調に揃えられたテーブルセット。\n厳かな雰囲気の中に得体のしれない不気味さが漂う。\nさぁ、席について？ドキドキのディナーをどうぞ…♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6391, /*level*/1, /*item_detail*/"不気味な洋館に置かれているローテーブル。\n近くにいると誰かに見られている気がしてくる。\n読書もティータイムも…イタズラ好きのあの子と、ね？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6392, /*level*/1, /*item_detail*/"謎の洋館に置かれている白いソファー。\n細部に美しい意匠が施されており高級感を感じさせる。\n一度座ったなら、アナタはこの館の新たな主。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6393, /*level*/1, /*item_detail*/"洋館の片隅に放置されていた古めかしいピアノ。\n上にかけられた布は朽ちかけており、蜘蛛の巣も見える。\n夜な夜な音が聞こえるという噂は、本当だろうか？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6394, /*level*/1, /*item_detail*/"洋館にある美しいつくりの暖炉。\nしばらく火は焚かれていないようだ。\n使えるのか気になったら…ほら、近くに来て、確かめて。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6395, /*level*/1, /*item_detail*/"不気味な雰囲気漂う館に置かれている調度品。\n枝にとまったカラスの瞳は、どこかを凝視している。\n見つめられているのはアナタ？それとも…。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6396, /*level*/1, /*item_detail*/"ジャックオーランタンを模した置物。\n人が座れる作りになっている。館の探索に疲れたら、\nどうぞ休んで。時間も気にせず、ゆっくりと…♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1502101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6397, /*level*/1, /*item_detail*/"魔女を思わせる帽子をかぶった黒猫のぬいぐるみ。\n誰もいない館で見つけたのに、埃ひとつ被っていない。\nきっと、館の主に大切にされていた「オキニイリ」だ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1501301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6398, /*level*/1, /*item_detail*/"館の壁に飾ってある絵画。カラスや黒猫、蜘蛛の巣など、\n絵画としてはあまり見ないモチーフのものばかり。\nこの館の主は、よほどの変わり者なのだろうか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6399, /*level*/1, /*item_detail*/"白を基調とした館の壁。\n古い洋館ではあるものの、壁は朽ち果ててはいない。\nまるで美しさを留める魔法をかけられているようだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6400, /*level*/1, /*item_detail*/"闇夜に浮かぶ巨大な月が照らす謎の洋館。\n昼間は主なき廃墟に見えるが、夜になると様々な怪現象が\n起き、この世ならざる者たちの宴が始まるという。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6401, /*level*/1, /*item_detail*/"謎の洋館に置かれている黒いソファー。\n上質でしっかりしたつくり。優雅な気分でひと息つこう。\n這い上がってくる寒気には、気づかないふりをして。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6402, /*level*/1, /*item_detail*/"黒を基調とした館の壁。\n不気味さすら感じる落ち着いた色。館の主はどんな人\nだろう。考え始めたら、アナタはもう魅入られている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6410, /*level*/1, /*item_detail*/"イベント「HALLOWEEN GAME」で獲得した\nバッターボックス。ほうきで思いっきり\nかっとばせ ! 悪霊をも魅了するホームラン ! !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6411, /*level*/1, /*item_detail*/"イベント「HALLOWEEN GAME」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。\n9人の少女たちは、果敢にも美しくグラウンドで輝いた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6412, /*level*/1, /*item_detail*/"イベント「HALLOWEEN GAME」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。\n打ち放たれた球は闇夜を晴らし、人々に希望を与えた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6413, /*level*/1, /*item_detail*/"イベント「HALLOWEEN GAME」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。少女の活躍と、始まりの一戦は伝説となった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6420, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得したステンドグラス。\n月明かりを受けて美しく輝く。眩い光に焦がれる\n少女たちの姿を、乙女は静かに見守っている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6421, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。ふたりの少女は\n手を取り合い、互いの本心を曝け出しあった。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6422, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。確かな渇望は、美しく\n咲く少女の心に常にある。そう、互いに知っていた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6423, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n黒白の百合が持つ毒は、永遠に人々を捉えて離さない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6430, /*level*/1, /*item_detail*/"美味しい練り切りが食べられる足湯カフェのテーブル。\n座ってほっこり、食べてまったり。\n心も体も色づく、あたたかい秋を楽しんで…♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6440, /*level*/1, /*item_detail*/"イベント「この恋の解を答えなさい」で獲得した学校の\n下駄箱。登下校で見る日常の風景は、時に恋の舞台へと\n変わる。ファーストステップは、想いを伝える練習から !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6441, /*level*/1, /*item_detail*/"イベント「この恋の解を答えなさい」で獲得した銅製の\nトロフィー。少女たちは、恋のドリルを解き始めた。\n恋について、自分なりの解を見つけるために。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6442, /*level*/1, /*item_detail*/"イベント「この恋の解を答えなさい」で獲得した白銀の\nトロフィー。少女たちは、自身の言葉でセリフを紡ぐ。\n大好きなあなたへ、恋する気持ちを伝えるために。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6443, /*level*/1, /*item_detail*/"イベント「この恋の解を答えなさい」で獲得した純金の\nトロフィー。少女たちは、恋の参考書をあなたに手渡す。\n欲しい解はひとつだけ。さぁ、「イエス」と答えなさい !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6450, /*level*/1, /*item_detail*/"金木犀カフェのテーブルセット。\nお気に入りの紅茶を飲みながら、芳醇なひとときを。\nおしゃべりにも、香り高い花が咲く。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6451, /*level*/1, /*item_detail*/"金木犀カフェのカウンター。\n可愛らしいドライフラワーが飾られている。\nカウンター越しに漂うのは、おもてなしの香り。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6452, /*level*/1, /*item_detail*/"金木犀カフェのカウンター。花の香りもふわり。\n本日の紅茶をお出しいたしました。\n初恋のフレーバー、どうぞ冷めないうちに…♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1558801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6453, /*level*/1, /*item_detail*/"金木犀カフェのカウンターチェア。\nその色は金木犀の花を思わせる。\n小さく可憐だが、強度は抜群 !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1509802, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6454, /*level*/1, /*item_detail*/"金木犀カフェの売り物が並ぶ商品棚。\n茶葉だけでなく、ジャムやコスメ、フレグランスも。\n甘いノスタルジックな香りをお持ち帰りください。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6455, /*level*/1, /*item_detail*/"金木犀カフェの紅茶棚。\nご注文の品を、こちらからお出しいたします。\nやすらぎの時間を彩る紅茶、ゆっくりとお選びください。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6456, /*level*/1, /*item_detail*/"金木犀カフェのキッチン。\nティーポットにお湯を注げば、甘い誘惑の香りが漂う…？\nお仕事の合間にも、ないしょのリラックスタイム♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6457, /*level*/1, /*item_detail*/"金木犀の花が咲く植木。\n小さくても、豊かな香りを届けてくれる。\n日当たりのよい場所に置いてあげよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6458, /*level*/1, /*item_detail*/"金木犀の花がいっぱいに咲く、大きな植木。\nみんなに愛でられて、あっという間に大きくなりました。\n秋の陽射しを受けて、キラキラと輝いている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6459, /*level*/1, /*item_detail*/"金木犀の花柄の壁紙。\n大好きな色と模様に囲まれれば、\n大好きな季節がいつでもそばに。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6460, /*level*/1, /*item_detail*/"金木犀カフェの床。\nたくさんのお客さんと、店員さんが歩き回る。\n響いてくるのは、賑やかな秋の足音。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6461, /*level*/1, /*item_detail*/"金木犀が咲き誇るガーデンカフェ。\n秋の風が、甘くやわらかい香りを運んでくる。\nあなたが夢見たやすらぎの場所、開店です。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6462, /*level*/1, /*item_detail*/"金木犀カフェの、色違いの紅茶棚。\nシックなフレーバーがお好みでしょうか？\n深みの増した味わいを、どうぞ目でもお楽しみください。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6463, /*level*/1, /*item_detail*/"銀木犀の花がしとやかに咲く、大きな植木。\n金と銀、探していたのはどちらの輝き？\n清楚な色と、奥ゆかしい香りで、きっとあなたも虜に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6470, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した、大きなスマホ型の\nフォトスポット。つながった記念に、パシャリと一枚 !\nふたりで思い出も笑顔もシェアしよう♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6471, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。宝物を探して、\n少女たちはふとした日常をカメラで切り抜いていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6472, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。誰かとつながりたい。\nそんな心のひみつも、いまの自分を作る大切なきっかけ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6473, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\nつながろう。素敵な甘さちょい足しの、大好きな自分で !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6480, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n魔法のプレゼント箱。マジカルステッキを振って、\n呪文を唱えて。キラキラの、素敵なクリスマスになるよ♪", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6490, /*level*/1, /*item_detail*/"日本庭園の池。雪が積もっている。\n雪降る中で和傘をさせば、薫るのは古き良き日本の心。\n寒さを忘れて、つい景色に浸りたくなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6491, /*level*/1, /*item_detail*/"燃えるような色合いが美しい、鮮やかな赤の和傘。\nその色が表すのは、快活さか、胸に秘めた情熱か。\n思い思いに傘をさして、華やかな時間を過ごそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6492, /*level*/1, /*item_detail*/"雪吊りが施された、大きな松の木。\n枝が折れないように、一本一本丁寧に吊られている。\n職人さんの真心と技術のたまもの。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6493, /*level*/1, /*item_detail*/"竹囲いが施された小さな木。\n雪から枝を守るため、丁寧に竹で囲われている。\n冬を耐え忍ぶ姿は、いじらしくも可愛らしい。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6494, /*level*/1, /*item_detail*/"赤い花弁が美しい、雪椿の木。\n白い雪とのコントラストが、思わず目を惹く。\nこの木の前で写真を撮ると、いい一枚になるという噂。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6495, /*level*/1, /*item_detail*/"雪が積もった石灯籠。\nどっしりとした佇まいが落ち着いた風情を感じさせる。\nたまには足を止め、柔らかな灯りに想いを馳せてみよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6496, /*level*/1, /*item_detail*/"竹を結わいて作られた柵。\nほんの少し褪せた色合いが味わい深い。\n簡易的でも、何かを囲うには十分な強度をもっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6497, /*level*/1, /*item_detail*/"竹を結わいて作られた、長い柵。\n雪の中でも埋もれることがないよう、\nひとつひとつ丁寧に、まっすぐ立てられている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6498, /*level*/1, /*item_detail*/"冬の日本庭園。\n辺り一面は白銀に染まり、空からは雪の華が舞い落ちる。\n心休まる穏やかな時間を、アイドルたちと共に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6499, /*level*/1, /*item_detail*/"風情ある文様が美しい、青の和傘。\n肩の上でくるりと回せば、気分は麗しの大和撫子。\n思い思いに傘をさして、落ち着いた時間を過ごそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1504401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6500, /*level*/1, /*item_detail*/"桃色の花弁が愛らしい、雪椿の木。\n寒い冬でも、大きな花を咲かせてくれる。\nその力強くも健気な姿は、毎日見守っていたくなる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6510, /*level*/1, /*item_detail*/"クリスマスカラーの蒸気機関車。\nステップに乗ったら、出発進行 ! 白煙を上げる機関車で、\nみんなにプレゼントを届けにいこう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559601, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6520, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得したダンボール衣装\n製作スペース。あってよかったダンボール。\n寒いときには、すっぽりくるまって暖をとろう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559701, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6521, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。たくさんの人々に\n歌と想いを届け、ぽかぽかと温かな交流ができた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6522, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。少女たちはプレゼント\nを贈りつづける。みんなの幸せな笑顔が見たいから。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6523, /*level*/1, /*item_detail*/"イベント「LIVE Infinity」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n冬の日の特別な思い出が、いつまでも優しく心を温める。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6530, /*level*/1, /*item_detail*/"緑色の辰をあしらった門松。\n厄を除けてくれそうな辰が、可愛らしく顔を出している。\nこれを飾って、みんなで辰年をお祝いしよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6531, /*level*/1, /*item_detail*/"赤色の辰をあしらった門松。\n福を集めてくれそうな赤い辰がよく目立つ。\nよい一年になるように、みんなでお願いしておこう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6540, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得したミニステージ。\nどのユニットも、唯一無二の輝きを持っている。\nそんな自信が、ステージに立つ表情からあふれてる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6541, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した銅製の\nトロフィー。イベントで活躍したＰに贈られる。たくさん\nのユニットの力で、3人はゴールまでの道を駆け抜けた。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6542, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した白銀の\nトロフィー。アイドルを輝かせたＰに贈られる。支え合い\n競い合う。それがアイドルで、ユニットだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6543, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した純金の\nトロフィー。アイドルを伝説級のステージに導いたＰに\n贈られる。みんなで想いを繋ぐ旅はここから始まる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6544, /*level*/1, /*item_detail*/"イベント「LIVE Carnival」で獲得した七色の\nトロフィー。いくつもの輝きを束ねたＰに贈られる。\nさぁ、会いに行こう。応援してくれる、みんなのもとへ !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001402, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6550, /*level*/1, /*item_detail*/"劇場の大階段。\n広く長い階段は、華やかな世界の象徴にして入り口。\n一番上でポーズを決めれば、きっと君も大スター☆", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1559801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6551, /*level*/1, /*item_detail*/"劇場ロビーのチケットカウンター。\n深紅の装飾が落ち着きと気品を感じさせる。\nここでチケットを買うと、少し大人になった気分。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6552, /*level*/1, /*item_detail*/"公演内容がわかる看板。\nその日行われる演目が描かれ、掲示されている。\n開演前は期待を高め、終演後は感動を蘇らせる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/2005, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6560, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n座敷。此処に棲まうのは、妖艶な魅力を纏う女郎蜘蛛。\nその視線に絡めとられたら、誰も逃れることはできない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1560201, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6580, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した人形劇セット。\n世にも不思議なおとぎ話のはじまり、はじまり。\n空想のようなセットの中で、操るのは人形？それとも…？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1560401, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6581, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。不思議な物語の世界を\n冒険しよう。それは誰かの人生そのものかもしれない。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6582, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。物語の向こうにある\n素敵な出会いは、心にいっぱいのキラキラをくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6583, /*level*/1, /*item_detail*/"イベント「LIVE Groove」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n旅立とう。たくさんの仲間と、筋書きのない広い世界へ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6590, /*level*/1, /*item_detail*/"UNIT LIVE TOUR「ConnecTrip ! 」山形公演の\n公式タオル。デア・アウローラのユニットロゴ入り。\n夜明けに似た一面の光を、あなたもどうか忘れないで。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6591, /*level*/1, /*item_detail*/"UNIT LIVE TOUR「ConnecTrip ! 」山形公演の\n公式タオル。レイジー・レイジーのユニットロゴ入り。\n壁に掛けて、考え続けて。この日のこと。ふたりのこと。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6592, /*level*/1, /*item_detail*/"UNIT LIVE TOUR「ConnecTrip ! 」山形公演の\n公式タオル。ルビーカウンテスのユニットロゴ入り。\n夢のようなひとときは、明日もずっと続いていく。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6600, /*level*/1, /*item_detail*/"ジャズバーのテーブルセット。\n座り心地のよいラウンジチェアに身を預けたら、\n揺れるランプの灯を見つめ、大人な時間を楽しもう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1560301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6601, /*level*/1, /*item_detail*/"ジャズバーのバーカウンター。\n少し年季の入ったムーディーなカウンター席は、\nひとり物思いにふけるのに、うってつけの場所。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1545301, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6602, /*level*/1, /*item_detail*/"黒いスタンドマイク。\n自分の好きな歌をしっとりと歌い上げよう。\nマイク一本さえあれば、そこが歌い手のステージだ。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1560101, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6610, /*level*/1, /*item_detail*/"イベント「ススメ ! シンデレラロード」で獲得した\n学校机。憧れの人の机に、こっそりチョコを入れよう。\nとろける甘さが、きっとあなたの想いを伝えてくれる。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1560901, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6620, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した天文台。\n天体望遠鏡が捉えるのは、ロマンに満ちた星空。\n広がる夜空の中から、お気に入りの星を見つけよう !", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1561001, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6621, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した銅製のトロフィー。\nイベントで活躍したＰに贈られる。互いの心のうちを\n見せあった少女たちの輝きは、星空にも負けないだろう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000902, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6622, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した白銀のトロフィー。\nアイドルを輝かせたＰに贈られる。少女たちの歌は星空に\n響き渡る。数百年先の光にも届け、そんな祈りと共に。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001003, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6623, /*level*/1, /*item_detail*/"イベント「LIVE Parade」で獲得した純金のトロフィー。\nアイドルを伝説級のステージに導いたＰに贈られる。\n星々のように輝くアイドルの日々は、永遠に記される。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1001102, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6630, /*level*/1, /*item_detail*/"すいせいをイメージした三日月チェア。\n星の歌姫になりきって、MVの再現が映える。\n座り心地も抜群で、くつろぎのスペースにもぴったり。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1560501, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6631, /*level*/1, /*item_detail*/"すいせいの専用マイクが取り付けられたスタンド。\nあしらわれた輝きは、絆の証。\nたくさんの想いを胸に、歌声を宇宙に響かせよう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1560801, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6632, /*level*/1, /*item_detail*/"りんごジュースの入った冷蔵庫。\n声を使うお仕事のときは、喉に良い気がするりんご !\n収録や配信のおともにいかが？", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1560701, /*individual_flag*/1);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6633, /*level*/1, /*item_detail*/"ぎらりと光る、そこそこ使い込まれた斧。\n戦うメイドはもちろん斧を持ちます。\n柄を握れば、自然と笑みが溢れてくるとか。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/0, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6634, /*level*/1, /*item_detail*/"衣装スタンド/星街すいせい。\nすいせいのトレードマークである衣装。\n本人とデザイナーのこだわりが細部にまで詰まっている。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1000302, /*individual_flag*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/6635, /*level*/1, /*item_detail*/"星街すいせいのぬいぐるみ。\nその可愛さは、販売後即完売したほどだとか。\n抱き上げれば、すいちゃんの元気な声が聞こえてきそう。", /*effect_value_1*/0, /*effect_value_2*/0, /*effect_value_3*/0, /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_item2_type*/0, /*lvup_item2_id*/0, /*lvup_item2_num*/0, /*lvup_time*/0, /*animation_id*/1533001, /*individual_flag*/0);
