INSERT INTO `sector3d_location` VALUES (/*id*/10000001, /*name*/"事務所", /*description*/"事務所。\nお仕事の相談をしたり、雑談をしたり。\n思い思いに集まったアイドルたちが、\n日々を楽しく過ごす場所。", /*sort*/6, /*new_flag*/0, /*idol_num*/5, /*thumbnail_id*/10000001, /*itemset_id*/1, /*default_open*/1, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2030/04/01 14:00:00");
INSERT INTO `sector3d_location` VALUES (/*id*/20000001, /*name*/"スイート・リビングルーム", /*description*/"愛を感じるリビングルーム。\nリビングにちりばめられたピンク色は、\nあの人への想いの表れ。\n一緒に、ドキドキのひとときを過ごそう♪", /*sort*/4, /*new_flag*/0, /*idol_num*/5, /*thumbnail_id*/20000001, /*itemset_id*/2, /*default_open*/0, /*start_date*/"2024/02/28 15:00:00", /*end_date*/"2030/04/01 14:00:00");
INSERT INTO `sector3d_location` VALUES (/*id*/20000002, /*name*/"洋館中庭・少女たちのお茶会", /*description*/"お茶会が開かれている、洋館の中庭。\n温かみのある洋館の美しさと、\n緑と青空の爽やかな華やかさが、\n少女たちの笑顔を彩ってくれる。", /*sort*/5, /*new_flag*/0, /*idol_num*/5, /*thumbnail_id*/20000002, /*itemset_id*/5, /*default_open*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2030/04/01 14:00:00");
INSERT INTO `sector3d_location` VALUES (/*id*/20000003, /*name*/"ドレミファ♪プレイルーム", /*description*/"カラフルな遊び心でいっぱいのプレイルーム。\nここじゃ大人も子どもも関係ない。\n目いっぱい遊んで、はしゃいじゃおう !\nお片づけは忘れずに !", /*sort*/3, /*new_flag*/0, /*idol_num*/5, /*thumbnail_id*/20000003, /*itemset_id*/3, /*default_open*/0, /*start_date*/"2024/05/30 15:00:00", /*end_date*/"2030/04/01 14:00:00");
INSERT INTO `sector3d_location` VALUES (/*id*/20000004, /*name*/"トコナツ☆ビーチ", /*description*/"青空に包まれた、常夏のビーチ。\n砂浜で、太陽に負けず大はしゃぎ？\n海の家で、飲み物片手にのんびりまったり？\nどっちも欲張っちゃおう ! 乗っちゃえWave♪", /*sort*/2, /*new_flag*/0, /*idol_num*/5, /*thumbnail_id*/20000004, /*itemset_id*/4, /*default_open*/0, /*start_date*/"2024/08/30 15:00:00", /*end_date*/"2030/04/01 14:00:00");
INSERT INTO `sector3d_location` VALUES (/*id*/20000005, /*name*/"CHRISTMAS WINDOW", /*description*/"窓のむこうに冬を感じる、リビングルーム。\nクリスマスの準備はバッチリ♪\n暖炉と煙突はもちろん、我らがサンタのため。\nあたたかな部屋のなかで、憩いのひとときを。", /*sort*/1, /*new_flag*/0, /*idol_num*/5, /*thumbnail_id*/20000005, /*itemset_id*/6, /*default_open*/0, /*start_date*/"2024/12/19 15:00:00", /*end_date*/"2030/04/01 14:00:00");
