INSERT INTO `sector3d_room_item` VALUES (/*id*/10000001, /*name*/"リビングテーブル", /*description*/"大きなテーブルと椅子のセット。\nリビングによく置かれる団らんの証。木の素材感と\n鮮やかなカラーリングで、温もりと楽しさの両方を演出。", /*category*/1, /*type*/1, /*sort*/29, /*flag_shop*/0, /*tile_size_x*/3, /*tile_size_y*/4, /*tile_size_z*/1, /*icon_id*/100001, /*itemset_id*/2, /*personality_anime*/0, /*start_date*/"2024/02/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000002, /*name*/"ラック", /*description*/"観葉植物が置かれた、低めのラック。\n収納力とデザイン性を両立した作りになっている。\n綺麗なお部屋は、こまめな整理整頓から。", /*category*/1, /*type*/1, /*sort*/30, /*flag_shop*/0, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/100002, /*itemset_id*/2, /*personality_anime*/0, /*start_date*/"2024/02/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000003, /*name*/"事務所テーブル", /*description*/"事務所のテーブル。\n木目が美しい落ち着いた色合い。\nシンプルなデザインでどんな場所にも馴染む。", /*category*/1, /*type*/1, /*sort*/1, /*flag_shop*/0, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/100003, /*itemset_id*/1, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000004, /*name*/"事務所ソファ①", /*description*/"事務所の2人掛けソファ。\n座り心地にこだわった一品。\n雑誌や台本を読むのにちょうどいい。", /*category*/1, /*type*/1, /*sort*/2, /*flag_shop*/0, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/100004, /*itemset_id*/1, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000005, /*name*/"事務所ソファ②", /*description*/"事務所の4人掛けソファ。\n高級感のある黒が、部屋を引き締めてくれる。\nゆったり座るうちに、寝てしまいそうになる。", /*category*/1, /*type*/1, /*sort*/3, /*flag_shop*/0, /*tile_size_x*/5, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/100005, /*itemset_id*/1, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000006, /*name*/"事務所キャビネット①", /*description*/"観葉植物が置かれた、事務所のキャビネット。\nふと目を向けたときに、植物に癒される。\n忙しい日々の中に、なにげない優しさを。", /*category*/1, /*type*/1, /*sort*/4, /*flag_shop*/0, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/100006, /*itemset_id*/1, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000007, /*name*/"事務所キャビネット②", /*description*/"事務所のキャビネット。\n書類はもちろん、雑誌やCDなども多いため、\n収納はいくらあっても困らない。", /*category*/1, /*type*/1, /*sort*/5, /*flag_shop*/0, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/100007, /*itemset_id*/1, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000009, /*name*/"ティーセットテーブル＆チェア", /*description*/"ティーセットテーブルとチェア。\n爽やかなテーブルクロスは、青空のよう。\n仲良しで集まって、温かな時間を過ごそう。", /*category*/1, /*type*/1, /*sort*/8, /*flag_shop*/0, /*tile_size_x*/4, /*tile_size_y*/4, /*tile_size_z*/1, /*icon_id*/100009, /*itemset_id*/5, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000010, /*name*/"ハンガーラック", /*description*/"ハンガーラック。\nアイドルにとって、ファッションは命。\n型崩れや余計なしわを抑えるための必需品。", /*category*/1, /*type*/1, /*sort*/14, /*flag_shop*/1, /*tile_size_x*/2, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/100010, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000011, /*name*/"大きな時計", /*description*/"アンティークな柱時計。\nどっしりとした佇まいが頼もしい。\n重厚な年月を演出したいときにオススメ。", /*category*/1, /*type*/1, /*sort*/15, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/2, /*icon_id*/100011, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000012, /*name*/"ハロウィンテーブル", /*description*/"ハロウィン仕様のテーブル。\nジャック・オー・ランタンやお菓子も用意して、\n賑やかなハロウィンの準備はバッチリ。", /*category*/1, /*type*/1, /*sort*/20, /*flag_shop*/1, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/100012, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/10/30 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000013, /*name*/"リビングテーブル＆チェア/ホワイト", /*description*/"ひとり用サイズのテーブルと椅子のセット。\nナチュラルかつシンプルなカラーリングで、\nどんな部屋とも相性抜群♪", /*category*/1, /*type*/1, /*sort*/32, /*flag_shop*/1, /*tile_size_x*/3, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/100013, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/02/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/10000014, /*name*/"本棚", /*description*/"ひきだし付きの、木製の本棚。\n本の隣にぬいぐるみがちょこんと座っている。\n可愛らしさの中に、どことなく生活感も感じられる家具。", /*category*/1, /*type*/1, /*sort*/33, /*flag_shop*/1, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/100014, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/02/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000001, /*name*/"ウッドブランコ", /*description*/"花に彩られたウッドブランコ。\n庭を飾るインテリアとしてもよし。\n少し腰を落ち着かせる場所としてもよし。", /*category*/2, /*type*/1, /*sort*/9, /*flag_shop*/0, /*tile_size_x*/4, /*tile_size_y*/3, /*tile_size_z*/2, /*icon_id*/200001, /*itemset_id*/5, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000002, /*name*/"ダンボール", /*description*/"どこにでもあるダンボール。\n特徴がない分、使い方は無限大。\n使うものの創意工夫が試される。", /*category*/2, /*type*/1, /*sort*/16, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/200002, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000003, /*name*/"バランスボール", /*description*/"バランスボール。\n健康維持の器具から愉快な遊び道具まで。\n日々の生活を思わせる一品。", /*category*/2, /*type*/1, /*sort*/17, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/200003, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000004, /*name*/"樽", /*description*/"雑多に積まれた樽。\n中身は入っていないので、飾りとして。\n独特のニュアンスを空間に与えてくれる。", /*category*/2, /*type*/1, /*sort*/18, /*flag_shop*/1, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/2, /*icon_id*/200004, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000005, /*name*/"魔法使いぴにゃ", /*description*/"ハロウィン仕様のぴにゃこら太。\n魔法使いの衣装でドレスアップしている。\n楽しいハロウィンは、ぴにゃと一緒にね。", /*category*/2, /*type*/1, /*sort*/21, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/200005, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/10/30 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000006, /*name*/"棺桶", /*description*/"思わず目を引く大きな棺桶。\n重厚な作りと美しい装飾が、\n高貴で怪しい空間を演出してくれる。", /*category*/2, /*type*/1, /*sort*/22, /*flag_shop*/1, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200006, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/10/30 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000007, /*name*/"ロッカー", /*description*/"大きなロッカー。\n学校の教室や部室など、多くの場所に\nひっそりとたたずむ、おなじみの存在。", /*category*/2, /*type*/1, /*sort*/23, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/200007, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/11/29 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000008, /*name*/"キャリーケース", /*description*/"旅行や遠征の必需品、キャリーケース。\n旅する前のドキドキも、旅した後の思い出も、\nダイヤルロックのその奥に。", /*category*/2, /*type*/1, /*sort*/24, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/200008, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/11/29 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000009, /*name*/"跳び箱", /*description*/"8段の跳び箱。\n体育の授業で立ちはだかる、みんなの目標。\nノスタルジックな青春を思わせる一品。", /*category*/2, /*type*/1, /*sort*/25, /*flag_shop*/1, /*tile_size_x*/2, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200009, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/11/29 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000010, /*name*/"スタンドライト", /*description*/"クラシカルなデザインのスタンドライト。\n暗いところに置くと、周囲をほんのり照らしてくれる。\nピンク色とあいまって、置けば気分はお姫様♪", /*category*/2, /*type*/1, /*sort*/34, /*flag_shop*/1, /*tile_size_x*/2, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200010, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/02/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000011, /*name*/"屏風", /*description*/"梅の木が描かれた金屏風。\n力強い筆遣いと鮮やかな色合いで存在感抜群。\n置くだけで、スペース全体を華やかにしてくれる。", /*category*/2, /*type*/1, /*sort*/26, /*flag_shop*/1, /*tile_size_x*/4, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200011, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/01/14 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000012, /*name*/"コミック", /*description*/"平積みされたコミック。\n話題の作品がずらりとならんでいる。\nアイドルのおすすめもこっそりまざっているかも？", /*category*/2, /*type*/1, /*sort*/27, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200012, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/01/14 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000013, /*name*/"ゴミ箱", /*description*/"スタイリッシュなゴミ箱。\n部屋の景色にさりげなく溶けこんでくれる。\n部屋の中だけでなく、今日の気分もキレイに。", /*category*/2, /*type*/1, /*sort*/35, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/200013, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/03/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000014, /*name*/"タイヤ", /*description*/"物言わぬタイヤ。\n誰かの愛車の一部か、トレーニング用品か。\n過去を語らぬ寡黙さが、アイドルたちにも大人気。", /*category*/2, /*type*/1, /*sort*/36, /*flag_shop*/1, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200014, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/03/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000015, /*name*/"うさぎ", /*description*/"誰かがいつも連れている、うさぎのぬいぐるみ。\n抱きつくのにちょうどいい大きさなんだよねー。\nとは、連れている誰かの独り言。", /*category*/2, /*type*/1, /*sort*/37, /*flag_shop*/1, /*tile_size_x*/3, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200015, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/03/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000022, /*name*/"テーブルランプ/すずらん", /*description*/"すずらんの形をしたテーブルランプ。\nファンタジックな雰囲気を演出してくれる。\n周囲がほんのり照らされ、なんだか穏やかな気分に。", /*category*/2, /*type*/1, /*sort*/47, /*flag_shop*/1, /*tile_size_x*/2, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200022, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/04/30 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000023, /*name*/"りんごろうのぬいぐるみ", /*description*/"大きなりんごろうのぬいぐるみ。\nマスコットとして癒し枠…のはずなのだが、\n独特の存在感と、そこはかとない威圧感を放っている。", /*category*/2, /*type*/1, /*sort*/48, /*flag_shop*/1, /*tile_size_x*/1, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/200023, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/04/30 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/20000024, /*name*/"自動販売機", /*description*/"青い自動販売機。\n日常風景の中にさりげなく溶け込む、おなじみの存在。\nちょっと頑張ったあなたに、ささやかな休憩を。", /*category*/2, /*type*/1, /*sort*/49, /*flag_shop*/1, /*tile_size_x*/2, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/200024, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2024/04/30 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/40000001, /*name*/"観葉植物", /*description*/"シンプルな観葉植物。\n居心地の良い空間を作るうえでの、縁の下の力持ち。\n青々とした葉が、見る人に安らぎを与えてくれる。", /*category*/4, /*type*/1, /*sort*/31, /*flag_shop*/0, /*tile_size_x*/1, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/400001, /*itemset_id*/2, /*personality_anime*/0, /*start_date*/"2024/02/28 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/40000002, /*name*/"事務所の観葉植物", /*description*/"事務所の観葉植物。\n適切に置かれた緑が、部屋の色合いを整える。\n事務所の雰囲気作りに一役買っている。", /*category*/4, /*type*/1, /*sort*/7, /*flag_shop*/0, /*tile_size_x*/1, /*tile_size_y*/2, /*tile_size_z*/2, /*icon_id*/400002, /*itemset_id*/1, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/40000003, /*name*/"プランター①", /*description*/"白いポット型のプランター。\nガーデンピックが可愛らしい。\nこれを飾るだけで、なんだか明るい雰囲気。", /*category*/4, /*type*/1, /*sort*/10, /*flag_shop*/0, /*tile_size_x*/2, /*tile_size_y*/2, /*tile_size_z*/1, /*icon_id*/400003, /*itemset_id*/5, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/40000004, /*name*/"プランター②", /*description*/"大きな樽が目を引くプランターセット。\n木の風合いと大きく咲いた花が、\nナチュラルな彩りを演出してくれる。", /*category*/4, /*type*/1, /*sort*/11, /*flag_shop*/0, /*tile_size_x*/2, /*tile_size_y*/4, /*tile_size_z*/1, /*icon_id*/400004, /*itemset_id*/5, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/40000005, /*name*/"プランター③", /*description*/"背丈の異なるプランターセット。\nどの花も綺麗に見ることができる配置。\nプランターのデザインを楽しみたい人にも。", /*category*/4, /*type*/1, /*sort*/12, /*flag_shop*/0, /*tile_size_x*/2, /*tile_size_y*/3, /*tile_size_z*/1, /*icon_id*/400005, /*itemset_id*/5, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/40000006, /*name*/"プランター④", /*description*/"列車のプランターセット。\nオモチャの列車のような、愛らしいデザイン。\n子どもから大人まで、見る人を笑顔にする。", /*category*/4, /*type*/1, /*sort*/13, /*flag_shop*/0, /*tile_size_x*/5, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/400006, /*itemset_id*/5, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
INSERT INTO `sector3d_room_item` VALUES (/*id*/40000007, /*name*/"ガーデンスペース", /*description*/"ガーデンスペース。\n思い思いに植えられた花が咲いている。\n日々に小さな彩りを与えてくれる。", /*category*/4, /*type*/1, /*sort*/19, /*flag_shop*/1, /*tile_size_x*/3, /*tile_size_y*/1, /*tile_size_z*/1, /*icon_id*/400007, /*itemset_id*/0, /*personality_anime*/0, /*start_date*/"2023/09/20 15:00:00", /*end_date*/"2099-09-03 00:00:00");
