INSERT INTO `sp_login_position` VALUES (/*day_count*/1, /*chara_index*/1, /*frame_type*/3, /*frame_position_x*/0, /*frame_position_y*/0, /*text_data*/"私はP.C.Sのみんなと\n『ラブレター』を\n歌いました♪");
